INSERT INTO `nghenhac` (`ID`, `TenBaiHat`, `LoiBaiHat`, `CaSy`, `TacGia`, `TheLoai`, `DuongDan`, `LuotNghe`) VALUES
(2, 'Để Em Rời Xa', '<p>Ver1 (Melody):&nbsp;<br />\r\n<br />\r\nPh&uacute;c Bồ:&nbsp;<br />\r\n<br />\r\nV&agrave; nếu e muốn đi xa nơi anh, gạt đi t&igrave;nh y&ecirc;u nơi anh&nbsp;<br />\r\nBỏ qua ng&agrave;y th&aacute;ng c&ugrave;ng nhau vượt qua, nếu em hiểu?&nbsp;<br />\r\nRồi l&uacute;c e kh&oacute;c trong v&ograve;ng tay anh, để anh nhận ra l&agrave; e l&agrave;m ra để anh mang đi theo&nbsp;<br />\r\nSuốt cuộc đời&hellip;&nbsp;<br />\r\n<br />\r\nJC Hưng:&nbsp;<br />\r\n<br />\r\nAi cũng sẽ chỉ một hạnh ph&uacute;c tr&ecirc;n d&ograve;ng đường đời&nbsp;<br />\r\nV&agrave; ai cũng sẽ nhận ra người m&igrave;nh ko thể qu&ecirc;n&nbsp;<br />\r\nTh&igrave; em h&atilde;y cứ đi t&igrave;m một hạnh ph&uacute;c mới&hellip;&nbsp;<br />\r\nT&igrave;m một người thay thế anh trong những đ&ecirc;m....&nbsp;<br />\r\n<br />\r\nHook (Ho&agrave;ng T&ocirc;n):&nbsp;<br />\r\n<br />\r\nĐể anh một m&igrave;nh nh&eacute; em em cứ đi đi,&nbsp;<br />\r\nĐập n&aacute;t hết ng&agrave;y th&aacute;ng qua em cứ đi đi&nbsp;<br />\r\nV&igrave; anh đ&atilde; chọn về m&igrave;nh thế th&ocirc;i&nbsp;<br />\r\nChằng thể n&agrave;o đổi thay anh biết thời gian chẳng thể n&agrave;o&nbsp;<br />\r\nQu&ecirc;n đi được một người&hellip;&nbsp;<br />\r\n<br />\r\nVer2 (Eszi):&nbsp;<br />\r\n<br />\r\nHơi thở đang dần h&ograve;a nhịp theo từng nỗi đau, mất m&aacute;t&nbsp;<br />\r\nKh&ocirc;ng gian cũng đang bao tr&ugrave;m l&ecirc;n một cảm x&uacute;c, rất kh&aacute;c.&nbsp;<br />\r\nKể từ khi em đi b&igrave;nh minh đ&atilde; ko c&ograve;n vương nắng.&nbsp;<br />\r\nHạnh ph&uacute;c của e đ&atilde; biến m&agrave;n đ&ecirc;m trong anh bao phủ th&agrave;nh sương trắng.&nbsp;<br />\r\nC&oacute; phải đ&acirc;y l&agrave; điều em muốn? (muốn hả?)&nbsp;<br />\r\nB&igrave;nh y&ecirc;n nơi anh chưa đủ l&agrave;m cho e vui v&agrave; trở n&ecirc;n sung sướng (hở)&nbsp;<br />\r\nEm cần j, c&ocirc; g&aacute;i ng&agrave;y xưa đ&acirc;u cần j đ&acirc;u&nbsp;<br />\r\nNhưng giờ đ&atilde; b&iacute;t t&igrave;m ai đ&oacute; kh&aacute;c đ&ecirc;m b&ecirc;n gối đấu&nbsp;<br />\r\nV&agrave; anh ko thể liếc mắt nh&igrave;n e v&agrave; anh biết chắc&nbsp;<br />\r\nTrong th&acirc;m t&acirc;m a lu&ocirc;n c&ograve;n một chỗ chờ em trở về&nbsp;<br />\r\nAnh tin v&agrave;o ph&eacute;p nhiệm m&agrave;u khoảng c&aacute;ch sẽ ko c&ograve;n xa&nbsp;<br />\r\nAnh tin một ng&agrave;y n&agrave;o đ&oacute; thời gian sẽ quay lại từ đầu&nbsp;<br />\r\n<br />\r\nVer3 (Melody):&nbsp;<br />\r\n<br />\r\nPh&uacute;c Bồ:&nbsp;<br />\r\n<br />\r\nV&agrave; nếu e muốn đi xa nơi anh, gạt đi t&igrave;nh y&ecirc;u nơi anh&nbsp;<br />\r\nBỏ qua ng&agrave;y th&aacute;ng c&ugrave;ng nhau vượt qua, nếu em hiểu?&nbsp;<br />\r\nRồi l&uacute;c e kh&oacute;c trong v&ograve;ng tay anh, để anh nhận ra l&agrave; e l&agrave;m ra để anh mang đi theo&nbsp;<br />\r\nSuốt cuộc đời&hellip;&nbsp;<br />\r\n<br />\r\nJC Hưng:&nbsp;<br />\r\n<br />\r\nAi cũng sẽ chỉ một hạnh ph&uacute;c tr&ecirc;n d&ograve;ng đường đời&nbsp;<br />\r\nV&agrave; ai cũng sẽ nhận ra người m&igrave;nh ko thể qu&ecirc;n&nbsp;<br />\r\nTh&igrave; em h&atilde;y cứ đi t&igrave;m một hạnh ph&uacute;c mới&hellip;&nbsp;<br />\r\nT&igrave;m một người thay thế anh trong những đ&ecirc;m....&nbsp;<br />\r\n<br />\r\nHook (Ho&agrave;ng T&ocirc;n):&nbsp;<br />\r\n<br />\r\nĐể anh một m&igrave;nh nh&eacute; em em cứ đi đi,&nbsp;<br />\r\nĐập n&aacute;t hết ng&agrave;y th&aacute;ng qua em cứ đi đi&nbsp;<br />\r\nV&igrave; anh đ&atilde; chọn về m&igrave;nh thế th&ocirc;i&nbsp;<br />\r\nChằng thể n&agrave;o đổi thay anh biết thời gian chẳng thể n&agrave;o&nbsp;<br />\r\nQu&ecirc;n đi được một người&hellip;&nbsp;<br />\r\n<br />\r\nBridge (Rap):&nbsp;<br />\r\n<br />\r\nEszi:&nbsp;<br />\r\n<br />\r\nC&oacute; viết l&ecirc;n th&ecirc;m bao mu&ocirc;n v&agrave;n kh&uacute;c ca th&igrave; cũng kh&ocirc;ng thể n&agrave;o trở n&ecirc;n v&ocirc; gi&aacute;&nbsp;<br />\r\nHơn l&agrave; đc c&ugrave;ng em tay trong tay đi xa, anh thức trắng một lần sẽ chẳng bao giờ đổi thay&nbsp;<br />\r\nT&igrave;nh y&ecirc;u anh trao t&igrave;nh vẫn sẽ ko bao giờ lung lay&nbsp;<br />\r\n<br />\r\nT&igrave;m một người thay thế anh trong những đ&ecirc;m&nbsp;<br />\r\nĐể anh một&hellip;để anh một&hellip;&nbsp;<br />\r\n<br />\r\nHook (Ho&agrave;ng T&ocirc;n):&nbsp;<br />\r\n<br />\r\nĐể anh một m&igrave;nh nh&eacute; em em cứ đi đi&nbsp;<br />\r\nĐập n&aacute;t hết ng&agrave;y th&aacute;ng qua em cứ đi đi&nbsp;<br />\r\nV&igrave; anh đ&atilde; chọn về m&igrave;nh thế th&ocirc;i&nbsp;<br />\r\nChằng thể n&agrave;o đổi thay anh biết thời gian chẳng thể n&agrave;o&nbsp;<br />\r\nQu&ecirc;n đi được một người&hellip;</p>\r\n', 'FB Boiz', 'FB Boiz', 'Việt Nam, Nhạc Trẻ', 'res/songs/deEmRoiXa-FBBoiz.mp3', 2000),
(14, 'Waiting For Love', '<p>Where there\'s a will, there\'s a way, kinda beautiful<br>\r\nAnd every night has its day, so magical<br>\r\nAnd if there\'s love in this life, there\'s no obstacle<br>\r\nThat can\'t be defeated</p>\r\n\r\n<p>For every tyrant to tear upon the vulnerable<br>\r\nIn every lost soul, the bones of the miracle<br>\r\nFor every dreamer, a dream we\'re unstoppable<br>\r\nWith something to believe in</p>\r\n\r\n<p>Monday left me broken<br>\r\nTuesday I was through with hoping<br>\r\nWednesday my empty arms were open<br>\r\nThursday waiting for love, waiting for love<br>\r\nBang the stars it\'s Friday<br>\r\nI\'m burning like a fire gone wild on Saturday<br>\r\nGuess I won\'t be coming to church on Sunday<br>\r\nI\'ll be waiting for love, waiting for love<br>\r\nTo cover on</p>\r\n\r\n<p>For every tyrant to tear upon the vulnerable<br>\r\nIn every lost soul, the bones of the miracle<br>\r\nFor every dreamer, a dream we\'re unstoppable<br>\r\nWith something to believe in</p>\r\n\r\n<p>Monday left me broken<br>\r\nTuesday I was through with hoping<br>\r\nWednesday my empty arms were open<br>\r\nThursday waiting for love, waiting for love<br>\r\nBang the stars it\'s Friday<br>\r\nI\'m burning like a fire gone wild on Saturday<br>\r\nGuess I won\'t be coming to church on Sunday<br>\r\nI\'ll be waiting for love, waiting for love<br>\r\nTo cover on</p>', 'Avicii', 'Avicii', 'Âu Mỹ, EDM', 'res/songs/Waiting-For-Love-Avicii.mp3', 99999),
(15, 'Yêu 5', '<p>Xin những bối rối n&agrave;y cứ thế l&ecirc;n ng&ocirc;i&nbsp;<br />\r\nXin con tim rẽ lối t&igrave;m gi&acirc;y ph&uacute;t nghẹn lời&nbsp;<br />\r\nCho th&ecirc;m chơi vơi, để những &aacute;nh mắt đ&ocirc;i m&ocirc;i,&nbsp;<br />\r\nChạm nhau mang theo gọi mời&nbsp;<br />\r\n<br />\r\nXin cho ta tan v&agrave;o những đắm đuối mi&ecirc;n man&nbsp;<br />\r\nKhi em gh&eacute; ngang đời chợt mang sắc hương thi&ecirc;n đ&agrave;ng&nbsp;<br />\r\nV&agrave; cho những ấm &aacute;p lại đến lấp k&iacute;n nh&acirc;n gian&nbsp;<br />\r\nNg&agrave;y ta y&ecirc;n vui c&ugrave;ng n&agrave;ng&nbsp;<br />\r\n<br />\r\n[Pre-Hook]&nbsp;<br />\r\nNhẹ nh&agrave;ng &acirc;u yếm giữa chốn mơ&nbsp;<br />\r\nC&ugrave;ng t&igrave;m kiếm bao &yacute; thơ&nbsp;<br />\r\nĐể đắm say trong tiếng nhạc du dương&nbsp;<br />\r\nHồn sa theo bước ta ngẩn ngơ&nbsp;<br />\r\nD&ugrave; ng&agrave;n kiếp ta vẫn chờ&nbsp;<br />\r\nChỉ cần c&oacute; ph&uacute;t gi&acirc;y n&agrave;y trao y&ecirc;u thương&nbsp;<br />\r\n<br />\r\n[Hook]&nbsp;<br />\r\nCuz baby it&rsquo;s always you&nbsp;<br />\r\nIt&#39;s always you&nbsp;<br />\r\nIt&#39;s always you&nbsp;<br />\r\nIt&#39;s always you...always you...&nbsp;<br />\r\n(x2)&nbsp;<br />\r\n<br />\r\n[Rap]&nbsp;<br />\r\nTa như đi qua hết bao nhi&ecirc;u m&ecirc; say tr&ecirc;n đời&nbsp;<br />\r\nNhư y&ecirc;u th&ecirc;m cả ngh&igrave;n kiếp khi ta một gi&acirc;y b&ecirc;n người&nbsp;<br />\r\nV&igrave; chỉ cẩn một &aacute;nh nh&igrave;n m&agrave; em trao&nbsp;<br />\r\nL&agrave; tuổi xu&acirc;n của ta như h&oacute;a được th&agrave;nh chi&ecirc;m bao&nbsp;<br />\r\nVới mỗi s&aacute;ng thức giấc nghe em b&ecirc;n ta vui cười&nbsp;<br />\r\nCho đ&ecirc;m ta ng&acirc;y ngất, ru tai &ecirc;m qua đ&ocirc;i lời&nbsp;<br />\r\nĐầy ngọt ng&agrave;o v&agrave; trong veo, c&ugrave;ng h&ograve;a l&agrave;m trăng sao&nbsp;<br />\r\nD&igrave;u dắt đ&ocirc;i tim b&ecirc;n nhau đi theo bao y&ecirc;u thương d&acirc;ng tr&agrave;o&nbsp;<br />\r\n<br />\r\nV&agrave; ta xin đ&aacute;nh đổi một rừng tia nắng để lấy ch&uacute;t hơi ấm của em&nbsp;<br />\r\nXin đổi đi hết tiền bạc danh tiếng, những thứ h&agrave;ng trăm người th&egrave;m&nbsp;<br />\r\nBởi cả ng&agrave;n điều ph&ugrave; phiếm đ&acirc;u bằng một tho&aacute;ng m&ocirc;i mềm&nbsp;<br />\r\nKhi đời n&agrave;y sẽ mang đầy nuối tiếc nếu kh&ocirc;ng c&oacute; n&agrave;ng ở b&ecirc;n&nbsp;<br />\r\nNh&igrave;n em ta mới th&ecirc;m y&ecirc;u từng khoảnh khắc&nbsp;<br />\r\nMang hết tương tư giấu v&agrave;o theo từng bản nhạc&nbsp;<br />\r\nĐể mai n&agrave;y t&igrave;nh nở như mu&ocirc;n hoa kh&ocirc;ng t&agrave;n sắc&nbsp;<br />\r\nLưu giữ m&atilde;i nơi n&agrave;y một c&acirc;u chuyện ng&agrave;n năm</p>\r\n', 'Rhymastic', 'Rhymastic', 'Việt Nam', 'res/songs/Yeu-5-Rhymastic.mp3', 5432),
(16, 'Tears', '<p>[Verse 1]<br />\r\nI tried hard to make you want me<br />\r\nBut we&#39;re not supposed to be<br />\r\nAnd the truth will always haunt me<br />\r\nEven though it set me free<br />\r\nAnd my tears fall like the ocean<br />\r\nAs they floated in the breeze<br />\r\nThey were falling in slow motion<br />\r\nAnd they brought me to my knees<br />\r\n<br />\r\n[Pre-Chorus]<br />\r\nYou&#39;re haunting me, taunting me all in my brain<br />\r\nTurn off the light and now all that remains<br />\r\nFills me with doubt<br />\r\nAnd I&#39;m shouting your name out loud<br />\r\nWhy do you wanna put me through the pain?<br />\r\nI get the feeling I&#39;ll never escape<br />\r\nI can&#39;t hide away from the shame of you<br />\r\n<br />\r\n[Chorus]<br />\r\nTears on the ground, tears on my pillow<br />\r\nYou won&#39;t bring me down<br />\r\nAnd I&#39;ll get over you<br />\r\nThese tears will get me through<br />\r\nAnd I&#39;ll get over you<br />\r\n<br />\r\n[Post-Chorus]<br />\r\nI&#39;ll get over you<br />\r\nI&#39;ll get over you<br />\r\nI&#39;ll get over you<br />\r\nI&#39;ll get over you<br />\r\n<br />\r\n[Verse 2]<br />\r\nWhen did you lose your emotion?<br />\r\nWhen did you become so cruel?<br />\r\nAnd if you want to cut me open<br />\r\nSays a thousands words &#39;bout you<br />\r\nAnd in time I know you&#39;ll leave me<br />\r\nLike a distant memory<br />\r\nI know love can be so easy<br />\r\nIf I start by loving me, oh<br />\r\n<br />\r\n[Pre-Chorus]<br />\r\nYou&#39;re haunting me, taunting me all in my brain<br />\r\nTurn off the light and now all that remains<br />\r\nFills me with doubt<br />\r\nAnd I&#39;m shouting your name out loud<br />\r\nWhy do you wanna put me through the pain?<br />\r\nI get the feeling I&#39;ll never escape<br />\r\nI can&#39;t hide away from the shame of you<br />\r\n<br />\r\n[Chorus 1]<br />\r\nTears on the ground, tears on my pillow<br />\r\nYou won&#39;t bring me down<br />\r\nAnd I&#39;ll get over you<br />\r\nThese tears will get me through<br />\r\nAnd I&#39;ll get over you<br />\r\n<br />\r\n[Post-Chorus]<br />\r\nI&#39;ll get over you<br />\r\nI&#39;ll get over you<br />\r\nI&#39;ll get over you<br />\r\nI&#39;ll get over you<br />\r\n<br />\r\n[Chorus 2]<br />\r\nTears on the ground, rain at my window<br />\r\nThe pain washes out<br />\r\nAnd I&#39;ll get over you<br />\r\nThese tears will get me through<br />\r\nAnd I&#39;ll get over you<br />\r\n<br />\r\n[Bridge]<br />\r\nI&#39;ll get over you<br />\r\nI&#39;ll get over you<br />\r\nI&#39;ll get over you<br />\r\nI don&#39;t need you to call me tonight<br />\r\n(I&#39;ll get over you)<br />\r\nI don&#39;t need you to see me if I&#39;m alright<br />\r\n(I&#39;ll get over you)<br />\r\nYou left me, so leave me, I&#39;m fine<br />\r\nI&#39;ll be here getting on with my life<br />\r\n<br />\r\n[Chorus 3]<br />\r\nTears on the ground, tears on my pillow<br />\r\nYou won&#39;t bring me down<br />\r\nTears on the ground, rain at my window<br />\r\nThe pain washes out<br />\r\nTears on the ground, rain at my window<br />\r\nThe pain washes out<br />\r\nAnd I&#39;ll get over you<br />\r\nOh yeah, I&#39;ll get over you<br />\r\n<br />\r\n[Outro]<br />\r\nI&#39;ll get over...<br />\r\nI&#39;ll get over you</p>\r\n', ' Clean Bandit, Louisa Johnson', ' Clean Bandit, Louisa Johnson', 'Âu Mỹ', 'res/songs/Tears - Clean Bandit.mp3', 25234),
(17, 'Thôi', '<p>Ng&agrave;y th&aacute;ng y&ecirc;u đương l&uacute;c đầu, t&igrave;nh cờ v&agrave; vội v&atilde;&nbsp;<br />\r\nD&ugrave; biết ta đ&atilde; từng, đau đớn cũng đ&ocirc;i lần&nbsp;<br />\r\nM&agrave; th&ocirc;i, y&ecirc;u đương l&agrave; m&ugrave; qu&aacute;ng&nbsp;<br />\r\nN&eacute;p v&agrave;o anh dẫu ch&uacute;t đắn đo ngại ngần.&nbsp;<br />\r\n<br />\r\nHạnh ph&uacute;c như m&acirc;y tho&aacute;ng qua, nhẹ nh&agrave;ng &ugrave;a theo gi&oacute;&nbsp;<br />\r\nNgười cũng xa m&atilde;i xa, đưa tay với lấy những &acirc;n t&igrave;nh&nbsp;<br />\r\nGạt đi lời anh n&oacute;i từng nồng n&agrave;n&nbsp;<br />\r\nL&agrave;m sao dễ qu&ecirc;n.&nbsp;<br />\r\n<br />\r\n[ĐK:]&nbsp;<br />\r\nN&eacute;n y&ecirc;u thương v&agrave;o tim v&ugrave;i ch&ocirc;n những th&aacute;ng năm ta c&ugrave;ng nhau&nbsp;<br />\r\nEm đ&atilde; kh&oacute;c bao đ&ecirc;m mong cho l&ograve;ng th&ocirc;i th&eacute;t g&agrave;o&nbsp;<br />\r\nNếu anh quay về lại đ&agrave;nh th&ocirc;i giấu k&iacute;n t&acirc;m tư từ em&nbsp;<br />\r\nAnh về đi, anh về đi, đừng mong chờ.&nbsp;<br />\r\n<br />\r\n<br />\r\nNgười nỡ qu&ecirc;n đi những lời th&igrave; thầm c&ugrave;ng năm th&aacute;ng&nbsp;<br />\r\nC&ugrave;ng n&oacute;i y&ecirc;u suốt đời, y&ecirc;u m&atilde;i chỉ em th&ocirc;i&nbsp;<br />\r\nV&igrave; em lu&ocirc;n tin điều gian dối&nbsp;<br />\r\nTr&aacute;ch l&agrave;m sao khi hai tr&aacute;i tim lạc nhịp.&nbsp;<br />\r\n<br />\r\nHạnh ph&uacute;c như m&acirc;y tho&aacute;ng qua, nhẹ nh&agrave;ng &ugrave;a theo gi&oacute;&nbsp;<br />\r\nNgười cũng xa m&atilde;i xa, đưa tay với lấy những &acirc;n t&igrave;nh&nbsp;<br />\r\nGạt đi lời anh n&oacute;i từng nồng n&agrave;n&nbsp;<br />\r\nL&agrave;m sao dễ qu&ecirc;n.&nbsp;<br />\r\n<br />\r\n[ĐK:]&nbsp;<br />\r\nN&eacute;n y&ecirc;u thương v&agrave;o tim v&ugrave;i ch&ocirc;n những th&aacute;ng năm ta c&ugrave;ng nhau&nbsp;<br />\r\nEm đ&atilde; kh&oacute;c bao đ&ecirc;m mong cho l&ograve;ng th&ocirc;i th&eacute;t g&agrave;o&nbsp;<br />\r\nNếu anh quay về lại đ&agrave;nh th&ocirc;i giấu k&iacute;n t&acirc;m tư từ em&nbsp;<br />\r\nAnh về đi, anh về đi, đừng mong m&atilde;i.</p>\r\n', 'Giang Hồng Ngọc', 'Tiên Tiên', 'Việt Nam', 'res/songs/Thoi-Giang-Hong-Ngoc.mp3', 1134),
(18, 'Hồi Ức Ngày Hôm Qua', '<p>Tạm biệt nh&eacute;, thầy c&ocirc;, m&aacute;i trường th&acirc;n thương&nbsp;<br />\r\nTạm biệt nh&eacute;, ghế đ&aacute; ơi đừng buồn khi t&ocirc;i đi&nbsp;<br />\r\nTạm biệt nh&eacute;, h&agrave;ng c&acirc;y x&otilde;a b&oacute;ng m&aacute;t t&ocirc;i đừng về&nbsp;<br />\r\nC&oacute; con đường n&agrave;o n&iacute;u ch&acirc;n người đi&nbsp;<br />\r\n<br />\r\nChạy theo ng&agrave;n ước muốn bay đến nơi xa x&ocirc;i&nbsp;<br />\r\nƯớc mơ của t&ocirc;i l&acirc;u nay vẫn lu&ocirc;n l&agrave; như thế&nbsp;<br />\r\nVậy m&agrave; sao giờ đ&ocirc;i ch&acirc;n chẳng thể đi,&nbsp;<br />\r\nC&oacute; thật l&agrave; s&acirc;n trường biết nhớ&nbsp;<br />\r\nBiết h&ocirc;m nay t&ocirc;i xa nơi đ&acirc;y, kh&eacute;p lại một thời&nbsp;<br />\r\n<br />\r\n<br />\r\nBạn c&ograve;n nhớ chiếc ghế m&agrave;u xanh, thứ Hai t&ocirc;i vẫn ngồi&nbsp;<br />\r\nC&ograve;n nhớ chiếc xe đạp cũ m&agrave; t&ocirc;i hay đi&nbsp;<br />\r\nNg&agrave;y mưa hay nắng đến lớp đều vui,&nbsp;<br />\r\nSao bỗng h&ocirc;m nay lại buồn&nbsp;<br />\r\nV&igrave; t&ocirc;i phải lớn l&ecirc;n rồi,&nbsp;<br />\r\nV&agrave; bước đi xa nơi đ&acirc;y&nbsp;<br />\r\n<br />\r\n<br />\r\nChạy theo ng&agrave;n ước muốn bay đến nơi xa x&ocirc;i&nbsp;<br />\r\nƯớc mơ của t&ocirc;i l&acirc;u nay vẫn lu&ocirc;n l&agrave; như thế&nbsp;<br />\r\nVậy m&agrave; sao giờ đ&ocirc;i ch&acirc;n chẳng thể đi,&nbsp;<br />\r\nC&oacute; thật l&agrave; s&acirc;n trường biết nhớ&nbsp;<br />\r\nBiết h&ocirc;m nay t&ocirc;i xa nơi đ&acirc;y, kh&eacute;p lại một thời&nbsp;<br />\r\n<br />\r\nChạy theo ng&agrave;n ước muốn bay đến nơi xa x&ocirc;i&nbsp;<br />\r\nƯớc mơ của t&ocirc;i l&acirc;u nay vẫn lu&ocirc;n l&agrave; thếeeeee&nbsp;<br />\r\nVậy m&agrave; sao giờ đ&ocirc;i ch&acirc;n chẳng thể đi,&nbsp;<br />\r\nC&oacute; thật l&agrave; s&acirc;n trường biết nhớ&nbsp;<br />\r\nBiết h&ocirc;m nay t&ocirc;i xa nơi đ&acirc;y, kh&eacute;p lại một thời&nbsp;<br />\r\n<br />\r\nTạm biệt nh&eacute;, thầy c&ocirc;, m&aacute;i trường th&acirc;n thương&nbsp;<br />\r\nTạm biệt nh&eacute;, ghế đ&aacute; ơi đừng buồn khi t&ocirc;i đi&nbsp;<br />\r\nTạm biệt nh&eacute;, h&agrave;ng c&acirc;y x&otilde;a b&oacute;ng m&aacute;t t&ocirc;i đừng về&nbsp;<br />\r\nC&oacute; con đường n&agrave;o n&iacute;u ch&acirc;n người đi&nbsp;<br />\r\n<br />\r\nV&igrave; giờ t&ocirc;i phải lớn l&ecirc;n rồi&nbsp;<br />\r\nV&agrave; bước đi&hellip;m&atilde;i xa&hellip;</p>\r\n', 'Hoàng Yến Chibi', 'Yul Lee', 'Việt Nam', 'res/songs/Hoi-Uc-Ngay-Hom-Qua-Hoang-Yen-Chibi.mp3', 8676),
(19, 'Alone', '<p>Whoo Whoo Whoo Whoo&nbsp;<br />\r\n(I don&#39;t wanna cry)&nbsp;<br />\r\nWhoo Whoo Whoo Whoo&nbsp;<br />\r\nPlease dry my eyes)&nbsp;<br />\r\nWhoo Whoo Whoo Whoo&nbsp;<br />\r\n(I&#39;m fallin&#39; down)&nbsp;<br />\r\nWhoo Whoo Whoo Whoo&nbsp;<br />\r\n(Without you I&#39;m fallin&#39; down)&nbsp;<br />\r\n<br />\r\nchuogi iri ma-neulkka non deche mwohalkka&nbsp;<br />\r\najik-nan ire hoksi dora olkka bwa&nbsp;<br />\r\nna jolde irone aninde iron jogob-son-neunde&nbsp;<br />\r\nsarang nomudo dokhe ajikdo mot kkenna bwa&nbsp;<br />\r\n<br />\r\nnomudo dalkomhan ni mare soga&nbsp;<br />\r\nijewa honja I&#39;m falling down I&#39;m falling down&nbsp;<br />\r\ncharari mannaji malgol geuresso&nbsp;<br />\r\niroke dwel jul mollasso&nbsp;<br />\r\nitorok swipge urin kkeuchin-gayo&nbsp;<br />\r\n<br />\r\n(we) tto na honja babeul mokgo&nbsp;<br />\r\nna honja yonghwa bogo&nbsp;<br />\r\nna honja norehago iroke na ulgo bulgo&nbsp;<br />\r\nnon ttonago obso huhwehedo soyong obso&nbsp;<br />\r\noneuldo na honja&nbsp;<br />\r\nWhoo Whoo Whoo Whoo Whoo Whoo Whoo Whoo&nbsp;<br />\r\n<br />\r\nna honja gireul gotgo na honja TV bogo&nbsp;<br />\r\nna honja chwihe bogo iroke meil ulgo bulgo&nbsp;<br />\r\nsarang cham dalkomhesso ibyoriran geurimja aneso&nbsp;<br />\r\noneuldo jam motja&nbsp;<br />\r\nWhoo Whoo Whoo Whoo Whoo Whoo Whoo&nbsp;<br />\r\n<br />\r\nDon&#39;t tell me it&#39;s over&nbsp;<br />\r\nnaranajwo dodeche nege non we ironeunde&nbsp;<br />\r\nOh Ma boy (Whoo Whoo Whoo)&nbsp;<br />\r\noneuldo na nunmullo jisewo&nbsp;<br />\r\n<br />\r\nnomudo dalkomhan ni mare soga&nbsp;<br />\r\nijewa honja I&#39;m falling down I&#39;m falling down&nbsp;<br />\r\ncharari mannaji malgol geuresso&nbsp;<br />\r\niroke dwel jul mollasso&nbsp;<br />\r\nitorok swipge urin kkeuchin-gayo&nbsp;<br />\r\n<br />\r\n(we) tto na honja babeul mokgo&nbsp;<br />\r\nna honja yonghwa bogo&nbsp;<br />\r\nna honja norehago iroke na ulgo bulgo&nbsp;<br />\r\nnon ttonago obso huhwehedo soyongobso&nbsp;<br />\r\noneuldo na honja&nbsp;<br />\r\nWhoo Whoo Whoo Whoo Whoo Whoo Whoo Whoo&nbsp;<br />\r\n<br />\r\nna honja gireul gotgo na honja TV bogo&nbsp;<br />\r\nna honja chwihe bogo iroke meil ulgo bulgo&nbsp;<br />\r\nsarang cham dalkomhesso ibyoriran geurimja aneso&nbsp;<br />\r\noneuldo jam motja&nbsp;<br />\r\nWhoo Whoo Whoo Whoo Whoo Whoo Whoo&nbsp;<br />\r\n<br />\r\ncham pponhadi pponhan noye geuron binbonhan gojitmare tto sogasso&nbsp;<br />\r\naju gabssan ni misoe hollin nal gatgo no jemiitge norasso&nbsp;<br />\r\nijewaso mwol ojjogesso geuriume gatyo na honjaso&nbsp;<br />\r\nniga borin ne mam gonghohe&nbsp;<br />\r\nkkok boryojyo tongbin gori gata&nbsp;<br />\r\n<br />\r\nWhoo Whoo Whoo Whoo&nbsp;<br />\r\n(I don&#39;t wanna cry)&nbsp;<br />\r\nWhoo Whoo Whoo Whoo&nbsp;<br />\r\n(Please dry my eyes)&nbsp;<br />\r\nWhoo Whoo Whoo Whoo&nbsp;<br />\r\n(I&#39;m fallin&#39; down)&nbsp;<br />\r\nWhoo Whoo Whoo Whoo&nbsp;<br />\r\n(Without you I&#39;m fallin&#39; down)</p>\r\n', 'SISTAR', 'SISTAR', 'Châu Á,Hàn Quốc, Dance Pop', 'res/songs/Alone-SISTAR.mp3', 544),
(20, 'Like You', '<p>Hiện chưa c&oacute; lời b&agrave;i h&aacute;t</p>\r\n', 'Honeyst', 'Honeyst', 'Châu Á,Hàn Quốc, Dance Pop', 'res/songs/Alone-SISTAR.mp3', 1563),
(21, 'Come Back To Me', '<p>The rain falls on my windows&nbsp;<br />\r\nAnd the coldness runs through my soul&nbsp;<br />\r\nAnd the rain falls, oh the rain falls&nbsp;<br />\r\nI don&rsquo;t want to be alone&nbsp;<br />\r\n<br />\r\nI wish that I could photoshop on&nbsp;<br />\r\nOur bad memories&nbsp;<br />\r\nBecause the flashbacks, oh the flashbacks&nbsp;<br />\r\nWon&rsquo;t leave me alone&nbsp;<br />\r\n<br />\r\nIf you come back to me&nbsp;<br />\r\nI&rsquo;ll be all that you need&nbsp;<br />\r\nBaby, come back to me&nbsp;<br />\r\nLet me make up for what happened in the past&nbsp;<br />\r\n<br />\r\nChorus&nbsp;<br />\r\n(Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nI&rsquo;ll be everything you need (Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nBoy you&rsquo;re one in a million (Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nI&rsquo;ll be everything you need (Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nYou&rsquo;re one in a million&nbsp;<br />\r\n(You&rsquo;re one in a million)&nbsp;<br />\r\n<br />\r\nMemories I have of Manhattan&nbsp;<br />\r\nShe goes shopping for new clothes&nbsp;<br />\r\nAnd she buys this, and she buys that&nbsp;<br />\r\nJust leave her alone&nbsp;<br />\r\nI wish that he would listen to her&nbsp;<br />\r\nSide of the story&nbsp;<br />\r\nIt isn&rsquo;t that bad, it isn&rsquo;t that bad&nbsp;<br />\r\nAnd she&rsquo;s wiser for it now&nbsp;<br />\r\n<br />\r\nI admit I cheated (admit I cheated )&nbsp;<br />\r\nDon&rsquo;t know why I did it (why I did it)&nbsp;<br />\r\nBut I do regret it (do regret it)&nbsp;<br />\r\nNothing I can do or say can change the past&nbsp;<br />\r\n<br />\r\nChorus&nbsp;<br />\r\n(Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nI&rsquo;ll be everything you need (Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nBoy you&rsquo;re one in a million (Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nI&rsquo;ll be everything you need (Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nYou&rsquo;re one in a million&nbsp;<br />\r\n(You&rsquo;re one in a million)&nbsp;<br />\r\n<br />\r\nEverything I ever did&nbsp;<br />\r\nHeaven knows I&rsquo;m sorry but&nbsp;<br />\r\nI was too young to see&nbsp;<br />\r\nYou were always there for me&nbsp;<br />\r\nAnd my curiosity&nbsp;<br />\r\nGot the better half of me&nbsp;<br />\r\nBaby take it easy on me&nbsp;<br />\r\nAnything from A to Z&nbsp;<br />\r\nTell me what you want to be&nbsp;<br />\r\nI open my heart to be&nbsp;<br />\r\nYou are more priority&nbsp;<br />\r\nCan&rsquo;t you see you punished me&nbsp;<br />\r\nMore than enough already&nbsp;<br />\r\nBaby take it easy on me&nbsp;<br />\r\n<br />\r\nBaby take it easy on me&nbsp;<br />\r\nBaby come back to me&nbsp;<br />\r\nBaby come back to me&nbsp;<br />\r\n<br />\r\nChorus&nbsp;<br />\r\n(Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nI&rsquo;ll be everything you need (Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nBoy you&rsquo;re one in a million (Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nI&rsquo;ll be everything you need (Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nYou&rsquo;re one in a million&nbsp;<br />\r\n(You&rsquo;re one in a million)&nbsp;<br />\r\n<br />\r\n(Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nI&rsquo;ll be everything you need (Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nBoy you&rsquo;re one in a million (Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nI&rsquo;ll be everything you need (Come back)&nbsp;<br />\r\nBaby come back to me (Come back)&nbsp;<br />\r\nYou&rsquo;re one in a million&nbsp;<br />\r\n(One in a million)&nbsp;<br />\r\n<br />\r\nLa la la la&nbsp;<br />\r\nLa la la la la la&nbsp;<br />\r\nLa la la la la la&nbsp;<br />\r\nLa la la</p>\r\n', 'Utada Hikaru', 'Utada Hikaru', 'Âu Mỹ', 'res/songs/Come Back To Me - Utada Hikaru.mp3', 64433),
(22, 'You Make Me Want To Be A Man', '<p>[Chorus:]&nbsp;<br />\r\nI really wanna tell you something&nbsp;<br />\r\nThis is just the way I am&nbsp;<br />\r\nI really wanna tell you something, but I can&#39;t&nbsp;<br />\r\nYou make me want to be a man&nbsp;<br />\r\nArguments that have no meaning&nbsp;<br />\r\nThis is just the way I am&nbsp;<br />\r\nYou really wanna tell me something, but you can&#39;t&nbsp;<br />\r\nYou make me want to be a man&nbsp;<br />\r\n<br />\r\nThe thunder and the rain called you and you came&nbsp;<br />\r\nWe didn&#39;t need to say much to communicate&nbsp;<br />\r\nNow it&#39;s different; 99% is misinterpreted&nbsp;<br />\r\n<br />\r\n[Chorus:]&nbsp;<br />\r\nI really wanna tell you something&nbsp;<br />\r\nThis is just the way I am&nbsp;<br />\r\nI really wanna tell you something, but I can&#39;t&nbsp;<br />\r\nYou make me want to be a man&nbsp;<br />\r\nArguments that have no meaning&nbsp;<br />\r\nThis is just the way I am&nbsp;<br />\r\nYou really wanna tell me something, but you can&#39;t&nbsp;<br />\r\nYou make me want to be a man&nbsp;<br />\r\n<br />\r\nEvery word you say finds a home in me&nbsp;<br />\r\nNothing that anyone could ever say&nbsp;<br />\r\nCould hurt me like this&nbsp;<br />\r\nBaby please, don&#39;t light that cigarette&nbsp;<br />\r\nDon&#39;t light that cigarette&nbsp;<br />\r\n<br />\r\n[Chorus:]&nbsp;<br />\r\nI really wanna tell you something&nbsp;<br />\r\nThis is just the way I am&nbsp;<br />\r\nI really wanna tell you something, but I can&#39;t&nbsp;<br />\r\nYou make me want to be a man&nbsp;<br />\r\nArguments that have no meaning&nbsp;<br />\r\nThis is just the way I am&nbsp;<br />\r\nYou really wanna tell me something, but you can&#39;t&nbsp;<br />\r\nYou make me want to be a man&nbsp;<br />\r\n<br />\r\n[Chorus:]&nbsp;<br />\r\nI really wanna tell you something&nbsp;<br />\r\nThis is just the way I am&nbsp;<br />\r\nI really wanna tell you something, but I can&#39;t&nbsp;<br />\r\nYou make me want to be a man&nbsp;<br />\r\nArguments that have no meaning&nbsp;<br />\r\nThis is just the way I am&nbsp;<br />\r\nYou really wanna tell me something, but you can&#39;t&nbsp;<br />\r\nYou make me want to be a man</p>\r\n', 'Utada Hikaru', 'Utada Hikaru', 'Âu Mỹ', 'res/songs/You Make Me Want To Be A Man Utada Hikaru.mp3', 3343),
(23, 'Easy Breezy', '<p>I still remember,&nbsp;<br />\r\nThe ways that you touched me.&nbsp;<br />\r\nNow I know I don&#39;t mean anything to you.&nbsp;<br />\r\nYou&#39;re Easy Breezy, and I&#39;m Japan-ezy.&nbsp;<br />\r\nSoon you&#39;ll mean exactly nothing to me.&nbsp;<br />\r\n<br />\r\nAnd that means...&nbsp;<br />\r\n<br />\r\nYou look stupid,&nbsp;<br />\r\nTelling all your friends how you got the best of me.&nbsp;<br />\r\nI intended to share the pleasure, only now I look stupid.&nbsp;<br />\r\nWe&#39;re living in a world with a lot of pressure,&nbsp;<br />\r\nIt&#39;s quite un-needed to put more pressure on me.&nbsp;<br />\r\n<br />\r\nYou came and went, and left my house like a breeze just passing by.&nbsp;<br />\r\nHello, goodbye, you left a note saying, &quot;&#39;t&#39;was nice stopping by.&quot;&nbsp;<br />\r\nI should have never, ever let you inside.&nbsp;<br />\r\n<br />\r\nI still remember,&nbsp;<br />\r\nThe ways that you touched me.&nbsp;<br />\r\nNow I know I don&#39;t mean anything to you.&nbsp;<br />\r\nYou&#39;re Easy Breezy, and I&#39;m Japan-ezy.&nbsp;<br />\r\nSoon you&#39;ll mean exactly nothing to me.&nbsp;<br />\r\nDoes that mean anything to you?&nbsp;<br />\r\n<br />\r\nEasy breezy,&nbsp;<br />\r\nDo you wrestle to hide that you&#39;re feeling lonely?&nbsp;<br />\r\nHow do I breathe with all this pressure on me?&nbsp;<br />\r\nEasy Breezy,&nbsp;<br />\r\nWhen you wrestle you know that you hurt somebody.&nbsp;<br />\r\nHow do I breathe with all this pressure on me?&nbsp;<br />\r\n<br />\r\nYou came and went, and left my house like a breeze just passing by.&nbsp;<br />\r\nKonnichiwa, Sayonara, was nice of you to stop by~&nbsp;<br />\r\nWould it amuse you if I told you that I...&nbsp;<br />\r\n<br />\r\nI still remember,&nbsp;<br />\r\nThe ways that you touched me.&nbsp;<br />\r\nNow I know I don&#39;t mean anything to you.&nbsp;<br />\r\nYou&#39;re Easy Breezy, and I&#39;m Japan-ezy.&nbsp;<br />\r\nSoon you&#39;ll mean exactly nothing to me.&nbsp;<br />\r\nDoes that mean anything to you?&nbsp;<br />\r\n<br />\r\nI still remember,&nbsp;<br />\r\nThe ways that you touched me.&nbsp;<br />\r\nNow I know I don&#39;t mean anything to you.&nbsp;<br />\r\nYou&#39;re Easy Breezy, and I&#39;m Japan-ezy.&nbsp;<br />\r\nSoon you&#39;ll mean exactly nothing to me.&nbsp;<br />\r\nDoes that mean anything to you?&nbsp;<br />\r\n<br />\r\nYou&#39;re Easy Breezy,&nbsp;<br />\r\nAnd I&#39;m Japan-ezy. Oh Oh, Oh Oh.&nbsp;<br />\r\n<br />\r\nYou&#39;re Easy Breezy,&nbsp;<br />\r\nAnd I&#39;m Japan-ezy. Oh Oh, Oh Oh.&nbsp;<br />\r\n<br />\r\nShe&#39;s got a new microphone,&nbsp;<br />\r\nShe&#39;s got a new microphone,&nbsp;<br />\r\nShe&#39;s got, she&#39;s got, she&#39;s got a new microphone&nbsp;<br />\r\nShe doesn&#39;t need you anymore,&nbsp;<br />\r\nYeah she doesn&#39;t need you anymore.&nbsp;<br />\r\nShe&#39;s got a new microphone,&nbsp;<br />\r\nShe doesn&#39;t need you anymore.&nbsp;<br />\r\n<br />\r\nOoh, Oh.</p>\r\n', 'Utada Hikaru', 'Utada Hikaru', 'Âu Mỹ', 'Vui lòng chọn file', 33),
(24, 'The Tree', '<p>난 그의 손을 만질 때&nbsp;<br />\r\n그의 날들을 꽤 오래 엿보았지&nbsp;<br />\r\n깊게 패인 손금에&nbsp;<br />\r\n모른 척해 온 외로움이 숨어있었고&nbsp;<br />\r\n이렇게 거칠었는 줄&nbsp;<br />\r\n다시금 알았네&nbsp;<br />\r\n그의 얼굴을 마주할 때&nbsp;<br />\r\n그의 어린 날들을 비춰보았지&nbsp;<br />\r\n떨어뜨린 입가에 한가득&nbsp;<br />\r\n지나간 시절을 머금고 있었고&nbsp;<br />\r\n낡고 오래된 기억을&nbsp;<br />\r\n여전히 견디고 있었네&nbsp;<br />\r\n낡고 오래된 기억을&nbsp;<br />\r\n여전히 견디우며&nbsp;<br />\r\n눈물 말리고 있었네&nbsp;<br />\r\n난 그의 주름살처럼&nbsp;<br />\r\n메마른 것을 본 적 단 한 번 없지&nbsp;<br />\r\n나를 힘겹게 안고&nbsp;<br />\r\n고요히 눈을 감기에&nbsp;<br />\r\n슬피 우는 법을 잊은 줄 알았고&nbsp;<br />\r\n이렇게 바람 부는 줄&nbsp;<br />\r\n나는 몰랐네&nbsp;<br />\r\n꽃 맺음이 다 한&nbsp;<br />\r\n굽은 등줄기는&nbsp;<br />\r\n초라했지만&nbsp;<br />\r\n그가 떠난 자리는&nbsp;<br />\r\n나무랄 곳 없이&nbsp;<br />\r\n텅 비어있게 했다</p>\r\n', ' Yang Hee Eun, Akdong Musician', ' Yang Hee Eun, Akdong Musician', 'Châu Á,Hàn Quốc, Pop', 'res/songs/The Tree Yang Hee Eun, Akdong.mp3', 5443),
(25, 'Rather Be (feat. Jess Glynne) ', '<p>[Verse 1: Jess Glynne]&nbsp;<br />\r\nWe&#39;re a thousand miles from comfort, we have traveled land and sea&nbsp;<br />\r\nBut as long as you are with me, there&#39;s no place I rather be&nbsp;<br />\r\nI would wait forever, exulted in the scene&nbsp;<br />\r\nAs long as I am with you, my heart continues to beat&nbsp;<br />\r\n<br />\r\n[Pre-Chorus: Jess Glynne]&nbsp;<br />\r\nWith every step we take, Kyoto to The Bay&nbsp;<br />\r\nStrolling so casually&nbsp;<br />\r\nWe&#39;re different and the same, gave you another name&nbsp;<br />\r\nSwitch up the batteries&nbsp;<br />\r\n<br />\r\n[Chorus: Jess Glynne]&nbsp;<br />\r\nIf you gave me a chance I would take it&nbsp;<br />\r\nIt&#39;s a shot in the dark but I&#39;ll make it&nbsp;<br />\r\nKnow with all of your heart, you can&#39;t shame me&nbsp;<br />\r\nWhen I am with you, there&#39;s no place I rather be&nbsp;<br />\r\nN-n-n-no, no, no, no place I rather be (3x)&nbsp;<br />\r\n<br />\r\n[Verse 2: Jess Glynne]&nbsp;<br />\r\nWe staked out on a mission to find our inner peace&nbsp;<br />\r\nMake it everlasting so nothing&#39;s incomplete&nbsp;<br />\r\nIt&#39;s easy being with you, sacred simplicity&nbsp;<br />\r\nAs long as we&#39;re together, there&#39;s no place I rather be&nbsp;<br />\r\n<br />\r\n[Pre-Chorus: Jess Glynne]&nbsp;<br />\r\n<br />\r\n[Chorus: Jess Glynne]&nbsp;<br />\r\n<br />\r\n[Bridge]&nbsp;<br />\r\nHmmmmmmmmmm, Hoooooooooo&nbsp;<br />\r\nBe (9x)&nbsp;<br />\r\nYeah-e-yeah-e-yeah-e-yeah-e-yeah, yeah, yeah&nbsp;<br />\r\n<br />\r\n[Chorus]</p>\r\n', 'Clean Bandit, Jess Glynne', 'Clean Bandit, Jess Glynne', 'Âu Mỹ', 'res/songs/Rather Be - Clean Bandit Jess Glynne.mp3', 675557),
(26, 'Thương Mấy Cũng Là Người Dưng', '<p>Th&igrave; th&ocirc;i anh nghĩ ta n&ecirc;n dừng lại khi l&ograve;ng em đ&atilde; chia hai,&nbsp;<br />\r\nV&igrave; anh đ&atilde; biết anh kh&ocirc;ng c&ograve;n l&agrave; người duy nhất em nhớ đ&ecirc;m qua,&nbsp;<br />\r\nH&atilde;y n&oacute;i anh nghe thật l&ograve;ng, em hết y&ecirc;u anh đ&uacute;ng kh&ocirc;ng?&nbsp;<br />\r\nCố nắm tay anh l&agrave;m chi nếu em phải đi.&nbsp;<br />\r\n<br />\r\nNgười thương đến mấy cũng sẽ đến ng&agrave;y, ng&agrave;y ch&uacute;ng ta th&agrave;nh người dưng&nbsp;<br />\r\nV&igrave; đ&acirc;u ai chỉ y&ecirc;u m&atilde;i một người, điều vĩnh viễn chẳng c&oacute; em ơi!&nbsp;<br />\r\nK&yacute; ức kh&ocirc;ng giữ ch&acirc;n người. Đ&acirc;u ai nhớ ai suốt đời.&nbsp;<br />\r\nNước mắt h&ocirc;m nay c&oacute; rơi cũng kh&ocirc;ng v&igrave; nhau nữa rồi.&nbsp;<br />\r\n<br />\r\nChorus:&nbsp;<br />\r\nCuộc đời l&agrave; thế quen nhau rồi qu&ecirc;n. Ph&uacute;t vui ng&agrave;y xưa sao nay buồn t&ecirc;nh?&nbsp;<br />\r\nV&igrave; người chẳng muốn ki&ecirc;n t&acirc;m gần b&ecirc;n. Ch&uacute;ng ta đ&atilde; kh&ocirc;ng v&igrave; nhau cố gắng.&nbsp;<br />\r\nL&ograve;ng người giờ gi&aacute; băng hơn m&ugrave;a đ&ocirc;ng. Những &acirc;n t&igrave;nh xưa xem như bằng kh&ocirc;ng.&nbsp;<br />\r\nLạnh l&ugrave;ng m&igrave;nh bước qua như người dưng. Ch&uacute;ng ta giờ đ&acirc;y chỉ l&agrave; một chuyện đ&atilde; từng.&nbsp;<br />\r\n<br />\r\nKODA:&nbsp;<br />\r\nM&ugrave;a c&acirc;y thay l&aacute;, em cũng thay l&ograve;ng, c&ugrave;ng với ai hạnh ph&uacute;c hay kh&ocirc;ng?&nbsp;<br />\r\nC&ograve;n ri&ecirc;ng anh vẫn đứng sau cuộc t&igrave;nh v&agrave; thấy thương dĩ v&atilde;ng sau lưng&nbsp;<br />\r\nNăm th&aacute;ng tr&ocirc;i qua thật nhiều, đ&atilde; khiến anh hiểu một điều:&nbsp;<br />\r\nEm vẫn m&atilde;i l&agrave; người dưng anh thương đến hết đời m&igrave;nh.</p>\r\n', 'Noo Phước Thịnh', 'Noo Phước Thịnh', 'Việt Nam, Nhạc Trẻ', 'res/songs/Thuong-May-Cung-La-Nguoi-Dung-Noo-Phuoc-Thinh.mp3', 4564),
(27, 'Counting Stars', '<p>Lately, I&#39;ve been, I&#39;ve been losing sleep<br />\r\nDreaming about the things that we could be<br />\r\nBut baby, I&#39;ve been, I&#39;ve been playing hard,<br />\r\nSitting, no more counting dollars<br />\r\nWe&#39;ll be counting stars, yeah we&#39;ll be counting stars.<br />\r\n<br />\r\nI see this life like a swinging vine<br />\r\nSwing my heart across the line<br />\r\nAnd my face is flashing signs<br />\r\nSeek it out and you shall find<br />\r\nOh, but I&#39;m not that old<br />\r\nYoung, but I&#39;m not that bold<br />\r\nI don&#39;t think the world is sold<br />\r\nI&#39;m just doing what we&#39;re told<br />\r\nI feel something so right<br />\r\nDoing the wrong thing<br />\r\nI feel something so wrong<br />\r\nDoing the right thing<br />\r\nI couldn&#39;t lie, couldn&#39;t lie, couldn&#39;t lie<br />\r\nEverything that kills me makes me feel alive<br />\r\n<br />\r\nLately, I&#39;ve been, I&#39;ve been losing sleep<br />\r\nDreaming about the things that we could be<br />\r\nBut baby, I&#39;ve been, I&#39;ve been playing hard,<br />\r\nSitting, no more counting dollars<br />\r\nWe&#39;ll be counting stars<br />\r\n<br />\r\nLately, I&#39;ve been, I&#39;ve been losing sleep<br />\r\nDreaming about the things that we could be<br />\r\nBut baby, I&#39;ve been, I&#39;ve been playing hard,<br />\r\nSitting, no more counting dollars<br />\r\nWe&#39;ll be, we&#39;ll be counting stars.<br />\r\n<br />\r\nI feel the love and I feel it burn<br />\r\nDown this river, every turn<br />\r\nHope is a four-letter word<br />\r\nMake that money, watch it burn<br />\r\nOh, but I&#39;m not that old<br />\r\nYoung, but I&#39;m not that bold<br />\r\nI don&#39;t think the world is sold<br />\r\nI&#39;m just doing what we&#39;re told<br />\r\nI feel something so wrong<br />\r\nDoing the right thing<br />\r\nI couldn&#39;t lie, couldn&#39;t lie, couldn&#39;t lie<br />\r\nEverything that downs me makes me wanna fly<br />\r\n<br />\r\nLately, I&#39;ve been, I&#39;ve been losing sleep<br />\r\nDreaming about the things that we could be<br />\r\nBut baby, I&#39;ve been, I&#39;ve been playing hard,<br />\r\nSitting, no more counting dollars<br />\r\nWe&#39;ll be counting stars<br />\r\n<br />\r\nLately, I&#39;ve been, I&#39;ve been losing sleep<br />\r\nDreaming about the things that we could be<br />\r\nBut baby, I&#39;ve been, I&#39;ve been playing hard,<br />\r\nSitting, no more counting dollars<br />\r\nWe&#39;ll be, we&#39;ll be counting stars<br />\r\n<br />\r\nTake that money<br />\r\nWatch it burn<br />\r\nSing in the river<br />\r\nThe lessons are learnt<br />\r\n<br />\r\nTake that money<br />\r\nWatch it burn<br />\r\nSing in the river<br />\r\nThe lessons are learnt<br />\r\n<br />\r\nTake that money<br />\r\nWatch it burn<br />\r\nSing in the river<br />\r\nThe lessons are learnt<br />\r\n<br />\r\nTake that money<br />\r\nWatch it burn<br />\r\nSing in the river<br />\r\nThe lessons are learnt<br />\r\n<br />\r\nEverything that kills me makes feel alive<br />\r\n<br />\r\nLately, I&#39;ve been, I&#39;ve been losing sleep<br />\r\nDreaming about the things that we could be<br />\r\nBut baby, I&#39;ve been, I&#39;ve been playing hard,<br />\r\nSitting, no more counting dollars<br />\r\nWe&#39;ll be counting stars<br />\r\n<br />\r\nLately, I&#39;ve been, I&#39;ve been losing sleep<br />\r\nDreaming about the things that we could be<br />\r\nBut baby, I&#39;ve been, I&#39;ve been playing hard,<br />\r\nSitting, no more counting dollars<br />\r\nWe&#39;ll be, we&#39;ll be, counting stars.<br />\r\n<br />\r\nTake that money<br />\r\nWatch it burn<br />\r\nSing in the river<br />\r\nThe lessons are learnt<br />\r\n<br />\r\nTake that money<br />\r\nWatch it burn<br />\r\nSing in the river<br />\r\nThe lessons are learnt<br />\r\n<br />\r\nTake that money<br />\r\nWatch it burn<br />\r\nSing in the river<br />\r\nThe lessons are learnt<br />\r\n<br />\r\nTake that money<br />\r\nWatch it burn<br />\r\nSing in the river<br />\r\nThe lessons are learnt.</p>\r\n', 'One Republic', 'One Republic', 'Âu Mỹ, Pop', 'res/songs/Counting Stars - OneRepublic.mp3', 97544);

