INSERT INTO `tinhot` (`ID`, `TieuDe`, `NoiDung`, `TomTat`, `ChuDe`, `AnhDaiDien`) VALUES
(4, 'Từ bỏ dòng nhạc \"già\", Nguyễn Hồng Ân tìm đến nhạc trẻ để làm mới mình', '<p><strong>Lột x&aacute;c khỏi phong c&aacute;ch quen thuộc, Nguyễn Hồng &Acirc;n muốn thử sức với d&ograve;ng nhạc trẻ trung để đến gần với kh&aacute;n giả hơn.</strong></p>\r\n\r\n<p>Ca kh&uacute;c mới toanh vừa được bổ sung v&agrave;o kho nhạc của nam ca sĩ điển trai<strong>&nbsp;<a href=\"http://tinnhac.com/nguyen-hong-an-lam-album-tuong-nho-co-nhac-si-anh-bang-54275.html\" target=\"_blank\">Nguyễn Hồng &Acirc;n</a></strong>vừa được ch&iacute;nh thức tr&igrave;nh l&agrave;ng người h&acirc;m mộ. Single&nbsp;<em>&quot;M&ugrave;a đ&atilde; xa&quot;</em>&nbsp;ch&iacute;nh l&agrave; ca kh&uacute;c đ&aacute;nh dấu bước chuyển m&igrave;nh đầy mới mẻ của Nguyễn Hồng &Acirc;n trong d&ograve;ng nhạc mới.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/batch-nha-4-14957676-4d92.jpg\" style=\"height:900px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Nguyễn Hồng &Acirc;n muốn l&agrave;m mới m&igrave;nh trong một ca kh&uacute;c nhạc trẻ</em></p>\r\n\r\n<p>Theo chia sẻ, MV<em>&nbsp;&ldquo;M&ugrave;a đ&atilde; xa&rdquo;</em>&nbsp;ch&iacute;nh l&agrave; sản phẩm đầu tay m&agrave;&nbsp;<strong><a href=\"http://tinnhac.com/nguyen-hong-an-lam-mv-nhac-trinh-54705.html\" target=\"_blank\">Nguyễn Hồng &Acirc;n</a></strong>&nbsp;kết hợp c&ugrave;ng nhạc sĩ trẻ Ti&ecirc;n Nguyễn để thực hiện. Kh&aacute; đắn đo v&agrave; lo lắng liệu rằng kh&aacute;n giả c&oacute; qu&aacute; shock với phong c&aacute;ch mới, nam ca sĩ vẫn quyết t&acirc;m đổi mới m&igrave;nh trong sản phẩm &acirc;m nhạc n&agrave;y để khẳng định khả năng biến chuyển trong d&ograve;ng nhạc, h&igrave;nh ảnh.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/2.jpg\" style=\"height:900px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Những h&igrave;nh ảnh mới nhất của nam ca sĩ tại Đ&agrave; Lạt</em></p>\r\n\r\n<p>Đ&acirc;y cũng l&agrave; sản phẩm đ&aacute;nh dấu ng&agrave;y trở lại của&nbsp;<strong><a href=\"http://tinnhac.com/nguyen-hong-an-doanh-thu-ban-album-moi-se-de-lam-tu-thien-25780.html\" target=\"_blank\">Nguyễn Hồng &Acirc;n</a></strong>&nbsp;sau khoảng thời gian d&agrave;i vắng b&oacute;ng để tập trung cho kinh doanh v&agrave; việc c&aacute; nh&acirc;n. &ldquo;L&aacute;nh&rdquo; truyền th&ocirc;ng một phần cũng v&igrave; bản th&acirc;n nam ca sĩ cảm thấy chưa c&oacute; g&igrave; thực sự mới mẻ để trở lại. Tuy nhi&ecirc;n, phần lớn l&agrave; do con đường anh lựa chọn kh&ocirc;ng ồn &agrave;o, x&ocirc; bồ như những g&igrave; truyền th&ocirc;ng mong muốn n&ecirc;n anh thường lựa chọn l&ugrave;i bước v&agrave; giữ m&igrave;nh bước chậm. Đ&oacute; cũng l&agrave; c&aacute;ch Nguyễn Hồng &Acirc;n tự khiến bản th&acirc;n vui v&agrave; thanh thản hơn trong con đường &acirc;m nhạc vốn nhiều ch&ocirc;ng gai m&agrave; anh lựa chọn.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/3.jpg\" style=\"height:899px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Đ&acirc;y l&agrave; sản phẩm đ&aacute;nh dấu cho sự trở lại của Nguyễn Hồng &Acirc;n sau thời gian vắng b&oacute;ng</em></p>\r\n\r\n<p>MV của<em>&nbsp;&ldquo;M&ugrave;a đ&atilde; xa&rdquo;</em>&nbsp;được nam ca sĩ lựa chọn th&agrave;nh phố hoa Đ&agrave; Lạt để thực hiện c&aacute;c cảnh quay. Với những thước phim nhẹ nh&agrave;ng nhưng kh&ocirc;ng k&eacute;m phần l&atilde;ng mạn, ca kh&uacute;c đang nhận được rất nhiều phản hồi t&iacute;ch cực từ ph&iacute;a kh&aacute;n giả.</p>\r\n', 'Lột xác khỏi phong cách quen thuộc, Nguyễn Hồng Ân muốn thử sức với dòng nhạc trẻ trung để đến gần với khán giả hơn.', 'Việt Nam', 'res/images/tinhot/2.jpg'),
(5, 'Quang Dũng tích cực tập luyện cho liveshow tâm huyết', '<p><strong>Nam ca sĩ d&agrave;nh nhiều thời gian tập luyện cho đ&ecirc;m liveshow được ấp tủ từ l&acirc;u &ldquo;Giấc mơ mang t&ecirc;n m&igrave;nh&rdquo;.</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/8/02-199c.jpg\" style=\"height:399px; width:600px\" /></p>\r\n\r\n<p>Đ&acirc;y l&agrave; một liveshow ho&agrave;nh tr&aacute;ng khi được tổ chức tại 3 địa điểm l&agrave; S&agrave;i G&ograve;n, H&agrave; Nội v&agrave; Mỹ.&nbsp;<strong><a href=\"http://tinnhac.com/nghe-si/quang-dung.html\" target=\"_blank\">Quang Dũng</a></strong>&nbsp;hiện đang tất bật với lịch tập luyện mỗi ng&agrave;y để mang lại những tiết mục ấn tượng nhất cho kh&aacute;n giả.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/8/05-a795.jpg\" style=\"height:840px; width:600px\" /></p>\r\n\r\n<p>Quang Dũng cũng tiết lộ rằng anh phải l&agrave;m việc 6 th&aacute;ng liền với h&agrave;ng loạt nhạc sĩ như Việt Anh, Anh Khoa, Trần Nhật Minh để chuẩn bị cho đ&ecirc;m nhạc n&agrave;y.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/8/15-2e9b.jpg\" style=\"height:399px; width:600px\" /></p>\r\n\r\n<p><em>Ca sĩ Quang Dũng c&ugrave;ng ekip thực hiện liveshow</em></p>\r\n\r\n<p>Đạo diễn Phạm Ho&agrave;ng Nam &ndash; người phụ tr&aacute;ch d&agrave;n dựng liveshow cho biết rằng sẽ kh&ocirc;ng l&agrave;m biến chất h&igrave;nh ảnh m&agrave; Quang Dũng x&acirc;y dựng suốt nhiều năm qua. Thay v&agrave;o đ&oacute;, ekip sẽ l&agrave;m bật l&ecirc;n gi&aacute; trị giao thoa giữa c&aacute;i cũ v&agrave; mới trong từng tiết mục của đ&ecirc;m nhạc.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/8/22-a709.jpg\" style=\"height:840px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/8/26-21a2.jpg\" style=\"height:840px; width:600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&aacute;ch mời tham gia biểu diễn trong liveshow của&nbsp;<strong>Quang Dũng</strong>&nbsp;gồm: Hồng Nhung, Hồng Ngọc, Lệ Quy&ecirc;n, Mỹ T&acirc;m, Thanh Thảo v&agrave; Phạm Thu H&agrave;. Họ ch&iacute;nh l&agrave; những nữ ca sĩ từng song ca v&agrave; gắn b&oacute; với Quang Dũng từ ng&agrave;y đầu ca h&aacute;t.</p>\r\n', '\r\nNam ca sĩ dành nhiều thời gian tập luyện cho đêm liveshow được ấp tủ từ lâu “Giấc mơ mang tên mình”.', 'Việt Nam', 'res/images/tinhot/02-199c.jpg'),
(6, 'Được xem là Bài hát hiện tượng nhưng “Anh cứ đi đi” vẫn vắng mặt ở Cống hiến 2017', '<p><strong>Theo Ban tổ chức của giải thưởng &Acirc;m nhạc Cống hiến 2017, mặc d&ugrave; rất hot nhưng ca kh&uacute;c &ldquo;Anh cứ đi đi&rdquo; kh&ocirc;ng c&oacute; nhiều s&aacute;ng tạo.</strong></p>\r\n\r\n<p>C&oacute; thể n&oacute;i,&nbsp;<em>&ldquo;Anh cứ đi đi&rdquo;</em>&nbsp;l&agrave; bản hit đưa t&ecirc;n tuổi của&nbsp;<a href=\"http://tinnhac.com/nghe-si/hari-won.html\" target=\"_blank\">Hari Won</a>&nbsp;l&ecirc;n tầm cao mới, gi&uacute;p c&ocirc; c&oacute; chỗ đứng nhất định tr&ecirc;n thị trường nhạc Việt. Để đến thời điểm hiện tại, khi nhắc đến Hari Won, người h&acirc;m mộ c&oacute; thể tự h&agrave;o gọi t&ecirc;n&nbsp;<em>&ldquo;Anh cứ đi đi&rdquo;</em>&nbsp;v&agrave; loạt th&agrave;nh t&iacute;ch ấn tượng m&agrave; ca kh&uacute;c n&agrave;y đạt được.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/9/1-9c04.jpg\" style=\"height:337px; width:600px\" /></p>\r\n\r\n<p><em>&quot;Anh cứ đi đi&quot; l&agrave; bản hit ấn tượng trong sự nghiệp ca h&aacute;t của Hari Won</em></p>\r\n\r\n<p><em>&ldquo;Anh cứ đi đi&rdquo;</em>&nbsp;l&agrave; s&aacute;ng t&aacute;c của nhạc sĩ Vương Anh T&uacute;, n&oacute;i về t&acirc;m sự của một c&ocirc; g&aacute;i đứng trước sự tan vỡ của mối t&igrave;nh m&agrave; c&ocirc; d&agrave;nh hết con tim. Lời ca buồn da diết, thể hiện đ&uacute;ng t&acirc;m trạng của những người đang thất t&igrave;nh khiến cho ca kh&uacute;c n&agrave;y nhanh ch&oacute;ng chiếm được sự đồng cảm của kh&aacute;n giả. V&agrave; phải n&oacute;i rằng, ngo&agrave;i điểm cộng của ca từ v&agrave; giai điệu, giọng h&aacute;t đầy tiến bộ v&agrave; gi&agrave;u cảm x&uacute;c của Hari Won khi thể hiện đ&atilde; gi&uacute;p cho ca kh&uacute;c n&agrave;y trở n&ecirc;n c&oacute; hồn hơn bao giờ hết.</p>\r\n\r\n<p>Cũng giống như&nbsp;<em>&ldquo;Sau tất cả&rdquo;, &ldquo;Anh cứ đi đi&rdquo;</em>&nbsp;c&oacute; một hấp lực kh&oacute; l&yacute; giải khi n&oacute; nhanh ch&oacute;ng trở th&agrave;nh một bản hit được mọi người y&ecirc;u mến, với đầy đủ mọi độ tuổi, đối tượng. Tr&agrave;o lưu cover&nbsp;<em>&ldquo;Anh cứ đi đi&rdquo;</em>&nbsp;cũng được nhiều nghệ sĩ v&agrave; c&aacute;c bạn trẻ h&agrave;o hứng tham gia. Với hơn 135 triệu lượt nghe,&nbsp;<em>&ldquo;Anh cứ đi đi&rdquo;</em>&nbsp;xứng đ&aacute;ng l&agrave; một trong những bản hit ấn tượng của Vpop cũng như trong sự nghiệp ca h&aacute;t vốn kh&aacute; nhạt nh&ograve;a từ trước tới nay của Hari Won.</p>\r\n\r\n<p>Ấn tượng l&agrave; thế, &ldquo;tạo b&atilde;o&rdquo; l&agrave;ng nhạc Việt l&agrave; thế, tuy nhi&ecirc;n ca kh&uacute;c&nbsp;<em>&ldquo;Anh cứ đi đi&rdquo;</em>&nbsp;lại kh&ocirc;ng lọt v&agrave;o danh s&aacute;ch đề cử của hạng mục B&agrave;i h&aacute;t của năm tại&nbsp;<a href=\"http://tinnhac.com/su-kien/giai-thuong-am-nhac-cong-hien-12-2017-520.html\" target=\"_blank\">giải thưởng &Acirc;m nhạc Cống hiến</a>&nbsp;năm nay. Trong khi đ&oacute;, một ca kh&uacute;c mới v&agrave; c&oacute; phần k&eacute;n người nghe như&nbsp;<a href=\"http://tinnhac.com/truong-thao-nhi-vuong-nghi-an-dao-nhai-tai-sing-my-song-91814.html\" target=\"_blank\">&ldquo;Tự sự tuổi 25&rdquo; của Trương Thảo Nhi</a>&nbsp;lại lọt v&agrave;o đề cử, b&ecirc;n cạnh c&aacute;c ca kh&uacute;c đ&igrave;nh đ&aacute;m kh&aacute;c như&nbsp;<em>&ldquo;Gửi anh xa nhớ&rdquo;</em>&nbsp;(B&iacute;ch Phương),&nbsp;<em>&ldquo;Mơ&rdquo;</em>&nbsp;(Vũ C&aacute;t Tường),&nbsp;<em>&ldquo;&Ocirc;ng b&agrave; anh&rdquo;</em>&nbsp;(L&ecirc; Thiện Hiếu),&nbsp;<em>&ldquo;Sau tất cả&rdquo;</em>&nbsp;(Erik). Điều n&agrave;y khiến cho nhiều kh&aacute;n giả, đặc biệt l&agrave; fan của Hari Won cảm thấy kh&oacute; hiểu v&agrave; rất thắc mắc.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/9/2-785c.jpg\" style=\"height:600px; width:600px\" /></p>\r\n\r\n<p><em>&quot;Thiếu t&iacute;nh s&aacute;ng tạo, kh&ocirc;ng c&oacute; g&igrave; đổi mới&quot; ch&iacute;nh l&agrave; l&yacute; do khiến &quot;Anh cứ đi đi&quot; vắng mặt tại Cống hiến 2017</em></p>\r\n\r\n<p>Giải đ&aacute;p l&yacute; do&nbsp;<em>&ldquo;Anh cứ đi đi&rdquo;</em>&nbsp;vắng mặt ở Cống hiến 2017, đại diện BTC giải thưởng năm nay cho biết hit của Hari l&agrave; một b&agrave;i h&aacute;t qu&aacute; b&igrave;nh thường, kh&ocirc;ng c&oacute; sự t&igrave;m t&ograve;i, s&aacute;ng tạo v&agrave; đ&oacute;ng g&oacute;p thiết thực cho sự phong ph&uacute;, ph&aacute;t triển của đời sống &acirc;m nhạc.&nbsp;<em>&ldquo;Kh&ocirc;ng phải b&agrave;i h&aacute;t n&agrave;o được nghe nhiều cũng c&oacute; t&ecirc;n ở giải Cống hiến. V&agrave; kh&ocirc;ng phải những b&agrave;i h&aacute;t được nghe nhiều đ&atilde; c&oacute; gi&aacute; trị nghệ thuật&rdquo;</em>&nbsp;&ndash; BTC khẳng định. Trong khi đ&oacute;, mặc d&ugrave; mới v&agrave; k&eacute;n người nghe, nhưng theo nhạc sĩ Gi&aacute;ng Son,&nbsp;<em>&ldquo;Tự sự tuổi 25&rdquo;</em>&nbsp;của Trương Thảo Nhi lại l&agrave; c&aacute; t&iacute;nh ri&ecirc;ng biệt v&agrave; mang một hơi thở mới cho l&agrave;ng nhạc trẻ hiện tại.</p>\r\n', 'Theo Ban tổ chức của giải thưởng Âm nhạc Cống hiến 2017, mặc dù rất hot nhưng ca khúc “Anh cứ đi đi” không có nhiều sáng tạo.', 'Việt Nam', 'res/images/tinhot/2-785c.jpg'),
(7, 'Khán giả sững sờ với thần thái diva như ngày nào của Celine Dion tại BBMAs 2017', '<p><strong>Nhan sắc, sức khỏe c&oacute; phai m&ograve;n nhưng thần th&aacute;i diva ấy vẫn bất tử theo thời gian.</strong></p>\r\n\r\n<p>Mặc d&ugrave; Drake l&agrave; người chiến thắng tại Billboad Music Awards 2017 với kỷ lục 13 giải thưởng (hơn Adele 12 giải), nhưng&nbsp;<strong><a href=\"http://tinnhac.com/celine-dion-khien-fan-nghen-ngao-khi-hat-lai-nhac-phim-titanic-tai-bbmas-2017-99086.html\" target=\"_blank\">Celine Dion</a></strong>&nbsp;mới ch&iacute;nh l&agrave; t&acirc;m điểm thu h&uacute;t mọi sự ch&uacute; &yacute; của lễ trao giải năm nay. Xuất hiện tr&ecirc;n s&acirc;n khấu với bộ v&aacute;y trắng được v&iacute; von như chỉ d&agrave;nh cho nữ ho&agrave;ng, Celine khiến nhiều người qu&ecirc;n mất rằng c&ocirc; sắp bước sang tuổi 50 với gần 33 năm đứng tr&ecirc;n s&acirc;n khấu.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/10/celine-dion7-26db.jpg\" style=\"height:399px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Celine Dion khiến c&ocirc;ng ch&uacute;ng sững sờ trước thần th&aacute;i diva vẹn nguy&ecirc;n như ng&agrave;y n&agrave;o</em></p>\r\n\r\n<p>C&oacute; mặt trong buổi lễ, diva người Canada đ&atilde; tr&igrave;nh diễn lại bản hit&nbsp;<em>&quot;My Heart Will Go On&quot;</em>&nbsp;- ca kh&uacute;c chủ đề của bộ phim Titanic. Kể từ ng&agrave;y ph&aacute;t h&agrave;nh(th&aacute;ng 12/1997), đến nay ca kh&uacute;c vinh dự được xếp v&agrave;o h&agrave;ng kinh điển, trong khi những diễn vi&ecirc;n như Leonardo DiCaprio v&agrave; Kate Winslet một bước vụt s&aacute;ng th&agrave;nh sao bự của l&agrave;ng giải tr&iacute;.</p>\r\n\r\n<p>Mặc d&ugrave; 20 năm đ&atilde; tr&ocirc;i qua nhưng&nbsp;<em>&quot;My Heart Will Go On&quot;</em>&nbsp;dường như vẫn tr&agrave;n ngập cảm x&uacute;c v&agrave; đầy sức mạnh qua tiếng h&aacute;t của Celine. Nữ danh ca cảm rất thấy hạnh ph&uacute;c v&igrave; khi lời ca kh&uacute;c cất l&ecirc;n, tất cả kh&aacute;n giả đ&atilde; h&ograve;a giọng c&ugrave;ng c&ocirc;. Điều đ&oacute; khiến m&agrave;n tr&igrave;nh diễn trở n&ecirc;n đặc biệt v&agrave; ngay sau đ&oacute;, Celine khẳng định kh&ocirc;ng bao giờ qu&ecirc;n gi&acirc;y ph&uacute;t n&agrave;y.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/10/celine-dion-billboar-c947.jpg\" style=\"height:348px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Nữ danh ca muốn h&aacute;t lại hit cũ để d&agrave;nh tặng chồng qu&aacute; cố&nbsp;</em></p>\r\n\r\n<p>Được biết, b&ecirc;n cạnh &yacute; nghĩa kỷ niệm 20 năm phim Titanic, đ&acirc;y c&ograve;n l&agrave; tiết mục m&agrave; Celine Dion muốn d&agrave;nh tặng người chồng qu&aacute; cố của m&igrave;nh - Ren&eacute; Ang&eacute;lil, đ&atilde; qua đời ở tuổi 73 v&igrave; chứng bệnh ung thư qu&aacute;i &aacute;c.</p>\r\n\r\n<p>Celine thẳng thắn chia sẻ:&nbsp;<em>&ldquo;T&ocirc;i thực sự, thực sự muốn chứng minh cho c&aacute;c con t&ocirc;i thấy rằng mẹ của ch&uacute;ng rất mạnh mẽ. Ren&eacute; lu&ocirc;n muốn cuộc sống của ch&uacute;ng t&ocirc;i tiếp tục một c&aacute;ch b&igrave;nh thường. Bạn biết đấy, t&ocirc;i đ&atilde; mất đi t&igrave;nh y&ecirc;u của cuộc đời m&igrave;nh. T&ocirc;i sẽ nhớ về h&igrave;nh ảnh tuyệt vời của anh ấy v&agrave; kh&ocirc;ng muốn giữ lại những k&yacute; ức tr&agrave;n ngập đau đớn kia. T&ocirc;i kh&ocirc;ng thể &iacute;ch kỷ như vậy. Phải để anh ấy ra đi được thanh thản. T&ocirc;i cảm nhận được sự b&igrave;nh y&ecirc;n</em>&rdquo;.</p>\r\n\r\n<p>Sau m&agrave;n biểu diễn, Celine Dion đ&atilde; kh&ocirc;ng giấu nổi sự x&uacute;c động. H&agrave;ng ng&agrave;n kh&aacute;n giả cũng đồng loạt đứng l&ecirc;n v&agrave; d&agrave;nh cho nữ diva những tr&agrave;ng vỗ tay tại lễ trao giải Billboard Music Awards 2017.</p>\r\n\r\n<p><strong>Một số h&igrave;nh ảnh của Celina Dion tại Billboard Music Awards 2017:</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/10/celine-dion-billboar-ee67.jpg\" style=\"height:837px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/10/celine-dion-billboar-f747.jpg\" style=\"height:834px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/10/12-6dd3.jpg\" style=\"height:843px; width:600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Nhan sắc, sức khỏe có phai mòn nhưng thần thái diva ấy vẫn bất tử theo thời gian.', 'Âu Mỹ', 'res/images/tinhot/celine-dion7-26db.jpg'),
(8, 'Vượt mặt Adele, Drake lập kỷ lục giải thưởng tại Billboard Music Awards 2017', '<p><strong>Rapper da m&agrave;u đ&atilde; chiến thắng ở hầu hết c&aacute;c giải thưởng quan trọng của Billboard năm nay.</strong></p>\r\n\r\n<p>Đ&ecirc;m qua, v&agrave;o l&uacute;c 20 giờ ng&agrave;y 21/5 (giờ địa phương), lễ trao giải&nbsp;<strong><a href=\"http://tinnhac.com/selena-gomez-se-thoi-tung-san-khau-billboard-music-awards-2017-voi-single-moi-98824.html\" target=\"_blank\">Billboard Music Awards 2017</a></strong>diễn ra tại s&acirc;n khấu T-Mobile Arena ở Las Vegas, Mỹ với sự g&oacute;p mặt của đ&ocirc;ng đảo nghệ sĩ nổi tiếng đến từ khắp mọi nơi tr&ecirc;n thế giới.&nbsp;Với việc gi&agrave;nh 13 giải trong số 22 đề cử (giống như The Chainsmokers), Drake đ&atilde; vượt qua nữ ca sĩ người Anh - Adele (12 giải) để trở th&agrave;nh nghệ sĩ đầu ti&ecirc;n gi&agrave;nh được nhiều giải nhất trong một năm.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/11/drake-bbmas-2017-sho-1e80.jpg\" style=\"height:397px; width:600px\" /></p>\r\n\r\n<p>Nam rapper da m&agrave;u đ&atilde; thắng hầu hết ở những giải quan trọng như &quot;Nghệ sĩ xuất sắc nhất&quot;, &quot;Nghệ sĩ nam xuất sắc nhất&quot;, &quot;Nghệ sĩ xuất sắc nhất Billboard 200&quot;, &quot;Nghệ sĩ xuất sắc nhất Billboard Hot 100&quot;, &quot;Nghệ sĩ c&oacute; ca kh&uacute;c b&aacute;n chạy nhất&quot;,....Trong khi đ&oacute;, ở hạng mục gi&agrave;nh cho nữ, Beyonc&eacute; thừa thắng x&ocirc;ng l&ecirc;n với c&aacute;c giải &quot;Nữ nghệ sĩ xuất sắc nhất&quot;, &quot;Nghệ sĩ R&amp;B xuất sắc nhất&quot; hay &quot;Nghệ sĩ c&oacute; tour ho&agrave;nh tr&aacute;ng nhất&quot;,...</p>\r\n\r\n<p>Hai nh&oacute;m nhạc Twenty One Pilots v&agrave; The Chainsmokers cũng chiến thắng ở những giải sở trường. Giải nghệ sĩ mới năm nay thuộc về Zayn Malik - cựu th&agrave;nh vi&ecirc;n One Direction; Blake Shelton thắng giải &quot;Nghệ sĩ nhạc đồng qu&ecirc; xuất sắc nhất&quot;.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, đ&aacute;ng ch&uacute; &yacute; nhất phải kể đến việc nh&oacute;m nhạc BTS đến từ H&agrave;n Quốc đ&atilde; vượt qua một loạt t&ecirc;n tuổi kh&aacute;c như Justin Bieber, Selena Gomez, Ariana Grande hay Shawn Mendes để ẵm lấy giải thưởng &quot;Top Social Artist&quot; do gi&agrave;nh số phiếu bầu vượt trội. Đ&acirc;y thực sự l&agrave; một bất ngờ chưa c&oacute; trong tiền lệ lễ trao giải Billboard Music Awards cho một nh&oacute;m nhạc đến từ ch&acirc;u &Aacute;.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/11/bts-bbmas-2017-carpe-8276.jpg\" style=\"height:396px; width:600px\" /></p>\r\n\r\n<p><strong>To&agrave;n bộ danh s&aacute;ch đề cử, kết quả thắng giải cập nhật từ Billboard:</strong></p>\r\n\r\n<p><strong>Top Artist:</strong><br />\r\nAdele<br />\r\nBeyonce<br />\r\nJustin Bieber<br />\r\nThe Chainsmokers<br />\r\n<strong>Drake -- WINNER</strong><br />\r\nAriana Grande<br />\r\nShawn Mendes<br />\r\nRihanna<br />\r\nTwenty One Pilots<br />\r\nThe Weeknd</p>\r\n\r\n<p><strong>Top New Artist:</strong><br />\r\nAlessia Cara<br />\r\nDesiigner<br />\r\nLil Uzi Vert<br />\r\nLukas Graham<br />\r\n<strong>Zayn -- WINNER</strong></p>\r\n\r\n<p><strong>Billboard Chart Achievement Award:</strong><br />\r\nLuke Bryan<br />\r\nNicki Minaj<br />\r\nThe Chainsmokers<br />\r\nThe Weeknd<br />\r\n<strong>Twenty One Pilots -- WINNER</strong><br />\r\n<br />\r\n<strong>Top Male Artist:</strong><br />\r\nJustin Bieber<br />\r\n<strong>Drake -- WINNER</strong><br />\r\nFuture<br />\r\nShawn Mendes<br />\r\nThe Weeknd</p>\r\n\r\n<p><strong>Top Female Artist:</strong><br />\r\nAdele<br />\r\n<strong>Beyonce -- WINNER</strong><br />\r\nAriana Grande<br />\r\nRihanna<br />\r\nSia</p>\r\n\r\n<p><strong>Top Duo/Group:</strong><br />\r\nThe Chainsmokers<br />\r\nColdplay<br />\r\nFlorida Georgia Line<br />\r\nGuns N&rsquo; Roses<br />\r\n<strong>Twenty One Pilots -- WINNER</strong></p>\r\n\r\n<p><strong>Top Billboard 200 Artist:</strong><br />\r\nBeyonce<br />\r\n<strong>Drake -- WINNER</strong><br />\r\nPrince<br />\r\nTwenty One Pilots<br />\r\nThe Weeknd</p>\r\n\r\n<p><strong>Top Hot 100 Artist:</strong><br />\r\nThe Chainsmokers<br />\r\n<strong>Drake -- WINNER</strong><br />\r\nRihanna<br />\r\nTwenty One Pilots<br />\r\nThe Weeknd</p>\r\n\r\n<p><strong>Top Song Sales Artist:</strong><br />\r\nThe Chainsmokers<br />\r\n<strong>Drake -- WINNER</strong><br />\r\nPrince<br />\r\nJustin Timberlake<br />\r\nTwenty One Pilots</p>\r\n\r\n<p><strong>Top Radio Songs Artist:</strong><br />\r\nJustin Bieber<br />\r\nThe Chainsmokers<br />\r\nDrake<br />\r\nRihanna<br />\r\n<strong>Twenty One Pilots -- WINNER</strong></p>\r\n\r\n<p><strong>Top Streaming Songs Artist:</strong><br />\r\nThe Chainsmokers<br />\r\nDesiigner<br />\r\n<strong>Drake -- WINNER</strong><br />\r\nRihanna<br />\r\nTwenty One Pilots</p>\r\n\r\n<p><strong>Top Social Artist:</strong><br />\r\nJustin Bieber<br />\r\nBTS -- WINNER<br />\r\nSelena Gomez<br />\r\nAriana Grande<br />\r\nShawn Mendes</p>\r\n\r\n<p><strong>Top Touring Artist:</strong><br />\r\nJustin Bieber<br />\r\n<strong>Beyonce -- WINNER</strong><br />\r\nColdplay<br />\r\nGuns N&rsquo; Roses<br />\r\nBruce Springsteen &amp; The E Street Band</p>\r\n\r\n<p><strong>Top R&amp;B Artist:</strong><br />\r\n<strong>Beyonce -- WINNER</strong><br />\r\nBruno Mars<br />\r\nFrank Ocean<br />\r\nRihanna<br />\r\nThe Weeknd</p>\r\n\r\n<p><strong>Top R&amp;B Tour:</strong><br />\r\n<strong>Beyonce -- WINNER</strong><br />\r\nLionel Richie<br />\r\nRihanna</p>\r\n\r\n<p><strong>Top Rap Artist:</strong><br />\r\nJ. Cole<br />\r\nDesiigner<br />\r\n<strong>Drake -- WINNER</strong><br />\r\nFuture<br />\r\nRae Sremmurd</p>\r\n\r\n<p><strong>Top Rap Tour</strong><br />\r\n<strong>Drake -- WINNER</strong><br />\r\nFuture<br />\r\nKanye West</p>\r\n\r\n<p><strong>Top Country Artist:</strong><br />\r\nFlorida Georgia Line<br />\r\n<strong>Blake Shelton -- WINNER</strong><br />\r\nKeith Urban<br />\r\nChris Stapleton<br />\r\nJason Aldean</p>\r\n\r\n<p><strong>Top Country Tour:</strong><br />\r\nLuke Bryan<br />\r\n<strong>Kenny Chesney -- WINNER</strong><br />\r\nDixie Chicks</p>\r\n\r\n<p><strong>Top Rock Artist:</strong><br />\r\nColdplay<br />\r\nThe Lumineers<br />\r\nMetallica<br />\r\n<strong>Twenty One Pilots -- WINNER</strong><br />\r\nX Ambassadors</p>\r\n\r\n<p><strong>Top Rock Tour:</strong><br />\r\n<strong>Coldplay -- WINNER</strong><br />\r\nGuns N&rsquo; Roses<br />\r\nBruce Springsteen &amp; The E Street Band</p>\r\n\r\n<p><strong>Top Latin Artist:</strong><br />\r\nJ Balvin<br />\r\n<strong>Juan Gabriel -- WINNER</strong><br />\r\nLos Plebes Del Rancho De Ariel Camacho<br />\r\nMaluma<br />\r\nNicky Jam</p>\r\n\r\n<p><strong>Top Dance/Electronic Artist:</strong><br />\r\n<strong>The Chainsmokers -- WINNER</strong><br />\r\nCalvin Harris<br />\r\nMajor Lazer<br />\r\nDJ Snake<br />\r\nLindsey Stirling</p>\r\n\r\n<p><strong>Top Christian Artist:</strong><br />\r\n<strong>Lauren Daigle -- WINNER</strong><br />\r\nHillsong Worship<br />\r\nHillary Scott &amp; the Family<br />\r\nSkillet<br />\r\nChris Tomlin</p>\r\n\r\n<p><strong>Top Gospel Artist:</strong><br />\r\nJekalyn Carr<br />\r\n<strong>Kirk Franklin -- WINNER</strong><br />\r\nTravis Greene<br />\r\nTamela Mann<br />\r\nHezekiah Walker</p>\r\n\r\n<p><strong>Top Billboard 200 Album:</strong><br />\r\nBeyonce, Lemonade<br />\r\n<strong>Drake, Views -- WINNER</strong><br />\r\nRihanna, Anti<br />\r\nTwenty One Pilots, Blurryface<br />\r\nThe Weeknd, Starboy</p>\r\n\r\n<p><strong>Top Soundtrack/Cast Album:</strong><br />\r\n<strong>Hamilton: An American Musical -- WINNER</strong><br />\r\nMoana<br />\r\nPurple Rain<br />\r\nSuicide Squad: The Album<br />\r\nTrolls</p>\r\n\r\n<p><strong>Top R&amp;B Album:</strong><br />\r\n<strong>Beyonce, Lemonade -- WINNER</strong><br />\r\nBruno Mars, 24K Magic<br />\r\nFrank Ocean, Blonde<br />\r\nRihanna, Anti<br />\r\nThe Weeknd, Starboy</p>\r\n\r\n<p><strong>Top Rap Album:</strong><br />\r\nJ. Cole, 4 Your Eyez Only<br />\r\n<strong>Drake, Views -- WINNER</strong><br />\r\nKevin Gates, Isla<br />\r\nDJ Khaled, Major Key<br />\r\nA Tribe Called Quest, We Got It From Here&hellip;Thank You For Your Service</p>\r\n\r\n<p><strong>Top Country Album:</strong><br />\r\nJason Aldean, They Don&rsquo;t Know<br />\r\nFlorida Georgia Line, Dig Your Roots<br />\r\nBlake Shelton, If I&rsquo;m Honest<br />\r\n<strong>Chris Stapleton, Traveller -- WINNER</strong><br />\r\nKeith Urban, Ripcord</p>\r\n\r\n<p><strong>Top Rock Album:</strong><br />\r\nThe Lumineers, Cleopatra<br />\r\nMetallica, Hardwired&hellip;To Self Destruct -- WINNER<br />\r\nRadiohead, A Moon Shaped Pool<br />\r\nRed Hot Chili Peppers, The Getaway<br />\r\nTwenty One Pilots, Blurryface<br />\r\n<br />\r\n<strong>Top Latin Album:</strong><br />\r\nJ Balvin, Energia<br />\r\nCNCO, Primera Cita<br />\r\n<strong>Juan Gabriel, Los Duo 2 -- WINNER</strong><br />\r\nJuan Gabriel, Vestido De Etiqueta: Por Eduardo Magallanes<br />\r\nLos Plebes del Rancho de Ariel Camacho, Recuerden Mi Estilo</p>\r\n\r\n<p><strong>Top Dance/Electronic Album:</strong><br />\r\nThe Chainsmokers, Bouquet<br />\r\nThe Chainsmokers, Collage&nbsp;<br />\r\nFlume, Skin<br />\r\nKygo, Cloud Nine<br />\r\n<strong>Lindsey Stirling, Brave Enough -- WINNER</strong></p>\r\n\r\n<p><strong>Top Christian Album:</strong><br />\r\nCasting Crowns, The Very Next Thing<br />\r\n<strong>Lauren Daigle, How Can It Be -- WINNER</strong><br />\r\nJoey + Rory, Hymns<br />\r\nHillary Scott &amp; The Family, Love Remains<br />\r\nSkillet, Unleashed</p>\r\n\r\n<p><strong>Top Gospel Album:</strong><br />\r\n<strong>Tamela Mann, One Way -- WINNER</strong><br />\r\nKirk Franklin, Losing My Religion<br />\r\nTravis Greene, The Hill<br />\r\nTasha Cobbs, One Place: Live<br />\r\nHezekiah Walker, Better: Azusa - The Next Generation 2</p>\r\n\r\n<p><strong>Top Hot 100 Song:</strong><br />\r\n<strong>The Chainsmokers Featuring Halsey, &#39;Closer&rdquo; -- WINNER</strong><br />\r\nThe Chainsmokers Featuring Daya, &ldquo;Don&rsquo;t Let Me Down&rdquo;<br />\r\nDrake Featuring WizKid &amp; Kyla, &ldquo;One Dance&rdquo;<br />\r\nJustin Timberlake, &ldquo;Can&rsquo;t Stop The Feeling!&rdquo;<br />\r\nTwenty One Pilots, &ldquo;Heathens&rdquo;</p>\r\n\r\n<p><strong>Top Selling Song:</strong><br />\r\nThe Chainsmokers Featuring Halsey, &ldquo;Closer&rdquo;<br />\r\nThe Chainsmokers Featuring Daya, &ldquo;Don&rsquo;t Let Me Down&rdquo;<br />\r\nDrake Featuring, WizKid &amp; Kyla &ldquo;One Dance&rdquo;<br />\r\n<strong>Justin Timberlake, &ldquo;Can&rsquo;t Stop The Feeling!&rdquo; -- WINNER</strong><br />\r\nTwenty One Pilots, &ldquo;Heathens&rdquo;</p>\r\n\r\n<p><strong>Top Radio Song:</strong><br />\r\nThe Chainsmokers Featuring Halsey, &ldquo;Closer&rdquo;<br />\r\nThe Chainsmokers Featuring Daya, &ldquo;Don&rsquo;t Let Me Down&rdquo;<br />\r\nDrake Featuring WizKid &amp; Kyla, &ldquo;One Dance&rdquo;<br />\r\nSia Featuring Sean Paul, &ldquo;Cheap Thrills&rdquo;<br />\r\n<strong>Justin Timberlake, &ldquo;Can&rsquo;t Stop The Feeling!&rdquo; -- WINNER</strong></p>\r\n\r\n<p><strong>Top Streaming Song (Audio):</strong><br />\r\nThe Chainsmokers Featuring Halsey, &ldquo;Closer&rdquo;<br />\r\n<strong>Drake Featuring WizKid &amp; Kyla, &ldquo;One Dance&rdquo; -- WINNER</strong><br />\r\nD.R.A.M. Featuring Lil Yachty, &ldquo;Broccoli&rdquo;<br />\r\nRihanna, &ldquo;Needed Me&rdquo;<br />\r\nThe Weeknd Featuring Daft Punk, &ldquo;Starboy&rdquo;</p>\r\n\r\n<p><strong>Top Streaming Song (Video):</strong><br />\r\nThe Chainsmokers Featuring Halsey, &ldquo;Closer&rdquo;<br />\r\n<strong>Desiigner, &#39;Panda&#39; -- WINNER</strong><br />\r\nZay Hilfigerr &amp; Zayion McCall, &ldquo;JuJu On That Beat (TZ Anthem)<br />\r\nRae Sremmurd Featuring Gucci Mane, &ldquo;Black Beatles&rdquo;<br />\r\nTwenty One Pilots, &ldquo;Heathens&rdquo;</p>\r\n\r\n<p><strong>Top Collaboration:</strong><br />\r\n<strong>The Chainsmokers Featuring Halsey, &ldquo;Closer&rdquo; -- WINNER</strong><br />\r\nThe Chainsmokers Featuring Daya, &ldquo;Don&rsquo;t Let Me Down&rdquo;<br />\r\nDrake Featuring WizKid &amp; Kyla, &ldquo;One Dance&rdquo;<br />\r\nSia Featuring Sean Paul, &ldquo;Cheap Thrills&rdquo;<br />\r\nThe Weeknd Featuring Daft Punk, &ldquo;Starboy&rdquo;</p>\r\n\r\n<p><strong>Top R&amp;B Song:</strong><br />\r\n<strong>Drake Featuring WizKid &amp; Kyla, &ldquo;One Dance&rdquo; -- WINNER</strong><br />\r\nBruno Mars, &ldquo;24K Magic&rdquo;<br />\r\nRihanna, &ldquo;Needed Me&rdquo;<br />\r\nRihanna Featuring Drake, &ldquo;Work&rdquo;&nbsp;<br />\r\nThe Weeknd Featuring Daft Punk, &ldquo;Starboy&rdquo;</p>\r\n\r\n<p><strong>Top R&amp;B Collaboration:</strong><br />\r\n<strong>Drake Featuring WizKid &amp; Kyla, &ldquo;One Dance&rdquo; -- WINNER</strong><br />\r\nPARTYNEXTDOOR Featuring Drake, &ldquo;Come And See Me&rdquo;<br />\r\nRihanna Featuring Drake, &ldquo;Work&rdquo;&nbsp;<br />\r\nThe Weeknd Featuring Daft Punk &ldquo;I Feel It Coming&rdquo;<br />\r\nThe Weeknd Featuring Daft Punk, &ldquo;Starboy&rdquo;</p>\r\n\r\n<p><strong>Top Rap Song:</strong><br />\r\n<strong>Desiigner, &ldquo;Panda&rdquo; -- WINNER</strong><br />\r\nDrake, &ldquo;Fake Love&rdquo;<br />\r\nD.R.A.M. Featuring Lil Yachty, &ldquo;Broccoli&rdquo;<br />\r\nMigos Featuring Lil Uzi Vert, &ldquo;Bad And Boujee&rdquo;<br />\r\nRae Sremmurd Featuring Gucci Mane, &ldquo;Black Beatles&rdquo;</p>\r\n\r\n<p><strong>Top Rap Collaboration:</strong><br />\r\nD.R.A.M. Featuring Lil Yachty, &ldquo;Broccoli&rdquo;<br />\r\nZay Hilfigerr &amp; Zayion McCall, &ldquo;JuJu On That Beat (TZ Anthem)<br />\r\nMachine Gun Kelly &amp; Camila Cabello, &ldquo;Bad Things&rdquo;<br />\r\nMigos Featuring Lil Uzi Vert, &ldquo;Bad and Boujee&rdquo;<br />\r\n<strong>Rae Sremmurd Featuring Gucci Mane, &ldquo;Black Beatles&rdquo; -- WINNER</strong></p>\r\n\r\n<p><strong>Top Country Song:</strong><br />\r\nKenny Chesney Featuring Pink, &ldquo;Setting The World On Fire&rdquo;<br />\r\n<strong>Florida Georgia Line, &ldquo;H.O.L.Y.&rdquo; -- WINNER</strong><br />\r\nFlorida Georgia Line Featuring Tim McGraw, &ldquo;May We All&rdquo;<br />\r\nLittle Big Town, &ldquo;Better Man&rdquo;<br />\r\nKeith Urban, &ldquo;Blue Ain&rsquo;t Your Color&rdquo;</p>\r\n\r\n<p><strong>Top Country Collaboration:</strong><br />\r\nDierks Bentley Featuring Elle King, &ldquo;Different For Girls&rdquo;<br />\r\n<strong>Kenny Chesney Featuring Pink, &ldquo;Setting The World On Fire&rdquo; -- WINNER</strong><br />\r\nEric Church Featuring Rhiannon Giddens, &ldquo;Kill A Word&rdquo;<br />\r\nFlorida Georgia Line Featuring Tim McGraw, &ldquo;May We All&rdquo;<br />\r\nChris Young Featuring Vince Gill, &ldquo;Sober Saturday Night&rdquo;</p>\r\n\r\n<p><strong>Top Rock Song:</strong><br />\r\nLil Wayne, Wiz Khalifa &amp; Imagine Dragons with Logic &amp; Ty Dolla $ign Featuring X Ambassadors, &ldquo;Sucker For Pain&rdquo;<br />\r\n<strong>Twenty One Pilots, &ldquo;Heathens&rdquo; -- WINNER</strong><br />\r\nTwenty One Pilots, &ldquo;Ride&rdquo;<br />\r\nTwenty One Pilots, &ldquo;Stressed Out&rdquo;<br />\r\nX Ambassadors, &ldquo;Unsteady&rdquo;</p>\r\n\r\n<p><strong>Top Latin Song:</strong><br />\r\nDaddy Yankee, &ldquo;Shaky Shaky&rdquo;<br />\r\nEnrique Iglesias Featuring Wisin, &ldquo;Duele El Corazon&rdquo;<br />\r\n<strong>Nicky Jam, &ldquo;Hasta El Amanecer&rdquo; -- WINNER</strong><br />\r\nShakira Featuring Maluma, &ldquo;Chantaje&rdquo;<br />\r\nCarlos Vives &amp; Shakira, &ldquo;La Bicicleta&quot;</p>\r\n\r\n<p><strong>Top Dance/Electronic Song:</strong><br />\r\n<strong>The Chainsmokers Featuring Halsey, &ldquo;Closer&rdquo; -- WINNER</strong><br />\r\nThe Chainsmokers Featuring Daya, &ldquo;Don&rsquo;t Let Me Down&rdquo;<br />\r\nCalvin Harris Featuring Rihanna, &ldquo;This Is What You Came For&rdquo;<br />\r\nMajor Lazer Featuring Justin Bieber &amp; MO, &ldquo;Cold Water&rdquo;<br />\r\nDJ Snake Featuring Justin Bieber, &ldquo;Let Me Love You&rdquo;</p>\r\n\r\n<p><strong>Top Christian Song:</strong><br />\r\nLauren Daigle, &ldquo;Trust In You&rdquo;<br />\r\n<strong>Hillary Scott &amp; The Family, &ldquo;Thy Will&rdquo; -- WINNER</strong><br />\r\nSkillet, &ldquo;Feel Invincible&rdquo;<br />\r\nRyan Stevenson Featuring GabeReal, &ldquo;Eye Of The Storm&rdquo;<br />\r\nZach Williams, &ldquo;Chain Breaker&rdquo;</p>\r\n\r\n<p><strong>Top Gospel Song:</strong><br />\r\nJekalyn Carr, &ldquo;You&rsquo;re Bigger&rdquo;<br />\r\nTasha Cobbs, Featuring Kierra Sheard &ldquo;Put A Praise On It&rdquo;<br />\r\nKirk Franklin, &ldquo;Wanna Be Happy??&rdquo;<br />\r\n<strong>Travis Greene, &ldquo;Made A Way&rdquo; -- WINNER</strong><br />\r\nHezekiah Walker, &ldquo;Better&rdquo;</p>\r\n', 'Rapper da màu đã chiến thắng ở hầu hết các giải thưởng quan trọng của Billboard năm nay.', 'Âu Mỹ', 'res/images/tinhot/drake-bbmas-2017-sho-1e80.jpg'),
(9, 'SF9 tổ chức họp fan quốc tế lần đầu tiên tại Manila', '<p><strong>Ngay sau màn tái xu&acirc;́t ấn tượng, các chàng trai SF9 đang rục rịch chu&acirc;̉n bị cho cu&ocirc;̣c gặp gỡ fan qu&ocirc;́c t&ecirc;́ đ&acirc;̀u ti&ecirc;n của mình.</strong></p>\r\n\r\n<p>Vào ngày&nbsp; 9 th&aacute;ng 5 vừa qua,&nbsp;<a href=\"http://tinnhac.com/nghe-si/sf9.html\" target=\"_blank\">SF9</a>&nbsp;chính thức c&ocirc;ng bố lịch tr&igrave;nh của tour diễn Fan Meeting Ch&acirc;u &Aacute; sắp tới, với tựa đề&nbsp;<em>&quot;Be My Fantasy&quot;</em>.</p>\r\n\r\n<p><em>&quot;Be My Fantasy&quot;</em>&nbsp;sẽ l&agrave; một sự kiện v&ocirc; cùng &yacute; nghĩa, v&igrave; đ&acirc;y l&agrave; lần đầu ti&ecirc;n các chàng trai gặp gỡ c&aacute;c fan quốc tế. Theo dự ki&ecirc;́n, hành trình l&acirc;̀n này sẽ đi quan các địa đi&ecirc;̉m: Băng Cốc, Singapore, Đ&agrave;i Bắc, cũng như Hồng K&ocirc;ng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/12/cwziuouiaacilc-2b30.jpg\" style=\"height:848px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Lịch tr&igrave;nh fan meeting Ch&acirc;u &Aacute;&nbsp;&quot;Be My Fantasy&quot; của SF9</em></p>\r\n\r\n<p>Tại Manila, sự kiện sẽ diễn ra tại Nh&agrave; h&aacute;t Theatre ở Solaire v&agrave;o ng&agrave;y 18 th&aacute;ng 6 l&uacute;c 7 giờ tối. V&eacute; VIP c&oacute; gi&aacute; khoảng 6.000 PHP (PHP là kí hi&ecirc;̣u ti&ecirc;̀n t&ecirc;̣ của Philippine) (tương đương với khoảng 2,7 tri&ecirc;̣u đ&ocirc;̀ng ti&ecirc;̀n Vi&ecirc;̣t), trong khi v&eacute; cao cấp l&agrave; 3.000 PHP (tương đương với 1,3 tri&ecirc;̣u đ&ocirc;̀ng ti&ecirc;̀n Vi&ecirc;̣t), kh&ocirc;ng bao gồm ph&iacute; đặt ph&ograve;ng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/12/20170522hellokpopsf9-f2d3.jpg\" style=\"height:838px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/12/20170522hellokpopsf9-99e6.jpg\" style=\"height:429px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Vé dự ki&ecirc;́n được bán v&agrave;o ng&agrave;y 27 th&aacute;ng 5 th&ocirc;ng qua k&ecirc;nh TicketWorld Manila l&uacute;c 12 giờ trưa</em></p>\r\n\r\n<p>Nếu có đi&ecirc;̀u ki&ecirc;̣n, bạn h&atilde;y ưu ti&ecirc;n chọn v&eacute; VIP. V&igrave; chủ sở hữu vị trí Vip sẽ được có cơ hội nhìn ngắm th&acirc;̀n tượng trong khoảng cách g&acirc;̀n nh&acirc;́t, chạm vào th&acirc;̀n tượng n&ecirc;́u họ có đi ngang qua &nbsp;hoặc có cơ h&ocirc;̣i xu&acirc;́t hi&ecirc;̣n trong những bức ảnh của nh&oacute;m. Ngo&agrave;i ra, chương trình sẽ c&oacute; ph&acirc;̀n &quot;bốc thăm may mắn&quot;, trong đ&oacute; những người c&oacute; v&eacute; VIP sẽ c&oacute; cơ hội gi&agrave;nh chi&ecirc;́n thắng và mang v&ecirc;̀ cho mình một trong 70 tấm &aacute;p ph&iacute;ch c&oacute; chữ hoặc m&ocirc;̣t trong 27 mẫu chữ k&yacute; ri&ecirc;ng của chính th&acirc;̀n tượng.</p>\r\n\r\n<p>Sensational Feeling 9, hay c&ograve;n được gọi l&agrave; SF9, l&agrave; nh&oacute;m nhạc dance nam được đào tạo theo quy trình đào tạo t&acirc;n binh mới của c&ocirc;ng ty FNC Entertainment, dưới h&ecirc;̣ th&ocirc;́ng NEO SCHOOL với th&ecirc;́ mạnh là vũ đạo và đ&acirc;y là 1 nhóm nhảy chứ kh&ocirc;ng phải ban nhạc. FNC gắn li&ecirc;̀n với t&ecirc;n tu&ocirc;̉i của các nhóm nhạc đình đám như CNBLUE, FTISLAND, AOA.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/12/20170522hellokpopsf9-132c.png\" style=\"height:363px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>SF9</em></p>\r\n\r\n<p>Trước khi hoạt đ&ocirc;̣ng chính thức, tám chàng trai: Youngbin, Inseong, Jaeyoon, Dawon, Rowoon, Zuho, Taeyang, Hwiyoung v&agrave; Chani đã cùng nhau tham gia chương trình thực t&ecirc;́ s&ocirc;́ng còn mang t&ecirc;n &quot;D.O.B.&quot; (Dance or Band).</p>\r\n\r\n<p>Đ&acirc;y là chương trình do chính FNC Entertainment hợp tác với đài Mnet t&ocirc;̉ chức. Mục đích của chương trình là giải quy&ecirc;́t vi&ecirc;̣c tìm hướng đi cho SF9 bởi FCN gặp khó khăn trong vi&ecirc;̣c lựa chọn giữa hướng đào tạo truy&ecirc;̀n th&ocirc;́ng hay phát tri&ecirc;̉n thành m&ocirc;̣t nhóm nhảy hoàn toàn mới. FNC đã đ&ecirc;̉ cho khán giả lựa chọn bằng cách sản xu&acirc;́t chương trình tr&ecirc;n. Kh&ocirc;ng may cho SF9, chương trình bị hủy phát sóng vì ratting quá th&acirc;́p. Chương trình bị cho là kh&ocirc;ng có gì mới so với các chương trình thực t&ecirc;́ trước đ&acirc;́y của các c&ocirc;ng ty lớn như YG, JYP.</p>\r\n\r\n<p>Sau đó, nhóm bắt đ&acirc;̀u hoạt đ&ocirc;̣ng chính thức và ph&aacute;t h&agrave;nh mini album đ&acirc;̀u ti&ecirc;n mang t&ecirc;n&nbsp;<em>&quot;Feeling Sensation&quot;</em>, g&ocirc;̀m ba ca khúc chủ đạo&nbsp;<em>&quot;Fanfare&quot;</em>,&nbsp;<em>&quot;K.O&quot;</em>&nbsp;v&agrave;&nbsp;<em>&quot;Together&quot;</em>. Nhóm đã đặt r&acirc;́t nhi&ecirc;̀u kì vọng vào sản ph&acirc;̉m đ&acirc;̀u ti&ecirc;n n&agrave;y.&nbsp;<em>&ldquo;Ch&uacute;ng t&ocirc;i đ&atilde; đ&aacute;nh bật đối thủ trong d.o.b để gi&agrave;nh lấy cơ hội ra mắt. Nh&oacute;m c&ograve;n những kỹ năng chưa từng tiết lộ trong d.o.b. Với tư c&aacute;ch l&agrave; nh&oacute;m nhảy nam đầu ti&ecirc;n của FNC, ch&uacute;ng t&ocirc;i đ&atilde; l&agrave;m việc chăm chỉ để kh&ocirc;ng l&agrave;m người h&acirc;m mộ thất vọng&rdquo;</em>&nbsp;&ndash; th&agrave;nh vi&ecirc;n Youngbin n&oacute;i trong buổi ra mắt đĩa nhạc.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, SF9 đã khi&ecirc;́n người h&acirc;m m&ocirc;̣ th&acirc;́t vọng khi mini album đầu ti&ecirc;n kh&ocirc;ng lọt n&ocirc;̉i Top 100 ở c&aacute;c bảng xếp hạng &acirc;m nhạc trực tuyến. Tr&ecirc;n YouTube, MV&nbsp;<em>&quot;Fanfare&quot;</em>&nbsp;chỉ đạt 200.000 lượt xem sau gần 3 ng&agrave;y l&ecirc;n s&oacute;ng.</p>\r\n\r\n<p>Bỏ qua l&acirc;̀n ra mắt mờ nhạt, chỉ mới th&aacute;ng trước, nh&oacute;m vừa tái xu&acirc;́t mini album thứ hai mang t&ecirc;n&nbsp;<em>&quot;Breaking Sensation&quot;</em>&nbsp;với ca khúc chủ đề&nbsp;<em>&quot;Easy Love&quot;</em>. Sản ph&acirc;̉m chú trọng ph&ocirc; di&ecirc;̃n những kỹ thuật hình th&ecirc;̉ v&ocirc; cùng độc đ&aacute;o.</p>\r\n\r\n<p>Theo đánh giá, đ&acirc;y là l&acirc;̀n tái xu&acirc;́t v&ocirc; cùng &acirc;́n tượng khi SF9 nhanh chóng d&acirc;̃n đ&acirc;̀u 8 bảng x&ecirc;́p hạng &acirc;m nhạc uy tín trong nước, đ&ocirc;̀ng thời &nbsp;leo l&ecirc;n xếp thứ 5 trong bảng xếp hạng Gaon Music Album ở H&agrave;n Quốc. Kh&ocirc;ng chỉ ở Hàn Qu&ocirc;́c, ca khúc chủ đ&ecirc;̀&nbsp;<em>&quot;Easy Love&quot;</em>&nbsp;cũng cũng nằm ở vị tr&iacute; dẫn đầu tại hạng mục Kpop tr&ecirc;n bảng xếp hạng iTunes ở Hoa Kì.</p>\r\n\r\n<p>Đáng ngạc nhi&ecirc;n hơn, album n&agrave;y c&ograve;n lọt v&agrave;o top 5 tr&ecirc;n bảng xếp hạng iTunes to&agrave;n cầu tại 13 nước Canada, Anh, Đức, &Uacute;c, H&agrave; Lan, Nga, T&acirc;y Ban Nha, Ireland, Romania, Thổ Nhĩ Kỳ, Thụy Điển, Na Uy v&agrave; Indonesia. SF9 xóa bỏ những nghi ngờ v&ecirc;̀ thực lực và khi&ecirc;́n kh&aacute;n giả phải ch&uacute; &yacute; v&agrave;o họ.</p>\r\n\r\n<p><strong>&quot;[BE MY FANTASY] ASIA FAN MEETING TOUR&quot;</strong>&nbsp;được FNC Entertainment v&agrave; IME PH l&ecirc;n ý tưởng và tạo cơ h&ocirc;̣i cho người h&acirc;m mộ được gặp gỡ th&acirc;̀n tượng của mình. Theo d&otilde;i th&ocirc;ng tin cụ thể về fan meeting của SF9&nbsp;<a href=\"https://www.facebook.com/SF9official/?fref=ts\" target=\"_blank\">tại đ&acirc;y</a>.</p>\r\n\r\n<p>Bạn đ&atilde; sẵn sàng trở thành là thành vi&ecirc;n của&nbsp;<em>&quot;Be My Fantasy&quot;</em>&nbsp;trong tháng Sáu này chưa?</p>\r\n', 'Ngay sau màn tái xuất ấn tượng, các chàng trai SF9 đang rục rịch chuẩn bị cho cuộc gặp gỡ fan quốc tế đầu tiên của mình.', 'Châu Á', 'res/images/tinhot/20170522hellokpopsf9-f2d3.jpg');

