INSERT INTO `tintuc` (`ID`, `TieuDe`, `NoiDung`, `TomTat`, `ChuDe`, `AnhDaiDien`) VALUES
(3, 'Từ bỏ dòng nhạc \"già\", Nguyễn Hồng Ân tìm đến nhạc trẻ để làm mới mình', '<p><strong>Lột x&aacute;c khỏi phong c&aacute;ch quen thuộc, Nguyễn Hồng &Acirc;n muốn thử sức với d&ograve;ng nhạc trẻ trung để đến gần với kh&aacute;n giả hơn.</strong></p>\r\n\r\n<p>Ca kh&uacute;c mới toanh vừa được bổ sung v&agrave;o kho nhạc của nam ca sĩ điển trai<strong>&nbsp;<a href=\"http://tinnhac.com/nguyen-hong-an-lam-album-tuong-nho-co-nhac-si-anh-bang-54275.html\" target=\"_blank\">Nguyễn Hồng &Acirc;n</a></strong>vừa được ch&iacute;nh thức tr&igrave;nh l&agrave;ng người h&acirc;m mộ. Single&nbsp;<em>&quot;M&ugrave;a đ&atilde; xa&quot;</em>&nbsp;ch&iacute;nh l&agrave; ca kh&uacute;c đ&aacute;nh dấu bước chuyển m&igrave;nh đầy mới mẻ của Nguyễn Hồng &Acirc;n trong d&ograve;ng nhạc mới.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/batch-nha-4-14957676-4d92.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Nguyễn Hồng &Acirc;n muốn l&agrave;m mới m&igrave;nh trong một ca kh&uacute;c nhạc trẻ</em></p>\r\n\r\n<p>Theo chia sẻ, MV<em>&nbsp;&ldquo;M&ugrave;a đ&atilde; xa&rdquo;</em>&nbsp;ch&iacute;nh l&agrave; sản phẩm đầu tay m&agrave;&nbsp;<strong><a href=\"http://tinnhac.com/nguyen-hong-an-lam-mv-nhac-trinh-54705.html\" target=\"_blank\">Nguyễn Hồng &Acirc;n</a></strong>&nbsp;kết hợp c&ugrave;ng nhạc sĩ trẻ Ti&ecirc;n Nguyễn để thực hiện. Kh&aacute; đắn đo v&agrave; lo lắng liệu rằng kh&aacute;n giả c&oacute; qu&aacute; shock với phong c&aacute;ch mới, nam ca sĩ vẫn quyết t&acirc;m đổi mới m&igrave;nh trong sản phẩm &acirc;m nhạc n&agrave;y để khẳng định khả năng biến chuyển trong d&ograve;ng nhạc, h&igrave;nh ảnh.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/2.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Những h&igrave;nh ảnh mới nhất của nam ca sĩ tại Đ&agrave; Lạt</em></p>\r\n\r\n<p>Đ&acirc;y cũng l&agrave; sản phẩm đ&aacute;nh dấu ng&agrave;y trở lại của&nbsp;<strong><a href=\"http://tinnhac.com/nguyen-hong-an-doanh-thu-ban-album-moi-se-de-lam-tu-thien-25780.html\" target=\"_blank\">Nguyễn Hồng &Acirc;n</a></strong>&nbsp;sau khoảng thời gian d&agrave;i vắng b&oacute;ng để tập trung cho kinh doanh v&agrave; việc c&aacute; nh&acirc;n. &ldquo;L&aacute;nh&rdquo; truyền th&ocirc;ng một phần cũng v&igrave; bản th&acirc;n nam ca sĩ cảm thấy chưa c&oacute; g&igrave; thực sự mới mẻ để trở lại. Tuy nhi&ecirc;n, phần lớn l&agrave; do con đường anh lựa chọn kh&ocirc;ng ồn &agrave;o, x&ocirc; bồ như những g&igrave; truyền th&ocirc;ng mong muốn n&ecirc;n anh thường lựa chọn l&ugrave;i bước v&agrave; giữ m&igrave;nh bước chậm. Đ&oacute; cũng l&agrave; c&aacute;ch Nguyễn Hồng &Acirc;n tự khiến bản th&acirc;n vui v&agrave; thanh thản hơn trong con đường &acirc;m nhạc vốn nhiều ch&ocirc;ng gai m&agrave; anh lựa chọn.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/3.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Đ&acirc;y l&agrave; sản phẩm đ&aacute;nh dấu cho sự trở lại của Nguyễn Hồng &Acirc;n sau thời gian vắng b&oacute;ng</em></p>\r\n\r\n<p>MV của<em>&nbsp;&ldquo;M&ugrave;a đ&atilde; xa&rdquo;</em>&nbsp;được nam ca sĩ lựa chọn th&agrave;nh phố hoa Đ&agrave; Lạt để thực hiện c&aacute;c cảnh quay. Với những thước phim nhẹ nh&agrave;ng nhưng kh&ocirc;ng k&eacute;m phần l&atilde;ng mạn, ca kh&uacute;c đang nhận được rất nhiều phản hồi t&iacute;ch cực từ ph&iacute;a kh&aacute;n giả.</p>\r\n', 'Lột xác khỏi phong cách quen thuộc, Nguyễn Hồng Ân muốn thử sức với dòng nhạc trẻ trung để đến gần với khán giả hơn.', 'Việt Nam', 'res/images/tintuc/2.jpg'),
(4, 'Ấm lòng khi Hiền Thục đứng ra tổ chức đêm nhạc gây quỹ từ thiện cho các bé bị xâm hại tình dục', '<p><strong>Đ&ecirc;m nhạc l&agrave; một m&oacute;n qu&agrave; nhỏ m&agrave; Hiền Thục muốn d&agrave;nh tặng cho ng&ocirc;i nh&agrave; &ldquo;Nhịp Cầu Hạnh Ph&uacute;c&rdquo; - nơi đang điều trị t&acirc;m l&yacute;, chăm s&oacute;c, nu&ocirc;i dưỡng tạm thời c&aacute;c trẻ em bị x&acirc;m hại t&igrave;nh dục.</strong></p>\r\n\r\n<p>Đứng ra để k&ecirc;u gọi mọi người ủng hộ cho&nbsp;<em>&quot;Quỹ nhi đồng của Thục&quot;</em>, nữ đại sứ đ&aacute;ng y&ecirc;u đang khiến rất nhiều bạn nhỏ mừng vui v&igrave; đ&ecirc;m nhạc d&agrave;ng tặng c&aacute;c b&eacute; bị x&acirc;m hại t&igrave;nh dục.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"hiền thục\" src=\"/GioiThieuAmNhac/upload/images/4.jpg\" style=\"height:750px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Hiền Thục sẽ thực hiện một đ&ecirc;m nhạc nhỏ g&acirc;y quỹ cho c&aacute;c b&eacute; bị x&acirc;m hại t&igrave;nh dục</em></p>\r\n\r\n<p>Hiểu r&otilde; nỗi đau v&agrave; day dứt của những gia đ&igrave;nh từng nếm trải cảm gi&aacute;c tủi nhục, nữ ca sĩ đ&atilde; li&ecirc;n tục tiếp x&uacute;c với những vụ việc v&agrave; gia đ&igrave;nh c&oacute; con em bị x&acirc;m hại để ấp ủ quyết t&acirc;m thực hiện đ&ecirc;m nhạc d&agrave;nh cho c&aacute;c b&eacute;. Cũng l&agrave;m mẹ v&agrave; thương con da diết, đ&acirc;y cũng l&agrave; m&oacute;n qu&agrave; m&agrave;&nbsp;<strong><a href=\"http://tinnhac.com/hien-thuc-mua-doc-quyen-ca-khuc-tang-cho-tro-cung-88996.html\" target=\"_blank\">Hiền Thục</a>&nbsp;</strong>muốn d&agrave;nh tặng những bậc l&agrave;m cha l&agrave;m mẹ v&agrave; những c&ocirc; / cậu b&eacute; phải chịu nỗi đau qu&aacute; lớn về t&acirc;m l&yacute; v&agrave; thể x&aacute;c.&nbsp;Nữ ca sĩ Hiền Thục đ&atilde; kết hợp với &ecirc;-k&iacute;p trong&nbsp;<em>&quot;Quỹ nhi đồng của Thục&quot;</em>&nbsp;l&ecirc;n &yacute; tưởng cho một đ&ecirc;m nhạc nhỏ g&acirc;y quỹ d&agrave;nh cho c&aacute;c b&eacute; bị x&acirc;m hại t&igrave;nh dục.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/5.jpg\" style=\"height:399px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Mẹ ca sĩ hỗ trợ rất nhiều cho c&ocirc; trong qu&aacute; tr&igrave;nh thực hiện đ&ecirc;m nhạc</em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/4739img5569-901c.jpg\" style=\"height:399px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Nữ ca sĩ tranh thủ tập nhạc c&ugrave;ng hai cậu em th&acirc;n thiết Kh&aacute;nh Ho&agrave;ng v&agrave; L&acirc;m Ph&uacute;c Qu&acirc;n</em></p>\r\n\r\n<p>G&oacute;p mặt trong đ&ecirc;m nhạc th&acirc;n mật, nữ ca sĩ h&eacute; lộ ngo&agrave;i những nghệ sĩ đ&igrave;nh đ&aacute;m như Mỹ Linh, Đức Tuấn, Nguyễn Hồng &Acirc;n,&nbsp; Yến Trang, Yến Nhi, Minh Trang&hellip;,<strong>&nbsp;<a href=\"http://tinnhac.com/hien-thuc-kasim-hoang-vu-tham-gia-sol-vang-thang-3-24056.html\" target=\"_blank\">Hiền Thục</a></strong>&nbsp;cho biết c&ocirc; đ&atilde; mời hai người em th&acirc;n thiết của m&igrave;nh l&agrave; Kh&aacute;nh Ho&agrave;ng v&agrave; L&acirc;m Ph&uacute;c Qu&acirc;n c&ugrave;ng tham gia v&agrave;o đ&ecirc;m nhạc. D&ugrave; kh&aacute; bận bịu, nữ ca sĩ vẫn tr&iacute;ch thời gian để tập nhạc c&ugrave;ng band v&agrave; hỗ trợ hai cậu em trai biểu diễn trong chương tr&igrave;nh. Ri&ecirc;ng c&ocirc; ngo&agrave;i những bản hit quen thuộc, nữ ca sĩ sẽ mang những ca kh&uacute;c thiếu nhi ngọt ng&agrave;o l&ecirc;n s&acirc;n khấu đ&ecirc;m nhạc để d&agrave;nh tặng c&aacute;c b&eacute;.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/img5552-23354-d0f7.jpg\" style=\"height:750px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Hiền Thục dự định sẽ chỉ mời 60 kh&aacute;ch tham dự đ&ecirc;m nhạc</em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/img5544-233513-f963.jpg\" style=\"height:750px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Nữ ca sĩ đứng ra lo liệu cho to&agrave;n bộ c&aacute;c kh&acirc;u chuẩn bị trước đ&ecirc;m nhạc</em></p>\r\n\r\n<p>L&agrave; người s&aacute;t c&aacute;nh v&agrave; hỗ trợ cho&nbsp;<strong><a href=\"http://tinnhac.com/hien-thuc-thuc-hien-minishow-de-gioi-thieu-album-moi-89458.html\" target=\"_blank\">Hiền Thục</a></strong>&nbsp;từ kh&acirc;u chuẩn bị, mẹ của nữ ca sĩ cũng b&agrave;y tỏ niềm vui v&agrave; tự h&agrave;o về đ&ecirc;m nhạc đ&aacute;ng qu&yacute; của con g&aacute;i.&nbsp;<em>&quot;T&ocirc;i tin ch&uacute;t c&ocirc;ng sức m&agrave; con m&igrave;nh bỏ ra sẽ mang đến những niềm vui cho c&aacute;c em b&eacute;&quot;</em>, mẹ Hiền Thục n&oacute;i.</p>\r\n\r\n<p>D&ugrave; vậy, đ&ecirc;m nhạc của&nbsp;<strong><a href=\"http://tinnhac.com/hien-thuc-trich-tien-thu-duoc-tu-album-moi-de-lam-tu-thien-90598.html\" target=\"_blank\">Hiền Thục</a></strong>&nbsp;chỉ g&oacute;i gọn trong số lượng kh&aacute;ch giới hạn 60 người. Do đ&oacute;, đ&iacute;ch th&acirc;n nữ ca sĩ phải đứng ra lo liệu từ kh&acirc;u chuẩn bị &acirc;m thanh, &aacute;nh s&aacute;ng đến cả tiệc tối, thức ăn d&agrave;nh cho c&aacute;c bạn nhỏ. Theo nữ ca sĩ, to&agrave;n bộ số tiền thu được từ đ&ecirc;m nhạc sẽ ủng hộ cho c&aacute;c em nhỏ gặp vấn nạn về x&acirc;m hại t&igrave;nh dục.</p>\r\n\r\n<p><em>&quot;T&ocirc;i mong mọi người ủng hộ cho ch&uacute;t th&agrave;nh &yacute; của m&igrave;nh. Mong rằng sau chương tr&igrave;nh quỹ nhi đồng của Thục sẽ c&oacute; th&ecirc;m được nguồn kinh ph&iacute; để đ&oacute;ng g&oacute;p, sữa chửa c&aacute;c ngồi nh&agrave; đang nu&ocirc;i c&aacute;c em bị x&acirc;m hại&quot;</em>, Hiền Thục k&ecirc;u gọi.</p>\r\n', 'Đêm nhạc là một món quà nhỏ mà Hiền Thục muốn dành tặng cho ngôi nhà “Nhịp Cầu Hạnh Phúc” - nơi đang điều trị tâm lý, chăm sóc, nuôi dưỡng tạm thời các trẻ em bị xâm hại tình dục.', 'Việt Nam', 'res/images/tintuc/ht.jpg'),
(5, 'Nhóm MTV lăn lê làm xe ôm, bảo vệ trong single mới khiến fan thương \"rớt nước mắt\"', '<p><strong>3 th&agrave;nh vi&ecirc;n MTV Band khiến fan đau l&ograve;ng khi phải l&agrave;m những c&ocirc;ng việc qu&aacute; nặng nhọc.</strong></p>\r\n\r\n<p>Kh&ocirc;ng c&ograve;n lung linh &ldquo;lụa l&agrave;&rdquo; như những MV trước đ&oacute;, mới đ&acirc;y những h&igrave;nh ảnh hậu trường cho single mới của boyband đ&atilde; khiến fan h&aacute; hốc mồm v&igrave;&hellip; qu&aacute; đ&aacute;ng thương.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/6.jpg\" style=\"height:399px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Ba th&agrave;nh vi&ecirc;n của nh&oacute;m nhạc MTV tinh nghịch trong những khoảnh khắc hậu trường&nbsp;</em></p>\r\n\r\n<p>Nếu n&oacute;i về những boyband c&oacute; tiếng vang v&agrave; ghi được dấu ấn kh&oacute; qu&ecirc;n trong l&ograve;ng kh&aacute;n giả v&agrave;o h&agrave;ng &ldquo;danh ca&rdquo;, chắc chắn kh&ocirc;ng thể kh&ocirc;ng nhắc đến nh&oacute;m nhạc&nbsp;<strong><a href=\"http://tinnhac.com/nhom-mtv-gia-gai-trong-mv-moi-26747.html\" target=\"_blank\">MTV</a></strong>. Kh&ocirc;ng chỉ l&agrave; nh&oacute;m nhạc ti&ecirc;n phong cho những xu hướng &acirc;m nhạc đ&igrave;nh đ&aacute;m thời chuyển giao thế hệ, MTV c&ograve;n l&agrave; c&aacute;i t&ecirc;n từng l&agrave;m đi&ecirc;n đảo giới trẻ khi thực hiện c&aacute;c MV từ đời l&ecirc;n m&agrave;n ảnh kh&ocirc;ng thể n&agrave;o ch&acirc;n thật hơn. Những th&agrave;nh vi&ecirc;n lu&ocirc;n trăn trở l&agrave;m sao để phản &aacute;nh những g&oacute;c khuất x&atilde; hội cũng ch&iacute;nh l&agrave; những c&acirc;u chuyện cuộc sống đầy &yacute; nghĩa đến với kh&aacute;n giả.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/4133nhaycsiynguyeyyn-9650.jpg\" style=\"height:399px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Nh&oacute;m nhạc đang nỗ lực thực hiện single mới để gửi đến kh&aacute;n giả sớm nhất c&oacute; thể</em></p>\r\n\r\n<p>Thậm ch&iacute; để c&oacute; thể mang đến c&aacute;i nh&igrave;n th&iacute;ch th&uacute; v&agrave; mới lạ trong mắt người h&acirc;m mộ,&nbsp;<strong><a href=\"http://tinnhac.com/cam-ly-lam-khach-moi-liveshow-ki-niem-cua-nhom-mtv-24793.html\" target=\"_blank\">MTV</a>&nbsp;</strong>sẵn s&agrave;ng biến h&oacute;a m&igrave;nh trong những clip bựa, độc đ&aacute;o v&agrave; xả th&acirc;n v&igrave; cảnh quay. V&agrave; mới đ&acirc;y những, MV&nbsp;<em>&ldquo;Niềm hạnh ph&uacute;c&rdquo;</em>&nbsp;khi được h&eacute; lộ những cảnh quay hậu trường đ&atilde; khiến fan h&acirc;m mộ th&iacute;ch th&uacute; khi một lần nữa, 3 th&agrave;nh vi&ecirc;n nh&oacute;m nhạc lại thay đổi m&igrave;nh trong những vai tr&ograve; mới mẻ.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/5133img2317-7045.jpg\" style=\"height:559px; width:373px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Anh ch&agrave;ng xe &ocirc;m th&acirc;n thiện - L&ecirc; Minh</em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/5227pdd8707-d3ea.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Anh ch&agrave;ng lau dọn ngọt ng&agrave;o - Thi&ecirc;n Vương</em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/5305img2302-eac7.jpg\" style=\"height:399px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Bảo vệ đ&aacute;ng y&ecirc;u - Anh Tuấn</em></p>\r\n\r\n<p>C&aacute;c th&agrave;nh vi&ecirc;n nh&oacute;m nhạc ho&aacute; th&acirc;n v&agrave;o vai xe &ocirc;m th&acirc;n thiện (L&ecirc; Minh), bảo vệ (Anh Tuấn) v&agrave; anh ch&agrave;ng lau dọn (Thi&ecirc;n Vương) để chuyển tải đến người xem một c&acirc;u chuyện đầy niềm vui. Với những t&igrave;nh huống được ph&oacute;ng đại c&ugrave;ng c&aacute;ch diễn tấu h&agrave;i khiến MV được dự đo&aacute;n l&agrave; một single mang đầy tiếng cười cho người nghe. L&agrave; một s&aacute;ng t&aacute;c được chắp b&uacute;t bởi nhạc sĩ Nguyễn D&acirc;n, ca kh&uacute;c n&oacute;i về một điều m&agrave; ai cũng muốn t&igrave;m kiếm cho bản th&acirc;n từ những suy nghĩ chất chứa về quan niệm về t&igrave;nh y&ecirc;u, về những g&igrave; m&agrave; ch&uacute;ng ta thường d&agrave;nh nhiều sự quan t&acirc;m trong cuộc sống.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/4231img2404-fe67.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Những khoảnh khắc v&ocirc; c&ugrave;ng b&igrave;nh dị của MTV trong c&aacute;c single gần đ&acirc;y</em></p>\r\n\r\n<p>Trước đ&oacute;,&nbsp;<strong><a href=\"http://tinnhac.com/nhom-mtv-ki-niem-15-ca-hat-bang-liveshow-khung-24732.html\" target=\"_blank\">MTV</a></strong>&nbsp;từng tr&igrave;nh l&agrave;ng 2 ca kh&uacute;c cực k&igrave; vui nhộn l&agrave;&nbsp;<em>&ldquo;Tại sao bạn đến tr&aacute;i đất n&agrave;y?&rdquo;</em>&nbsp;hay gần đ&acirc;y nhất l&agrave; hai phi&ecirc;n bản Bolero v&agrave; EDM của&nbsp;<em>&ldquo;Đừng nh&igrave;n bề ngo&agrave;i&rdquo;.</em>&nbsp;Đều l&agrave; những tạo h&igrave;nh b&aacute; đạo, MTV đang dần đến gần với tr&aacute;i tim kh&aacute;n giả trẻ theo c&aacute;ch ri&ecirc;ng.</p>\r\n', '3 thành viên MTV Band khiến fan đau lòng khi phải làm những công việc quá nặng nhọc.', 'Việt Nam', 'res/images/tintuc/4113mtvvaynguyeyynda-cf68.jpg'),
(6, '\"Đả nữ\" Ngô Thanh Vân chính thức góp mặt trong siêu xuất phẩm - \"Star Wars: The Last Jedi\"', '<p><strong>Si&ecirc;u phẩm &ldquo;Star Wars&rdquo; sẽ ch&iacute;nh thức ra rạp v&agrave;o cuối năm nay.</strong></p>\r\n\r\n<p>Theo th&ocirc;ng tin mới nhất, Ng&ocirc; Thanh V&acirc;n l&agrave; diễn vi&ecirc;n Việt Nam duy nhất được g&oacute;p mặt trong si&ecirc;u bom tấn Hollywood mang t&ecirc;n<em>&nbsp;&ldquo;Star Wars: The Last Jedi&rdquo;</em>. Mặc d&ugrave; chỉ vừa c&ocirc;ng bố, tuy nhi&ecirc;n người h&acirc;m mộ lại v&ocirc; c&ugrave;ng quan t&acirc;m v&igrave; đ&acirc;y l&agrave; si&ecirc;u phẩm được mong chờ từ l&uacute;c bấm m&aacute;y.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/20170525-053745-1578-f04b.jpg\" style=\"height:542px; width:578px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Th&ocirc;ng b&aacute;o về Ng&ocirc; Thanh V&acirc;n tr&ecirc;n&nbsp;trang Vanity Fair</em></p>\r\n\r\n<p>Trước đ&oacute;, v&agrave;o cuối năm 2016, th&ocirc;ng tin về việc&nbsp;<strong><a href=\"http://tinnhac.com/365band-quay-tung-bung-trong-sinh-nhat-muon-cua-su-phu-ngo-thanh-van-55949.html\" target=\"_blank\">Ng&ocirc; Thanh V&acirc;n</a></strong>&nbsp;g&oacute;p mặt trong si&ecirc;u phẩm Star Wars đ&atilde; dấy l&ecirc;n khiến fan đứng ngồi kh&ocirc;ng y&ecirc;n. Nhiều đồn đo&aacute;n v&agrave; lo lắng rằng điều n&agrave;y kh&oacute; trở th&agrave;nh sự thật khi diễn vi&ecirc;n Việt Nam qu&aacute; &iacute;t đất diễn tại quốc tế. D&ugrave; vậy trang Vanity Fair đ&atilde; ch&iacute;nh thức h&eacute; lộ vai diễn của một số diễn vi&ecirc;n trong phim<em>&nbsp;&ldquo;Star Wars: The Last Jedi&rdquo;</em>&nbsp;v&agrave; c&oacute; đề cập đến Ng&ocirc; Thanh V&acirc;n. Th&ocirc;ng tin vừa c&ocirc;ng bố đ&atilde; khiến fan h&acirc;m mộ vui sướng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/20170525-053759-1560-64f5.jpg\" style=\"height:814px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Đ&acirc;y l&agrave; một trải nghiệm mới mẻ th&uacute; vị của nữ diễn vi&ecirc;n Ng&ocirc; Thanh V&acirc;n</em></p>\r\n\r\n<p>Tr&ecirc;n th&ocirc;ng b&aacute;o,&nbsp;<strong><a href=\"http://tinnhac.com/ngo-thanh-van-bong-gio-noi-the-remix-thieu-cong-bang-26921.html\" target=\"_blank\">Ng&ocirc; Thanh V&acirc;n</a>&nbsp;</strong>được b&aacute;o ch&iacute; nhắc đến với t&ecirc;n quốc tế l&agrave; Vernonica Ngo. Nữ ca sĩ / diễn vi&ecirc;n cũng sẽ h&oacute;a th&acirc;n v&agrave;o vai một nữ phi c&ocirc;ng c&oacute; t&ecirc;n l&agrave; Paige được trực tiếp chỉ dạy bởi Poe Dameron do Oscar Isaac thủ vai. &ldquo;Đả nữ&rdquo; cũng sẽ l&agrave; chị/em g&aacute;i của nh&acirc;n vật Rose Tico, một vai quan trọng trong phần The Last Jedi do diễn vi&ecirc;n Việt Kiều Marie Tran thủ vai.</p>\r\n\r\n<p>T&aacute;c phẩm điện ảnh bom tấn Hollywood n&agrave;y sẽ ch&iacute;nh thức được giới thiệu v&agrave;o ng&agrave;y 13/12/2017.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/1495762163-149575995-3a74.jpg\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Ng&ocirc; Thanh V&acirc;n vui mừng khi tượng phật được đấu gi&aacute; với gi&aacute;&nbsp;cao</em></p>\r\n\r\n<p>Ngo&agrave;i ra, mới đ&acirc;y&nbsp;<strong><a href=\"http://tinnhac.com/will-365-noi-loan-trong-ca-khuc-duoc-ngo-thanh-van-danh-tang-26770.html\" target=\"_blank\">Ng&ocirc; Thanh V&acirc;n</a>&nbsp;</strong>cũng đ&atilde; thực hiện th&agrave;nh c&ocirc;ng việc g&acirc;y quỹ cho&nbsp;<em>&quot;Vết sẹo cuộc đời lần 7&quot;.</em>&nbsp;Theo c&ocirc;ng bố mới nhất, số tiền g&acirc;y quỹ l&ecirc;n đến gần 8 tỉ đồng m&agrave; ch&uacute; yếu đến từ tượng phật v&agrave;ng được thỉnh từ Nepal. Tượng đ&atilde; được đấu gi&aacute; với mức gi&aacute; rất cao, chiếm gần 5&nbsp;tỉ đồng trong tổng số tiền thu được.&nbsp;Với số tiền n&agrave;y, chương tr&igrave;nh đ&atilde; ch&iacute;nh thức cứu sống được th&ecirc;m 252 b&eacute; khỏi căn bệnh tim bẩm sinh,&nbsp;n&acirc;ng con số tổng số b&eacute; được cứu sống l&ecirc;n 1,500 em đến thời điểm n&agrave;y.</p>\r\n', 'Siêu phẩm “Star Wars” sẽ chính thức ra rạp vào cuối năm nay.', 'Việt Nam', 'res/images/tintuc/20170525-053745-1578-f04b.jpg'),
(7, 'Oplus: Chúng tôi thiếu một bản hit để ghi dấu trong lòng khán giả', '<p><strong>&Aacute; qu&acirc;n chương tr&igrave;nh &ldquo;The X-Factor &ndash; Nh&acirc;n tố b&iacute; ẩn&rdquo; m&ugrave;a đầu ti&ecirc;n vừa ra mắt album &acirc;m nhạc đầu tay c&oacute; t&ecirc;n &ldquo;Smooth&rdquo;.</strong></p>\r\n\r\n<p>Chiều h&ocirc;m qua, Oplus &ndash; boyband trưởng th&agrave;nh từ cuộc thi The X-Factor vừa ch&iacute;nh thức gửi đến người h&acirc;m mộ sản phẩm &acirc;m nhạc album vol 2 -&nbsp;<em>&quot;Smooth&quot;</em>. Album l&agrave; dự &aacute;n &acirc;m nhạc được nh&oacute;m nhạc ấp ủ v&agrave; đầu tư chỉnh chu từ h&igrave;nh ảnh đến &acirc;m thanh.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/1/2-2a43.jpg\" style=\"height:337px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Oplus - nh&oacute;m nhạc rất t&agrave;i năng&nbsp;</em></p>\r\n\r\n<p>To&agrave;n bộ 7 b&agrave;i trong album được nh&oacute;m OPlus thực hiện được giao trọng tr&aacute;ch cho th&agrave;nh vi&ecirc;n đồng h&agrave;nh c&ugrave;ng nh&oacute;m từ l&uacute;c th&agrave;nh lập - Zen. Ngo&agrave;i 2 b&agrave;i l&agrave;&nbsp;<em>&ldquo;Wish&rdquo;</em>&nbsp;v&agrave;<em>&nbsp;&ldquo;Mưa r&agrave;o&rdquo;</em>&nbsp;đ&atilde; được OPlus cho ra mắt trước đ&oacute; th&igrave; 5 b&agrave;i c&ograve;n lại l&agrave;&nbsp;<em>&ldquo;Con đường đ&atilde; qua&rdquo;, &ldquo;Mơ m&agrave;ng&rdquo;, &ldquo;Hồi ức&rdquo;, &ldquo;Bước đến sau&rdquo;, &ldquo;Gi&oacute; đầu m&ugrave;a&rdquo;</em>&nbsp;l&agrave; những b&agrave;i h&aacute;t được s&aacute;ng t&aacute;c, thu mới ho&agrave;n to&agrave;n. Trong đ&oacute;, &ldquo;Con đường đ&atilde; qua&rdquo; c&ograve;n l&agrave; nhạc phim ch&iacute;nh của bộ phim<em>&nbsp;&ldquo;Ranh giới&rdquo;</em>&nbsp;sắp ra mắt.</p>\r\n\r\n<p>Album<em>&nbsp;&ldquo;Smooth&rdquo;</em>&nbsp;kh&ocirc;ng thuộc d&ograve;ng nhạc thị trường dễ nghe ch&oacute;ng ch&aacute;n, n&oacute; mang &acirc;m hưởng của cuộc sống, của những g&oacute;c khuất mềm mại trong t&acirc;m hồn mỗi người m&agrave; tại đ&oacute;, họ t&igrave;m thấy nhau trong ca từ v&agrave; giai điệu.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/1/d-cb34.jpg\" style=\"height:356px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Tuấn Hưng ph&aacute;t biểu trong buổi họp b&aacute;o ra mắt album mới của nh&oacute;m nhạc</em></p>\r\n\r\n<p>Nếu nhắc đến những nghệ sĩ th&acirc;n thuộc v&agrave; từng hỗ trợ&nbsp;<strong><a href=\"http://tinnhac.com/nhom-nhac-oplus-ra-mat-mv-dau-tay-bang-tieng-anh-23821.html\" target=\"_blank\">Oplus</a></strong>&nbsp;tr&ecirc;n chặng đường khẳng định t&ecirc;n tuổi, kh&ocirc;ng thể qu&ecirc;n những t&igrave;nh cảm ưu &aacute;i của những ca sĩ gạo cội như Mỹ Linh, Lam Trường, Thu Phương, H&agrave; Anh Tuấn&hellip; Đặc biệt, người anh v&agrave; cũng l&agrave; người thầy đ&atilde; dẫn dắt nh&oacute;m nhạc từ l&uacute;c debut &ndash; ca sĩ Tuấn Hưng l&agrave; người m&agrave; Oplus lu&ocirc;n b&agrave;y tỏ l&ograve;ng tri &acirc;n s&acirc;u sắc. Trong buổi ra mắt sản phẩm đ&aacute;nh dấu cho chặng đường hoạt động &acirc;m nhạc nghi&ecirc;m t&uacute;c v&agrave; chuy&ecirc;n nghiệp, Oplus lu&ocirc;n nhắc đến Tuấn Hưng như một thần tượng, tượng đ&agrave;i &acirc;m nhạc thực thụ.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/1/csfhh-102d.jpg\" style=\"height:306px; width:490px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Anh vừa l&agrave; người anh vừa l&agrave; thầy của Oplus từ l&uacute;c debut</em></p>\r\n\r\n<p>Với Tuấn Hưng,&nbsp;<strong><a href=\"http://tinnhac.com/oplus-viet-tam-thu-nhan-ngay-dau-nam-moi-54141.html\" target=\"_blank\">Oplus</a>&nbsp;</strong>tập hợp những th&agrave;nh vi&ecirc;n trẻ đầy nhiệt huyết, lu&ocirc;n t&igrave;m t&ograve;i v&agrave; học hỏi c&aacute;i mới. Đ&oacute; l&agrave; l&yacute; do anh lu&ocirc;n tạo điều kiện cho Oplus lưu diễn c&ugrave;ng trong những chuyến đi lưu diễn ở hải ngoại.<em>&nbsp;&ldquo;Từ khi nghe OPlus h&aacute;t ở chương tr&igrave;nh Nh&acirc;n tố b&iacute; ẩn, t&ocirc;i đ&atilde; c&oacute; cảm t&igrave;nh với nh&oacute;m. Nh&oacute;m h&aacute;t tốt đến mức khiến t&ocirc;i giật m&igrave;nh. D&ugrave; l&uacute;c đ&oacute; vẫn chưa biết r&otilde; OPlus l&agrave; ai nhưng t&ocirc;i đ&atilde; muốn mời nh&oacute;m tham gia liveshow của m&igrave;nh rồi. Duy&ecirc;n phận đưa đẩy để một thời gian sau ch&uacute;ng t&ocirc;i diễn ở liveshow &ldquo;Đam m&ecirc;&rdquo;. Rồi trở th&agrave;nh anh em th&acirc;n thiết, rồi OPlus theo t&ocirc;i đi diễn ở nước ngo&agrave;i. T&ocirc;i muốn mang t&ecirc;n tuổi c&aacute;c em đi c&agrave;ng xa c&agrave;ng tốt&rdquo;,</em>&nbsp;Tuấn Hưng bộc bạch.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/1/unnamed1-a349.jpg\" style=\"height:375px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Rất nhiều bạn b&egrave; đ&atilde; tới ch&uacute;c mừng Oplus với sản phẩm &acirc;m nhạc mới&nbsp;</em></p>\r\n\r\n<p>V&agrave; cũng ch&iacute;nh nhờ đ&agrave;n anh dẫn dắt,&nbsp;<strong><a href=\"http://tinnhac.com/thu-phuong-cung-oplus-giu-lai-hanh-phuc-54552.html\" target=\"_blank\">Oplus</a>&nbsp;</strong>đ&atilde; c&oacute; nhiều cơ hội t&iacute;ch c&oacute;p tiền bạc để thực hiện album đầu tay được ấp ủ từ l&acirc;u. Tuy nhi&ecirc;n điều OPlus trăn trở đ&oacute; l&agrave; một dấu ấn &acirc;m nhạc thật đặc sắc.&nbsp;<em>&ldquo;L&agrave; ca sĩ, ai cũng mong m&igrave;nh c&oacute; b&agrave;i &ldquo;hit&rdquo;. Nhưng n&oacute; phụ thuộc v&agrave;o nhiều yếu tố như may mắn, thời điểm ra b&agrave;i h&aacute;t, chất lượng, hay&hellip; scandal đi k&egrave;m. Cũng c&oacute; thể v&igrave; nh&oacute;m&hellip; kh&ocirc;ng đẹp giai n&ecirc;n sức lan tỏa kh&ocirc;ng được tốt lắm. N&oacute;i vậy chứ ch&uacute;ng t&ocirc;i muốn giữ h&igrave;nh ảnh l&agrave; nghệ sĩ &ldquo;sạch&rdquo; cho đến khi n&agrave;o c&oacute; thể. D&ugrave; hiểu phải l&agrave;m thế n&agrave;o mới được y&ecirc;u mến nhưng ch&uacute;ng t&ocirc;i kh&ocirc;ng bất chấp tất cả để được nổi tiếng&rdquo;,</em>&nbsp;c&aacute;c th&agrave;nh vi&ecirc;n chia sẻ.</p>\r\n\r\n<p>Trong thời gian tới, nh&oacute;m nhạc cũng c&oacute; những dự &aacute;n &acirc;m nhạc chất lượng hợp t&aacute;c với nhạc sĩ Nguyễn Hải Phong v&agrave; một dự &aacute;n ri&ecirc;ng với nhạc sĩ Phương Uy&ecirc;n v&agrave; nhạc sĩ Thanh T&acirc;m (dự kiến ph&aacute;t h&agrave;nh v&agrave;o năm 2018).</p>\r\n', 'Á quân chương trình “The X-Factor – Nhân tố bí ẩn” mùa đầu tiên vừa ra mắt album âm nhạc đầu tay có tên “Smooth”.', 'Việt Nam', 'res/images/tintuc/2-2a43.jpg'),
(8, 'Top 6 tân binh Kpop năm 2017 có xuất thân từ các show thực tế sống còn', '<p><strong>C&oacute; thể n&oacute;i rằng c&aacute;c chương tr&igrave;nh thực tế l&agrave; c&aacute;i n&ocirc;i gi&uacute;p nhiều ng&ocirc;i sao đến với c&ocirc;ng ch&uacute;ng. Ch&iacute;nh v&igrave; thế m&agrave; c&aacute;c c&ocirc;ng ty quản l&yacute; đ&atilde; đưa g&agrave; cưng của m&igrave;nh tham dự nhiều chương tr&igrave;nh trước khi debut solo hoặc trong một nh&oacute;m nhạc.</strong></p>\r\n\r\n<p>Từ đầu năm 2017 đến nay, cộng đồng fan Kpop li&ecirc;n tục đ&oacute;n nhận th&ocirc;ng tin nhiều &ldquo;người cũ&rdquo; trong c&aacute;c chương tr&igrave;nh thực tế như Produce 101, Kpop Star sẽ ra mắt với c&aacute;c vai tr&ograve; kh&aacute;c nhau. C&oacute; người mạnh dạn solo, c&oacute; người debut trong nh&oacute;m nhạc. D&ugrave; chưa biết c&oacute; th&agrave;nh c&ocirc;ng trong tương lai hay kh&ocirc;ng nhưng &iacute;t nhiều g&igrave; họ cũng đ&atilde; g&acirc;y được sự ch&uacute; &yacute; bởi v&igrave; &ldquo;quen mặt&rdquo;.</p>\r\n\r\n<p>H&atilde;y c&ugrave;ng điểm qua những t&acirc;n binh của Kpop c&oacute; xuất th&acirc;n từ c&aacute;c show thực tế sống c&ograve;n đ&igrave;nh đ&aacute;m:</p>\r\n\r\n<p><strong>6 - Kriesha Chu</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/2/kpop-girl-group-surv-816a.jpg\" style=\"height:896px; width:600px\" /></p>\r\n\r\n<p>Kriesha Chu l&agrave; một trong những th&iacute; sinh ấn tượng nhất của chương tr&igrave;nh Kpop Star 6. C&ocirc; g&aacute;i t&agrave;i năng n&agrave;y đ&atilde; đ&aacute;nh cắp tr&aacute;i tim của kh&ocirc;ng &iacute;t nh&agrave; sản xuất h&agrave;ng đầu của YG, JYP, Antenna Music v&agrave; nhiều hơn thế nữa. Trong cuộc thi,&nbsp;<strong><a href=\"http://tinnhac.com/nghe-si/kriesha-tiu.html\" target=\"_blank\">Kriesha</a></strong>&nbsp;kết hợp c&ugrave;ng những th&iacute; sinh kh&aacute;c lập th&agrave;nh đội KWINS v&agrave; gi&agrave;nh được vị tr&iacute; &aacute; qu&acirc;n. Sau khi cuộc thi kết th&uacute;c, c&ocirc; k&yacute; hợp đồng với Urban Works để ch&iacute;nh thức ra mắt solo v&agrave;o ng&agrave;y 24 th&aacute;ng 5 bằng sản phẩm đầu tay mang t&ecirc;n&nbsp;<em>&quot;Trouble&quot;</em>.</p>\r\n\r\n<p><strong>5 - Elris</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/2/kpop-girl-group-surv-9e22.jpg\" style=\"height:900px; width:600px\" /></p>\r\n\r\n<p>Elris l&agrave; một nh&oacute;m mới gồm 5 th&agrave;nh vi&ecirc;n của HUNUS Entertainment. Th&agrave;nh vi&ecirc;n c&ugrave;ng đội KWINS với Kriesha -&nbsp;<strong>Kim SoHee</strong>&nbsp;l&agrave; center của nh&oacute;m nhạc n&agrave;y. Được xem l&agrave; nh&oacute;m nhạc em g&aacute;i đầu ti&ecirc;n của&nbsp;<strong>TOPP DOGG</strong>, mọi người đang chờ đợi Elris l&agrave;m n&ecirc;n thương hiệu sau khi ch&iacute;nh thức debut v&agrave;o ng&agrave;y 1 th&aacute;ng 6 tới.</p>\r\n\r\n<p><strong>4 - Kim ChungHa</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/2/kpop-girl-group-surv-d2a2.jpg\" style=\"height:899px; width:600px\" /></p>\r\n\r\n<p>&ldquo;Cỗ m&aacute;y nhảy&rdquo; của I.O.I cuối c&ugrave;ng sẽ debut solo theo đ&uacute;ng ước nguyện của c&ocirc; ấy.<strong>&nbsp;<a href=\"http://tinnhac.com/nghe-si/chungha-ioi.html\" target=\"_blank\">Kim ChungHa</a></strong>&nbsp;đ&atilde; ph&aacute;t h&agrave;nh ca kh&uacute;c pre-debut mang t&ecirc;n&nbsp;<em>&ldquo;WEEK</em>&rdquo; v&agrave;o th&aacute;ng 4 vừa qua. Sau thời gian chuẩn bị kỹ lưỡng th&igrave; Chungha sẽ ch&iacute;nh thức ch&agrave;o s&acirc;n Kpop v&agrave;o ng&agrave;y 7 th&aacute;ng 6. Nữ ca sĩ hứa hẹn sẽ g&acirc;y ấn tượng bởi ở c&ocirc; hội tụ đủ c&aacute;c kỹ năng nhảy lẫn thanh nhạc đi&ecirc;u luyện.</p>\r\n\r\n<p><strong>3 - i-Teen Girls (Fantagio Girls)</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/2/kpop-girl-group-surv-bc5f.jpg\" style=\"height:285px; width:600px\" /></p>\r\n\r\n<p><strong>I-Teen Girls</strong>&nbsp;hay c&ograve;n được gọi l&agrave; Fantagio Girls, đang chuẩn bị cho lần ra mắt ch&iacute;nh thức của họ v&agrave;o m&ugrave;a h&egrave; n&agrave;y. Nh&oacute;m được đ&aacute;nh gi&aacute; cao v&igrave; c&oacute; sự g&oacute;p mặt của hai th&agrave;nh vi&ecirc;n của&nbsp;<strong><a href=\"http://tinnhac.com/nghe-si/ioi.html\" target=\"_blank\">I.O.I</a></strong>, Produce 101 m&ugrave;a 1 l&agrave; Kim DoYeon v&agrave; Choi YooJung. Sau khi được đ&agrave;o tạo tại nước ngo&agrave;i v&agrave; nh&aacute; h&agrave;ng những th&agrave;nh vi&ecirc;n t&agrave;i năng, nh&oacute;m nhạc n&agrave;y tự tin sẽ th&agrave;nh c&ocirc;ng sau khi debut.</p>\r\n\r\n<p><strong>2 - DAYDAY</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/2/kpop-girl-group-surv-9b33.jpg\" style=\"height:427px; width:600px\" /></p>\r\n\r\n<p>Điều g&igrave; xảy ra khi một c&ocirc;ng ty kết hợp th&iacute; sinh t&agrave;i năng của cả&nbsp;<strong>Produce 101</strong>&nbsp;v&agrave; Kpop Star 6? V&agrave; kết quả sẽ l&agrave; DAYDAY &ndash; một girlgroup của HYWY Entertainment. Nh&oacute;m n&agrave;y bao gồm c&aacute;c th&agrave;nh vi&ecirc;n Lee SuHyun nổi l&ecirc;n từ Produce 101, Jeon MinJu của Kpop Star 6/The Ark v&agrave; những c&ocirc; n&agrave;ng xinh đẹp, t&agrave;i năng kh&aacute;c nữa. Với đội h&igrave;nh thế n&agrave;y th&igrave; liệu rằng DAYDAY sẽ gi&agrave;nh được giải T&acirc;n binh trong năm 2017 hay kh&ocirc;ng?</p>\r\n\r\n<p><strong>1 - PRISTIN</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/2/kpop-girl-group-surv-44cf.jpg\" style=\"height:399px; width:600px\" /></p>\r\n\r\n<p>T&iacute;nh đến th&aacute;ng 5 năm 2017 th&igrave;&nbsp;<strong><a href=\"http://tinnhac.com/nghe-si/pristin.html\" target=\"_blank\">PRISTIN</a></strong>&nbsp;đang l&agrave; ứng cử vi&ecirc;n s&aacute;ng gi&aacute; nhất cho giải thưởng T&acirc;n binh v&agrave;o cuối năm nay. Girlgroup n&agrave;y l&agrave; đ&agrave;n em của nh&oacute;m nhạc nam đ&igrave;nh đ&aacute;m SEVENTEEN. PRISTIN c&oacute; sự g&oacute;p mặt của 2 cựu th&agrave;nh vi&ecirc;n của nh&oacute;m I.O.I l&agrave; KyulKyung v&agrave; NaYoung. C&oacute; lẽ nhờ đ&oacute; m&agrave; nh&oacute;m đ&atilde; thu h&uacute;t sự ch&uacute; &yacute; cực kỳ lớn ngay từ trước khi debut ch&iacute;nh thức. C&aacute;c c&ocirc; n&agrave;ng t&acirc;n binh t&agrave;i năng v&agrave; xinh đẹp n&agrave;y đ&atilde; đ&aacute;nh dấu bước ch&agrave;o s&acirc;n với album&nbsp;<em>&quot;HI! PRISTIN</em>&rdquo; c&ugrave;ng ca kh&uacute;c chủ đề &ldquo;<em>Wee Woo&rdquo;</em>.</p>\r\n', 'Có thể nói rằng các chương trình thực tế là cái nôi giúp nhiều ngôi sao đến với công chúng. Chính vì thế mà các công ty quản lý đã đưa gà cưng của mình tham dự nhiều chương trình trước khi debut solo hoặc trong một nhóm nhạc.', 'Châu Á', 'res/images/tintuc/kpop-girl-group-surv-44cf.jpg'),
(9, 'NCT 127 xác nhận gia nhập đường đua comeback tháng 6 Munnie (lược dịch), Thứ năm, 25/05/2017 16:33', '<p><strong>Tuy nhi&ecirc;n, ng&agrave;y trở lại cụ thể hiện vẫn chưa được c&ocirc;ng ty quản l&yacute; x&aacute;c nhận.</strong></p>\r\n\r\n<p>C&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u, nhiều nguồn tin khẳng định một trong những nh&oacute;m nhỏ của&nbsp;<strong><a href=\"http://tinnhac.com/nghe-si/nct-neo-culture-technology.html\" target=\"_blank\">NCT</a>&nbsp;</strong>- NCT 127, sẽ sớm trở lại trong th&aacute;ng 6 n&agrave;y. Thậm ch&iacute;, b&aacute;o ch&iacute; H&agrave;n Quốc c&ograve;n cho biết, nh&oacute;m đ&atilde; bắt đầu tiến h&agrave;nh quay MV mới v&agrave;o ng&agrave;y h&ocirc;m qua. V&agrave; giờ đ&acirc;y, c&ocirc;ng ty quản l&yacute; SM Entertainment đ&atilde; ch&iacute;nh thức l&ecirc;n tiếng phản hồi về th&ocirc;ng tin n&agrave;y.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/3/nct-127-6074.jpg\" style=\"height:300px; width:600px\" /></p>\r\n\r\n<p>Những b&agrave;i b&aacute;o trước đ&acirc;y đều&nbsp;khẳng định rằng, c&aacute;c ch&agrave;ng trai NCT 127 đ&atilde; c&oacute; mặt tại Mo Cheo thuộc tỉnh Kyeonggi để tiến h&agrave;nh quay một MV mới. Theo kế hoạch, nh&oacute;m sẽ c&oacute; m&agrave;n comeback v&agrave;o đầu th&aacute;ng 6 tới đ&acirc;y. Nếu đ&uacute;ng như những g&igrave; b&aacute;o ch&iacute; đăng tải th&igrave; đ&acirc;y sẽ l&agrave; lần trở lại thứ 2 của nh&oacute;m trong năm nay, sau khi ph&aacute;t h&agrave;nh mini album thứ hai mang t&ecirc;n &quot;<em>Limitless</em>&quot; hồi th&aacute;ng 1 vừa qua.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/3/nct-nct-127148384801-f1c6.jpg\" style=\"height:300px; width:600px\" /></p>\r\n\r\n<p>V&agrave;o s&aacute;ng 25 th&aacute;ng 5, một đại diện đến từ SM Entertainment đ&atilde; tiết lộ với truyền th&ocirc;ng H&agrave;n:&nbsp;&quot;<em>Đ&uacute;ng l&agrave; NCT 127 đang trong qu&aacute; tr&igrave;nh chuẩn bị cho album comeback của nh&oacute;m. Họ thực sự đang ghi h&igrave;nh cho MV mới. Tuy nhi&ecirc;n, kế hoạch trở lại cụ thể của nh&oacute;m vẫn chưa được x&aacute;c nhận chi tiết. Xin h&atilde;y chờ đ&oacute;n c&aacute;c hoạt động sắp tới của NCT 127</em>&quot;.</p>\r\n\r\n<p>NCT 127 debut với đội h&igrave;nh 7 th&agrave;nh vi&ecirc;n bao gồm Taeil, Taeyong, Yuta, Jaehyun, Winwin, Mark, Haechan v&agrave;o th&aacute;ng 7 năm 2016. V&agrave;o th&aacute;ng 1 năm nay, nh&oacute;m x&aacute;c nhận bổ sung th&ecirc;m hai th&agrave;nh vi&ecirc;n Johnny v&agrave; Doyoung, đồng thời trở lại ấn tượng với mini album thứ hai v&agrave; ca kh&uacute;c chủ đề &quot;<em>Limitless</em>&quot;.</p>\r\n', 'Tuy nhiên, ngày trở lại cụ thể hiện vẫn chưa được công ty quản lý xác nhận.', 'Châu Á', 'res/images/tintuc/ikon-comeback-new-st-bc01.jpg'),
(10, 'Bài hát của iKON rớt hạng ', '<p><strong>Chỉ sau hai ng&agrave;y ph&aacute;t h&agrave;nh, cả hai ca kh&uacute;c của iKON đ&atilde; rơi khỏi top 50 tr&ecirc;n hầu hết c&aacute;c BXH &acirc;m nhạc uy t&iacute;n.</strong></p>\r\n\r\n<p>Từ trước đến nay,&nbsp;<strong>YG Entertainment</strong>&nbsp;vốn nổi tiếng l&agrave; nơi tụ họp của c&aacute;c &quot;qu&aacute;i vật nhạc số&quot; KPOP. Từ những nghệ sĩ trẻ như&nbsp;<strong>Black Pink, Akdong Musician, WINNER, Zion.T</strong>&nbsp;đến những &quot;c&acirc;y đa c&acirc;y đề&quot; như&nbsp;<strong>Big Bang, 2NE1, PSY, Epik High</strong>... tất cả đều &quot;l&agrave;m ăn&quot; v&ocirc; c&ugrave;ng ngon nghẻ tr&ecirc;n c&aacute;c bảng xếp hạng nhạc số. Việc mỗi lần t&aacute;i xuất đều trụ vững ng&ocirc;i đầu c&aacute;c BXH digital dường như đ&atilde; trở th&agrave;nh một đặc trưng bất di bất dịch của d&agrave;n nghệ sĩ YG. Thế nhưng, một trường hợp ngoại lệ đ&atilde; v&ocirc; t&igrave;nh ph&aacute; vỡ &quot;truyền thống&quot; đ&aacute;ng tự h&agrave;o đ&oacute;. Nh&acirc;n vật đang được nhắc đến ở đ&acirc;y l&agrave; boygroup 7 th&agrave;nh vi&ecirc;n -&nbsp;<strong><a href=\"http://tinnhac.com/nghe-si/ikon.html\" target=\"_blank\">iKON</a></strong>.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/4/uploadblingblingthum-36c6.jpg\" style=\"height:375px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>iKON</em></p>\r\n\r\n<p>Ng&agrave;y 22 th&aacute;ng 5 vừa qua, iKON&nbsp;<a href=\"http://tinnhac.com/sau-tat-ca-cuoi-cung-ikon-cung-chinh-thuc-tro-lai-voi-bo-doi-mv-chat-khong-chiu-noi-99095.html\" target=\"_blank\">ph&aacute;t h&agrave;nh single t&aacute;i xuất</a><em>&nbsp;&quot;NEW KIDS: BEGIN&quot;</em>&nbsp;với bộ đ&ocirc;i ca kh&uacute;c chủ đề l&agrave;&nbsp;<em>&quot;Bling Bling&quot;&nbsp;</em>v&agrave;<em>&nbsp;&quot;B-Day&quot;</em>. Cứ ngỡ, bằng sự tham gia trực tiếp của CEO Yang Hyun Suk với tư c&aacute;ch nh&agrave; sản xuất th&igrave; sản phẩm comeback của&nbsp;<a href=\"http://tinnhac.com/nghe-si/ikon.html\" target=\"_blank\">iKON</a>&nbsp;sẽ đủ sức &quot;h&ocirc; mưa gọi gi&oacute;&quot; tr&ecirc;n khắp c&aacute;c s&acirc;n khấu KPOP. Nhưng kh&ocirc;ng, chỉ sau hai ng&agrave;y ra l&ograve;, cả hai ca kh&uacute;c của iKON đều rơi khỏi top 50 tr&ecirc;n hầu hết c&aacute;c BXH &acirc;m nhạc uy t&iacute;n.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/4/untitled1-3282.png\" style=\"height:377px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Thời điểm trưa 25/5, &quot;Bling Bling&quot; đứng thứ 55 tại BXH Melon...</em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/4/untitled-d660.png\" style=\"height:338px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>...trong khi &quot;B-Day&quot; đ&atilde; rớt xuống hạng 83</em></p>\r\n\r\n<p>C&oacute; hai nguy&ecirc;n nh&acirc;n dẫn đến sự thất bại đau đớn của&nbsp;<strong><a href=\"http://tinnhac.com/nghe-si/ikon.html\" target=\"_blank\">iKON</a></strong>. Một l&agrave; sự sụp đổ của fandom iKONIC, hai l&agrave; chất lượng đ&aacute;ng thất vọng của bộ đ&ocirc;i b&agrave;i h&aacute;t chủ đề.</p>\r\n\r\n<p>N&oacute;i về<strong>&nbsp;iKONIC</strong>, thật kh&oacute; để h&igrave;nh dung quy m&ocirc; của fandom n&agrave;y đang ở mức độ thế n&agrave;o. Dĩ nhi&ecirc;n họ kh&ocirc;ng phải l&agrave; một fandom nhỏ ở KPOP, nhưng để n&oacute;i về số lượng th&igrave; chắc hẳn cũng chẳng phải l&agrave; con số g&igrave; ấn tượng cho cam. Nếu nh&igrave;n v&agrave;o thực tế, đợt comeback của Seventeen với&nbsp;<em>&quot;Don&#39;t Wanna Cry&quot;</em>&nbsp;diễn ra c&ugrave;ng ng&agrave;y c&ugrave;ng giờ với iKON cũng kh&ocirc;ng đạt được th&agrave;nh t&iacute;ch n&agrave;o nổi bật, song nhờ c&oacute; fandom Carat hậu thuẫn, ca kh&uacute;c của họ vẫn trụ vững top 50 tr&ecirc;n tất cả c&aacute;c trang nhạc số. Vậy th&igrave; iKON c&oacute; g&igrave;?</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/4/untitled2-0f5e.png\" style=\"height:338px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>B&agrave;i h&aacute;t của &quot;đối thủ&quot; Seventeen được xếp ở hạng 38, c&ograve;n iKON đ&atilde; &quot;lạc tr&ocirc;i&quot; phương n&agrave;o?</em></p>\r\n\r\n<p>Debut từ 2015,&nbsp;<strong><a href=\"http://tinnhac.com/nghe-si/ikon.html\" target=\"_blank\">iKON</a></strong>&nbsp;nhanh ch&oacute;ng trở th&agrave;nh &quot;t&acirc;n binh qu&aacute;i vật&quot;, sở hữu hit đỉnh&nbsp;<em>&quot;Rhythm Ta&quot;</em>, thắng nhiều giải Rookie, tương lai mở ra biết bao nhi&ecirc;u điều hứa hẹn trước mắt. V&agrave; rồi YG cho nh&oacute;m... mất t&iacute;ch 1,5 năm để quảng b&aacute; ở Trung Quốc, Nhật Bản.</p>\r\n\r\n<p>Hoạt động d&agrave;i hơi ở thị trường nước ngo&agrave;i đem đến cho iKON danh tiếng v&agrave; nguồn thu dồi d&agrave;o nhưng ai m&agrave; ngờ được n&oacute; đ&atilde; khiến fandom của nh&oacute;m tại qu&ecirc; nh&agrave; ho&agrave;n to&agrave;n vỡ vụn. N&ecirc;n nhớ, khoảng thời gian m&agrave; iKON rời đi, đ&atilde; c&oacute; biết bao idolgroup mới ra đời? Họ cũng trẻ, cũng đẹp v&agrave; cũng c&oacute; sức h&uacute;t n&agrave;o chẳng hề&nbsp;thua k&eacute;m g&igrave; boygroup YG. iKON vẫn chưa đạt đến tầm Big Bang, vậy họ c&oacute; thể lấy cớ g&igrave; để r&agrave;ng buộc người h&acirc;m mộ gắn b&oacute; với m&igrave;nh?</p>\r\n\r\n<p>Dường như b&agrave;i học m&agrave; WINNER phải nhận lấy trong đợt quảng b&aacute;&nbsp;<em>&quot;Baby Baby&quot;&nbsp;</em>v&agrave;<em>&nbsp;&quot;Sentimental&quot;</em>&nbsp;vẫn chưa đủ để YG r&uacute;t kinh nghiệm cho<a href=\"http://tinnhac.com/nghe-si/ikon.html\" target=\"_blank\">&nbsp;iKON</a>. Việc giờ đ&acirc;y chẳng ai th&egrave;m stream (nghe trực tuyến) hay download (tải) nhạc của iKON&nbsp;bỗng chốc trở th&agrave;nh một lẽ thường t&igrave;nh, một sự thật cay đắng m&agrave; nh&oacute;m v&agrave; YG phải g&aacute;nh chịu.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/4/ikon-comeback-new-st-bc01.jpg\" style=\"height:451px; width:560px\" /></p>\r\n\r\n<p>Lại n&oacute;i về&nbsp;<em>&quot;Bling Bling&quot;&nbsp;</em>v&agrave;<em>&nbsp;&quot;B-Day&quot;</em>,&nbsp;<a href=\"http://tinnhac.com/sau-tat-ca-cuoi-cung-ikon-cung-chinh-thuc-tro-lai-voi-bo-doi-mv-chat-khong-chiu-noi-99095.html\" target=\"_blank\">vũ kh&iacute; ch&iacute;nh trong m&agrave;n comeback</a>&nbsp;lần n&agrave;y. Hiển nhi&ecirc;n, gu &acirc;m nhạc của mỗi người đều rất ri&ecirc;ng, kh&ocirc;ng ai giống ai ho&agrave;n to&agrave;n. Tuy nhi&ecirc;n ch&uacute;ng ta vẫn giao nhau ở một chuẩn mực thưởng thức chung n&agrave;o đ&oacute;. Rất tiếc, cả hai ca kh&uacute;c&nbsp;<em>&quot;Bling Bling&quot;&nbsp;</em>lẫn<em>&nbsp;&quot;B-Day&quot;</em>&nbsp;dường như chẳng thể bắt được thị hiếu phổ th&ocirc;ng của c&ocirc;ng ch&uacute;ng.</p>\r\n\r\n<p>D&ugrave; được s&aacute;ng t&aacute;c &quot;đo ni đ&oacute;ng gi&agrave;y&quot; để ph&ugrave; hợp với giọng h&aacute;t, giọng rap của 7 th&agrave;nh vi&ecirc;n song cả hai b&agrave;i n&agrave;y vẫn&nbsp;kh&ocirc;ng để lại bất kỳ giai điệu đ&aacute;ng nhớ n&agrave;o. Tất cả những g&igrave; c&oacute; thể đọng lại trong đầu kh&aacute;n giả đ&oacute; l&agrave; bộ đ&ocirc;i ca kh&uacute;c n&agrave;y đều l&agrave; nhạc hip hop, kh&aacute; hầm hố v&agrave;... &quot;trơn tuột&quot;.</p>\r\n\r\n<p>C&oacute; nhiều &yacute; kiến c&oacute; thể cho rằng bản th&acirc;n nhạc hip hop l&agrave; phải mang t&iacute;nh c&aacute; nh&acirc;n, độc đ&aacute;o nhưng xin đừng qu&ecirc;n&nbsp;rằng ng&agrave;y nay c&oacute; rất nhiều b&agrave;i hip hop cũng g&acirc;y nghiện cao v&agrave; ch&uacute;ng thường xuy&ecirc;n leo l&ecirc;n top đầu bảng xếp hạng, chứ kh&ocirc;ng m&ocirc;ng lung như&nbsp;<em>&quot;Bling Bling&quot;&nbsp;</em>v&agrave;<em>&nbsp;&quot;B-Day&quot;</em>. C&aacute;i c&ocirc;ng ch&uacute;ng cần ở&nbsp;<a href=\"http://tinnhac.com/nghe-si/ikon.html\" target=\"_blank\">iKON</a>, chỉ đơn giản l&agrave; phong c&aacute;ch, l&agrave; chất nhạc đỉnh cao như thời&nbsp;<em>&quot;Rhythm Ta&quot;</em>&nbsp;chứ kh&ocirc;ng phải l&agrave; thứ &acirc;m nhạc lạ lẫm như thế n&agrave;y!</p>\r\n', 'Chỉ sau hai ngày phát hành, cả hai ca khúc của iKON đã rơi khỏi top 50 trên hầu hết các BXH âm nhạc uy tín.', 'Châu Á', 'res/images/tintuc/uploadblingblingthum-36c6.jpg');
INSERT INTO `tintuc` (`ID`, `TieuDe`, `NoiDung`, `TomTat`, `ChuDe`, `AnhDaiDien`) VALUES
(11, 'Kim Heechul và một ngàn lẻ một chuyện \"không đỡ được\"', '<p><strong>Kh&ocirc;ng phải ngẫu nhi&ecirc;n m&agrave; c&aacute;c fan Kpop đều n&oacute;i: &quot;Tr&ecirc;n đời n&agrave;y chỉ c&oacute; 3 loại người l&agrave; con trai, con g&aacute;i v&agrave; Kim Heechul&quot;</strong></p>\r\n\r\n<p>Giải th&iacute;ch một c&aacute;ch dễ hiểu th&igrave; 4D c&oacute; nghĩa l&agrave; chiều kh&ocirc;ng gian thứ 4, nơi c&aacute;c nh&agrave; khoa học đặt giả thiết l&agrave; c&oacute; tồn tại sự sống của người ngo&agrave;i h&agrave;nh tinh. V&agrave; trong Kpop, cụm từ 4D được cụ thể h&oacute;a bằng những thần tượng c&oacute; t&iacute;nh c&aacute;ch hơi lập dị, tưng tửng v&agrave; &quot;cần phải đưa về h&agrave;nh tinh mẹ gấp&quot;. Đương nhi&ecirc;n, nhắc tới 4D th&igrave; kh&ocirc;ng thể n&agrave;o kh&ocirc;ng nhắc tới c&aacute;i t&ecirc;n&nbsp;<a href=\"http://tinnhac.com/nghe-si/heechul.html\" target=\"_blank\">Kim Heechul</a>, bởi anh m&agrave; xếp thứ 2 trong danh s&aacute;ch Idol 4D th&igrave; c&oacute; lẽ kh&ocirc;ng ai d&aacute;m đứng nhất!</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/5/150608-heechul-91-febc.jpg\" style=\"height:399px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\">Debut với tư c&aacute;ch l&agrave; diễn vi&ecirc;n trước khi trở th&agrave;nh một mẩu của Super Junior như b&acirc;y giờ, Heechul g&acirc;y ấn tượng mạnh bằng vai diễn Baek Jinwoo của &quot;Sharp 2&quot;. Trong phim, Heechul h&oacute;a th&acirc;n th&agrave;nh trưởng nh&oacute;m nhiếp ảnh &quot;vạn người m&ecirc;&quot; nhưng lại kh&ocirc;ng hay bộc lộ bản th&acirc;n, kh&aacute; điềm tĩnh v&agrave; b&iacute; ẩn bởi qu&aacute; khứ bị che giấu. Nhập vai rất ngọt, song Heechul ngo&agrave;i đời lại tr&aacute;i ngược ho&agrave;n to&agrave;n với Baek Jinwoo - hoạt ng&ocirc;n, thẳng thắn v&agrave; c&oacute; những ph&aacute;t biểu hay h&agrave;nh động kh&ocirc;ng đỡ được.<br />\r\n<img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/5/150512-md-wonju-fans-4c51.jpg\" style=\"height:399px; width:600px\" /></p>\r\n\r\n<p>Mang trong m&igrave;nh nh&oacute;m m&aacute;u AB - nh&oacute;m m&aacute;u đại diện cho sự &quot;đi&ecirc;n&quot;, lu&ocirc;n tạo ra điểm kh&aacute;c biệt v&agrave; đầy m&acirc;u thuẫn, v&igrave; thế kh&ocirc;ng kh&oacute; để nhận ra n&eacute;t qu&aacute;i qu&aacute;i trong h&agrave;nh động cũng như lời n&oacute;i của anh ch&agrave;ng đẹp hơn hoa n&agrave;y. V&agrave; đ&ocirc;i khi, Heechul cũng ngẫu hứng biến tấu nh&oacute;m m&aacute;u n&agrave;y bằng c&aacute;ch n&oacute;i m&igrave;nh thuộc nh&oacute;m m&aacute;u... C!</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/5/giphy-72b9.gif\" style=\"height:200px; width:480px\" /></p>\r\n\r\n<p>Trước đ&acirc;y khi&nbsp;<a href=\"http://tinnhac.com/nghe-si/hangeng.html\" target=\"_blank\">Hangeng</a>&nbsp;(Hankyung) c&ograve;n l&agrave; th&agrave;nh vi&ecirc;n trong&nbsp;<strong>Super Junior</strong>&nbsp;v&agrave; chưa s&otilde;i tiếng H&agrave;n, Heechul l&agrave; người tận t&igrave;nh chỉ bảo cho Hangeng tiếng H&agrave;n, dạy nhiệt t&igrave;nh tới mức dạy lu&ocirc;n cả những c&acirc;u... chửi thề cho cựu th&agrave;nh vi&ecirc;n n&agrave;y! Anh cũng hết sức bảo vệ Hangeng, ch&iacute;nh Heechul đ&atilde; kể rằng m&igrave;nh đ&atilde; &quot;xử đẹp&quot; anti-fan v&igrave; anti-fan x&uacute;c phạm tới Hangeng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c sao nam H&agrave;n Quốc thường kh&aacute; ngại ng&ugrave;ng khi nhận m&igrave;nh l&agrave; fan của một Idol nữ n&agrave;o đ&oacute;. Tuy nhi&ecirc;n, Heechul lại kh&ocirc;ng hề d&egrave; dặt khi lu&ocirc;n miệng nhận m&igrave;nh l&agrave; fan cứng của&nbsp;<a href=\"http://tinnhac.com/nghe-si/so-hee.html\" target=\"_blank\">Sohee</a>&nbsp;(cựu th&agrave;nh vi&ecirc;n Wonder Girls) v&agrave; ki&ecirc;n tr&igrave; h&acirc;m mộ c&ocirc; trong suốt 10 năm. Anh c&ograve;n gia nhập cả fancafe của Wonder Girls v&agrave; được mọi người đặt biệt danh l&agrave; Soheechul. Khi Yoona hỏi tại sao anh lại chỉ th&iacute;ch Sohee, anh ch&agrave;ng &quot;đanh đ&aacute;&quot; đ&aacute;p lời: &quot;<em>Em cũng th&acirc;n với 2PM hơn l&agrave; th&acirc;n anh c&ograve;n g&igrave;!</em>&quot;</p>\r\n\r\n<p>V&agrave; cũng chỉ c&oacute; Kim Heechul mới d&aacute;m c&ocirc;ng khai l&ecirc;n truyền h&igrave;nh cổ vũ cho những c&ocirc;ng ty giải tr&iacute; kh&aacute;c ngo&agrave;i SM Entertainment. Nổi tiếng l&agrave; thần tượng Sohee bền bỉ v&agrave; bị fan soi ra l&agrave; h&acirc;m mộ th&agrave;nh vi&ecirc;n Momo của Twice, Heechul vẫn thường d&agrave;nh cho JYP Entertainment những lời c&oacute; c&aacute;nh như &quot;<em>JYP cố l&ecirc;n!</em>&quot; hay &quot;<em>T&ocirc;i rất th&iacute;ch JYP (Entertainment)</em>&quot;. YG Entertainment cũng kh&ocirc;ng phải l&agrave; ngoại lệ, Heechul vẫn gửi tới đối thủ một mất một c&ograve;n của c&ocirc;ng ty m&igrave;nh lời động vi&ecirc;n: &quot;<em>Gia đ&igrave;nh YG, h&ograve;a b&igrave;nh! Cố l&ecirc;n!</em>&quot;.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/5/soheechul-fc47.jpg\" style=\"height:317px; width:600px\" /></p>\r\n\r\n<p>Nhắc tới Heechul, kh&aacute;n giả kh&ocirc;ng thể n&agrave;o kh&ocirc;ng nhớ tới những phi vụ &quot;động trời&quot; anh g&acirc;y ra, m&agrave; người giải quyết chẳng ai kh&aacute;c ch&iacute;nh l&agrave; người s&aacute;ng lập ra SM - Chủ tịch Lee Sooman. Được chỉ định trở th&agrave;nh lead vocal của nh&oacute;m nhạc dự &aacute;n, nhưng Heechul lại kh&ocirc;ng đồng t&igrave;nh v&igrave; anh chỉ th&iacute;ch v&agrave; giữ vững quan điểm muốn h&aacute;t metal rock, v&igrave; thế n&ecirc;n SM đ&atilde; bắt anh viết bản kiểm điểm. Thế nhưng những d&ograve;ng kiểm điểm của anh lại chẳng hối lỗi tẹo n&agrave;o: &quot;<em>T&ocirc;i chả biết m&igrave;nh l&agrave;m sai chỗ n&agrave;o cả, mấy người bắt t&ocirc;i viết th&igrave; t&ocirc;i viết th&ocirc;i!</em>&quot;, thế n&ecirc;n kh&ocirc;ng lạ khi anh bị đ&igrave;nh chỉ luyện tập. Ngay cả l&uacute;c&nbsp;<a href=\"http://tinnhac.com/nghe-si/yunho-dbsk.html\" target=\"_blank\">Yunho</a>&nbsp;v&agrave;&nbsp;<a href=\"http://tinnhac.com/nghe-si/dong-hae.html\" target=\"_blank\">Donghae</a>&nbsp;hỏi anh rằng anh kh&ocirc;ng tới c&ocirc;ng ty cũng kh&ocirc;ng sao chứ, anh vẫn mạnh miệng: &quot;<em>C&oacute; việc g&igrave; cần th&igrave; người ta gọi!</em>&quot; Rốt cuộc, chủ tịch Lee đ&atilde; phải li&ecirc;n lạc với anh v&agrave; đưa anh đi đ&oacute;ng phim.</p>\r\n\r\n<p>Trong một lần kh&aacute;c, khi thể hiện khả năng giải tr&iacute; của m&igrave;nh, Heechul đ&atilde; chọn tuyệt chi&ecirc;u giả giọng, m&agrave; đối tượng anh giả giọng ch&iacute;nh l&agrave; người thầy Lee Sooman. Bởi v&igrave; trước đ&acirc;y chưa c&oacute; ai d&aacute;m giả giọng vị chủ tịch quyền lực n&agrave;y, thế n&ecirc;n nh&agrave; đ&agrave;i đ&atilde; c&acirc;n nhắc cắt đoạn của Heechul đi. Nhưng thầy Lee Sooman lại chủ động li&ecirc;n hệ v&agrave; gật đầu đồng &yacute; với tiết mục n&agrave;y, để rồi sau đ&oacute; &quot;g&agrave;&quot; nh&agrave; SM cũng bắt đầu giả giọng thầy. Nhiều người c&ograve;n rỉ tai nhau rằng chủ tịch Lee Sooman khắt khe với nhiều thần tượng hay trainee trong c&ocirc;ng ty, nhưng với Heechul th&igrave;... th&ocirc;i, chủ tịch cũng &quot;lười&quot; quản anh rồi!</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/5/super-junior-heechul-33af.jpg\" style=\"height:599px; width:600px\" /></p>\r\n\r\n<p>C&oacute; thể Idol rất sợ sasaeng, c&ograve;n Heechul th&igrave; thẳng tay chỉnh sasaeng fan của m&igrave;nh tới nơi tới chốn. Một sasaeng gọi điện cho Heechul v&agrave;o l&uacute;c 3 giờ s&aacute;ng, v&agrave; Heechul đ&atilde; trả lời như sau:</p>\r\n\r\n<p>Sasaeng: Oppa...</p>\r\n\r\n<p>Heechul: Eh!!!</p>\r\n\r\n<p>Sasaeng:... Dạ?</p>\r\n\r\n<p>Heechul: Giờ l&agrave; mấy giờ rồi c&oacute; biết kh&ocirc;ng?</p>\r\n\r\n<p>Sasaeng: Dạ 3 giờ s&aacute;ng.</p>\r\n\r\n<p>Heechul: Thế giờ n&agrave;y anh c&oacute; n&ecirc;n đi ngủ kh&ocirc;ng?</p>\r\n\r\n<p>Sasaeng: N&ecirc;n...</p>\r\n\r\n<p>Heechul: Nếu n&oacute;i chuyện điện thoại giờ n&agrave;y với em th&igrave; anh c&oacute; thấy mệt kh&ocirc;ng?</p>\r\n\r\n<p>Sasaeng: C&oacute;...</p>\r\n\r\n<p>Heechul: Thế th&igrave; em c&oacute; n&ecirc;n c&uacute;p m&aacute;y đi kh&ocirc;ng?</p>\r\n\r\n<p>Sasaeng: Dạ n&ecirc;n.</p>\r\n\r\n<p>Heechul: Hiểu rồi th&igrave; c&uacute;p m&aacute;y đi. S&aacute;ng mai gọi lại!</p>\r\n\r\n<p>Kh&ocirc;ng chỉ sasaeng, m&agrave; ngay cả E.L.F anh ch&agrave;ng l&eacute;m lỉnh n&agrave;y cũng kh&ocirc;ng bu&ocirc;ng tha. Fan cầm s&uacute;ng phun nước bắn v&agrave;o người anh, nếu như l&agrave; Idol kh&aacute;c th&igrave; họ sẽ n&eacute; đi, hoặc l&agrave; bảo fan ngừng lại, c&ograve;n Heechul th&igrave; độc hơn nhiều, anh ch&agrave;ng lấy hẳn chai nước 2 l&iacute;t để &quot;b&aacute;o th&ugrave;&quot; v&agrave; tất nhi&ecirc;n, c&ocirc; n&agrave;ng kia ướt mem!</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/5/photo-1-149330269615-db28.jpg\" style=\"height:314px; width:600px\" /></p>\r\n\r\n<p>Nhờ c&oacute; những m&agrave;n fanservice của thần tượng m&agrave; c&aacute;c fan c&oacute; cảm hứng viết ra h&agrave;ng loạt fanfic, song c&oacute; lẽ Kpop chưa từng chứng kiến thần tượng n&agrave;o tự viết fanfic cho m&igrave;nh như ch&iacute;nh Kim Heechul cả! Viết cho m&igrave;nh v&agrave; trưởng nh&oacute;m&nbsp;<a href=\"http://tinnhac.com/nghe-si/leeteuk-super-junior.html\" target=\"_blank\">Leeteuk</a>&nbsp;hai &quot;t&aacute;c phẩm&quot;, Heechul cũng kh&ocirc;ng qu&ecirc;n xo&aacute;y s&acirc;u v&agrave;o &quot;mối t&igrave;nh&quot; tay ba giữa&nbsp;<a href=\"http://tinnhac.com/nghe-si/eunhyuk-super-junior.html\" target=\"_blank\">Eunhyuk</a>&nbsp;- Donghae -&nbsp;<a href=\"http://tinnhac.com/nghe-si/choi-siwon-super-junior.html\" target=\"_blank\">Siwon</a>. Anh c&ograve;n đăng tải những c&acirc;u chuyện n&agrave;y c&ocirc;ng khai tr&ecirc;n c&aacute;c trang mạng x&atilde; hội để fan c&oacute; thể tiện v&agrave;o đọc.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/5/239473d7ccf9e2a26da9-2e72.jpg\" style=\"height:900px; width:600px\" /></p>\r\n\r\n<p>Mặc kệ những lời b&agrave;n t&aacute;n về giới t&iacute;nh, Heechul vẫn rất th&iacute;ch sưu tầm những m&oacute;n c&oacute; in h&igrave;nh c&ocirc;ng ch&uacute;a Anna. Anh cũng chẳng ngần ngại khi khoe với mọi người về sở th&iacute;ch n&agrave;y. Thậm ch&iacute;, anh c&ograve;n giả l&agrave;m n&agrave;ng c&ocirc;ng ch&uacute;a n&agrave;y mỗi khi c&oacute; dịp v&agrave; xinh đẹp kh&ocirc;ng k&eacute;m bản gốc.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/5/1f66aa0eac123bcb1835-c5c8.jpg\" style=\"height:405px; width:600px\" /></p>\r\n\r\n<p>Nổi tiếng l&agrave; ch&agrave;ng trai c&oacute; quan hệ rộng nhất showbiz, nhưng kh&ocirc;ng phải ai mỹ nam đẹp hơn hoa n&agrave;y cũng kết th&acirc;n, m&agrave; anh c&oacute; một hội bạn th&acirc;n ri&ecirc;ng, bao gồm những nghệ sỹ c&oacute; c&aacute; t&iacute;nh mạnh, theo &quot;trường ph&aacute;i&quot; 4D na n&aacute; Heechul: Jungmo (Trax), Junhyung (Highlight), Hani (EXID),... Bởi vậy mới n&oacute;i Heechul kh&ocirc;ng c&oacute; dị nhất, chỉ c&oacute; dị hơn!</p>\r\n', 'Không phải ngẫu nhiên mà các fan Kpop đều nói: \"Trên đời này chỉ có 3 loại người là con trai, con gái và Kim Heechul\"', 'Châu Á', 'res/images/tintuc/150608-heechul-91-febc.jpg'),
(12, 'Charlie Puth: Từ kẻ trầm mặc trở thành ngôi sao thế giới', '<p><strong>Khu&ocirc;n mặt điển trai c&ugrave;ng với chất giọng đầy cảm x&uacute;c, Charlie Puth khiến người ta đ&ecirc; m&ecirc; trong những bản nhạc được h&ograve;a tấu một c&aacute;ch bắt tai v&agrave; g&acirc;y nghiện. Tr&aacute;i với những g&igrave; người ta nghĩ về một người đ&agrave;n &ocirc;ng nổi tiếng, Charlie Puth đ&atilde; lột x&aacute;c từ kẻ nh&uacute;t nh&aacute;t th&agrave;nh ng&ocirc;i sao được săn đ&oacute;n.</strong></p>\r\n\r\n<p><strong>Từ g&atilde; v&ocirc; danh th&agrave;nh ng&ocirc;i sao thế giới</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/6/20160804-044906-char-02d5.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p>Nhắc đến Charlie Puth, người ta nhớ ngay đến ch&agrave;ng trai với l&ocirc;ng m&agrave;y bị đứt n&eacute;t. Tưởng chừng như đ&oacute; l&agrave; điều kh&ocirc;ng ho&agrave;n hảo trong gương mặt điển trai, nhưng ngược lại, n&oacute; khiến anh trở n&ecirc;n trở n&ecirc;n đặc biệt, như một điểm nhấn ho&agrave;n hảo kh&ocirc;ng thể thiếu. Điểm xuất ph&aacute;t của Charlie Puth khiến người ta li&ecirc;n tưởng đến &ocirc;ng ho&agrave;ng của thể loại nhạc điện tử nổi tiếng Alan Walker khi Charlie Puth l&agrave; một g&atilde; trai nh&uacute;t nh&aacute;t v&agrave; kh&eacute;p k&iacute;n. Dường như thế giới tuổi thơ của Charlie Puth chỉ g&oacute;i gọn trong căn nh&agrave; nhỏ với những thiết bị điện tử. Trong c&aacute;ch n&oacute;i chuyện, Charlie Puth ngượng ng&ugrave;ng, e thẹn, thậm ch&iacute; c&oacute; phần ấp &uacute;ng. Charlie Puth sớm được tiếp x&uacute;c với &acirc;m nhạc. Anh học về &acirc;m nhạc cổ điển v&agrave; piano từ năm bốn tuổi, tham gia kh&oacute;a học nhạc Jazz v&agrave;o năm 10 tuổi. Đ&acirc;y ch&iacute;nh l&agrave; nền tảng để Charlie Puth ph&aacute;t triển con đường sản xuất &acirc;m nhạc sau n&agrave;y.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/6/6b3953d99385a85b8d65-1ff8.jpg\" style=\"height:899px; width:600px\" /></p>\r\n\r\n<p>D&ograve;ng nhạc của Charlie Puth dễ khiến người ta ng&acirc;y ngất bởi c&aacute;ch h&ograve;a &acirc;m th&ocirc;ng minh v&agrave; s&aacute;ng tạo. Nhịp beat c&aacute;c sản phẩm &acirc;m nhạc đều bắt tay v&agrave; ch&iacute;nh điều đ&oacute; tạo n&ecirc;n một cơn sốt m&atilde;nh liệt với t&iacute;n đồ &acirc;m nhạc tr&ecirc;n thế giới. Cũng giống như Justin Bieber, Cherlie Puth cũng nổi l&ecirc;n từ k&ecirc;nh Youtube. V&agrave; qua một bản cover, cuộc đời của Charlie Puth thay đổi bằng cuộc điện thoại. Anh nhanh ch&oacute;ng bước v&agrave;o l&agrave;ng nhạc với sự ng&acirc;y thơ v&agrave; trong s&aacute;ng của một nghệ sĩ mới toanh v&agrave; được ưu &aacute;i rất nhiều.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/6/charlie-puth-2015-bi-c809.jpg\" style=\"height:397px; width:600px\" /></p>\r\n\r\n<p>Tuy nhi&ecirc;n, c&uacute; ấn định th&agrave;nh c&ocirc;ng rực rỡ nhất của Charlie Puth ch&iacute;nh l&agrave; ca kh&uacute;c<em>&nbsp;&ldquo;See You Again&rdquo;</em>. Trong giai đoạn s&aacute;ng t&aacute;c, anh nhớ đến một người bạn đ&atilde; ra đi trong một vụ tai nạn &ocirc; t&ocirc;, v&agrave; n&oacute; khiến anh c&oacute; cảm x&uacute;c m&atilde;nh liệt. Để khi rồi khi ho&agrave;n th&agrave;nh ca kh&uacute;c, giọng h&aacute;t ngọt ng&agrave;o của Charlie Puth c&ugrave;ng với chất rap với chủ đề gia đ&igrave;nh của Wiz Khalifa, tất cả l&agrave;m n&ecirc;n một giai điệu pop ballad đầy cảm x&uacute;c. Ca kh&uacute;c nhanh ch&oacute;ng lan rộng tr&ecirc;n to&agrave;n thế giới, trở th&agrave;nh một điểm nhấn kh&ocirc;ng thể qu&ecirc;n trong sự nghiệp của Charlie Puth. Đặc biệt hơn, người ta bắt đầu nhận ra Charlie Puth trong chất giọng ngọt ng&agrave;o đầy say đắm, đằng sau giai điệu piano du dương đưa người nghe đến cực kho&aacute;i cảm x&uacute;c.</p>\r\n\r\n<p>Sau th&agrave;nh c&ocirc;ng của<em>&nbsp;&ldquo;See You Again&rdquo;</em>, Charlie Puth đ&atilde; tiếp tục ra hai ca kh&uacute;c mang t&ecirc;n&nbsp;<em>&ldquo;We Don&rsquo;t Talk Anymore&rdquo;&nbsp;</em>v&agrave;&nbsp;<em>&ldquo;Attention&rdquo;</em>. Kh&ocirc;ng chi&ecirc;u tr&ograve;, kh&ocirc;ng cần quảng c&aacute;o qu&aacute; nhiều, giai điệu hook v&agrave; điệp kh&uacute;c khiến người ta ng&acirc;y ngất nghe từ khi thanh &acirc;m chạm v&agrave;o m&agrave;ng nhĩ, v&agrave; điều đ&oacute; đưa những ca kh&uacute;c của anh đến gần hơn với kh&aacute;n giả. Cho đến thời điểm n&agrave;y,<em>&nbsp;&ldquo;We Don&rsquo;t Talk Anymore&rdquo;</em>&nbsp;đ&atilde; đạt được hơn một tỷ lượt xem tr&ecirc;n k&ecirc;nh Youtube, ghi t&ecirc;n anh trở th&agrave;nh một trong những nghệ sĩ th&agrave;nh c&ocirc;ng nhất hiện nay.</p>\r\n\r\n<p><strong>Nhưng rồi, Charlie Puth vẫn m&atilde;i kẹt ở tuổi 20</strong></p>\r\n\r\n<p>Với những th&agrave;nh c&ocirc;ng đạt được, nhưng Charlie Puth vẫn kh&ocirc;ng bị đồng h&oacute;a bởi những h&agrave;o quang trong ng&agrave;nh c&ocirc;ng nghiệp giải tr&iacute; đầy thị phi. Trong cuộc sống h&agrave;ng ng&agrave;y, Charlie Puth vẫn giữ được sự nhạy cảm v&agrave; khi&ecirc;m tốn của m&igrave;nh. Được xem l&agrave; g&atilde; trai ngoan của l&agrave;ng nhạc &Acirc;u Mỹ b&ecirc;n c&acirc;y đ&agrave;n piano huyền thoại, kể từ khi gia nhập l&agrave;ng giải tr&iacute;, Charlie Puth chỉ ch&uacute; t&acirc;m v&agrave;o c&aacute;c hoạt động &acirc;m nhạc của m&igrave;nh v&agrave; sở hữu t&igrave;nh sử v&ocirc; c&ugrave;ng &ldquo;trong s&aacute;ng&rdquo;.</p>\r\n\r\n<p>Kh&ocirc;ng qu&aacute; nhiều scandal t&igrave;nh &aacute;i ồn &agrave;o đang bao bọc l&agrave;ng c&ocirc;ng nghiệp giải tr&iacute; đương đại, Charlie Puth từng c&oacute; mối quan hệ t&igrave;nh cảm ch&oacute;ng v&aacute;nh với Selena Gomez, v&agrave; mối t&igrave;nh nổi tiếng nhất của anh l&agrave; Bella Thorne, tuy nhi&ecirc;n hai người đ&atilde; nhanh ch&oacute;ng chia tay khi Charlie Puth ph&aacute;t hiện bạn g&aacute;i bắt c&aacute; hai tay. Đ&oacute; c&oacute; thể l&agrave; cảm hứng để anh s&aacute;ng t&aacute;c ca kh&uacute;c&nbsp;<em>&ldquo;Attention&rdquo;</em>.</p>\r\n\r\n<p>T&agrave;i năng, điển trai c&ugrave;ng lối sống c&oacute; phần l&agrave;nh mạnh v&agrave; &iacute;t thị phi, Charlie Puth trở th&agrave;nh gương mặt nghệ sĩ đang được thế giới săn đ&oacute;n. Anh hứa hẹn sẽ trở th&agrave;nh nh&acirc;n tố mới trong l&agrave;ng giải tr&iacute; đương đại.</p>\r\n', 'Khuôn mặt điển trai cùng với chất giọng đầy cảm xúc, Charlie Puth khiến người ta đê mê trong những bản nhạc được hòa tấu một cách bắt tai và gây nghiện. Trái với những gì người ta nghĩ về một người đàn ông nổi tiếng, Charlie Puth đã lột xác từ kẻ nhút nhát thành ngôi sao được săn đón.', 'Âu Mỹ', 'res/images/tintuc/20160804-044906-char-02d5.jpg'),
(13, 'Chris Cornell: Cuộc đời nhiều thăng trầm huyền thoại nhạc rock', '<p><strong>Sự ra đi đột ngột của Chris Cornell khiến những t&iacute;n đồ nhạc rock b&agrave;ng ho&agrave;ng. V&agrave; trong những ng&agrave;y qua, ca kh&uacute;c bất hủ &ldquo;You Know My Name&rdquo; một lần nữa vang l&ecirc;n, như b&agrave;i điếu văn cho một cuộc đời lừng lẫy nhưng kh&ocirc;ng k&eacute;m phần danh vọng.</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/7/chriscornell2015-min-cb04.jpg\" style=\"height:313px; width:600px\" /></p>\r\n\r\n<p>Chris Cornell mang một n&eacute;t đẹp đậm chất rock. Đầu ti&ecirc;n l&agrave; &aacute;nh mắt h&uacute;t hồn của &ocirc;ng lu&ocirc;n bừng s&aacute;ng, ngay cả khi Chris mang d&aacute;ng vẻ l&agrave; ch&agrave;ng thanh ni&ecirc;n với m&aacute;i t&oacute;c ngắn đầy năng động hay l&agrave;n t&oacute;c xoăn đậm chất nghệ sĩ như g&atilde; du mục tr&ecirc;n miền nhạc rock đầy biến động. Tất nhi&ecirc;n, người c&oacute; đ&ocirc;i mắt đẹp như thế, ắt hẳn cũng đ&atilde; trải qua nhiều đớn đau. Thời trẻ khi chưa đến 12 tuổi, &ocirc;ng ch&igrave;m trong những t&aacute;c phẩm kinh điển của nh&oacute;m nhạc The Beatles khi nh&igrave;n thấy bộ sưu tập The Beatles bị bỏ dưới tầng hầm của người h&agrave;ng x&oacute;m. Cornell c&ocirc; độc v&agrave; s&acirc;u lắng như ch&iacute;nh &aacute;nh mắt của &ocirc;ng, v&agrave; tất cả mọi cảm x&uacute;c đều th&ocirc;ng qua rock. Trong những năm thiếu ni&ecirc;n, Chris Cornell rơi v&agrave;o trầm cảm trầm trọng, &ocirc;ng bỏ học v&agrave; dường như tự nhốt m&igrave;nh trong căn nh&agrave; nhỏ. Trước khi trở th&agrave;nh một nhạc sĩ th&agrave;nh c&ocirc;ng, Chris Cornell cũng từng l&agrave;m nhiều c&ocirc;ng việc kh&aacute;c trước khi vụt s&aacute;ng trở th&agrave;nh một huyền thoại nhạc rock.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/7/chriscornellarrivals-ad93.jpg\" style=\"height:594px; width:449px\" /></p>\r\n\r\n<p>Chris Cornell sinh năm 1964 tại Seattle, Mỹ. Với giọng h&aacute;t &acirc;m vực cao v&agrave; cực kỳ nội lực, Chris c&ugrave;ng nh&oacute;m Soundgarden m&agrave; anh th&agrave;nh lập, đ&atilde; c&oacute; những đ&oacute;ng g&oacute;p quan trọng trong việc tạo dựng d&ograve;ng nhạc grunge rock &quot;đặc sản&quot; v&ugrave;ng Seattle suốt thập ni&ecirc;n 1990. Soundgarden được xếp v&agrave;o tứ trụ nhạc grunge c&ugrave;ng Nirvana của Kurt Cobain, Pearl Jam v&agrave; Alice In Chains. C&aacute;ch h&aacute;t của Chris s&acirc;u lắng, mạnh mẽ, phảng phất phong vị cuộc đời của một g&atilde; trai đ&atilde; đi qua rất nhiều những đau đớn, gục ng&atilde; để t&igrave;m đến &aacute;nh s&aacute;ng của cuộc đời m&igrave;nh.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/7/image-min-6f64.jpg\" style=\"height:337px; width:600px\" /></p>\r\n\r\n<p>Năm 1994, Soundgarden ra mắt album&nbsp;<em>Superunknown</em>&nbsp;v&agrave; nhận hai giải Grammy danh gi&aacute; với t&aacute;c phẩm n&agrave;y. Năm 1997, nh&oacute;m tuy&ecirc;n bố ngừng hoạt động. Chris Cornell theo đuổi sự nghiệp solo. Năm 2001, anh c&ugrave;ng với 3 th&agrave;nh vi&ecirc;n của Rage Against The Machine lập ra nh&oacute;m nhạc mới mang t&ecirc;n Audioslave. Hoạt động trong 6 năm, nh&oacute;m ra 3 album v&agrave; đặc biệt ghi dấu với buổi diễn tại Cuba năm 2005.</p>\r\n\r\n<p>Audioslave tan r&atilde; năm 2007, Chris quay trở lại với Soundgarden v&agrave; ra một album v&agrave;o năm 2012. T&iacute;nh đến thời điểm n&agrave;y, &ocirc;ng đ&atilde; ra mắt bốn album solo l&agrave;&nbsp;<em>Euphoria Morning (1999), Carry On (2007), Scream (2009), Higher Truth (2015).</em></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/GioiThieuAmNhac/upload/images/tin/7/landscape-1495098841-1598.jpg\" style=\"height:300px; width:600px\" /></p>\r\n\r\n<p>Một trong những ca kh&uacute;c nổi tiếng của Chris Cornell l&agrave;&nbsp;<em>&ldquo;You Know My Name&rdquo;</em>&nbsp;l&agrave; b&agrave;i h&aacute;t chủ đề của James Bond năm 2006. Với hy vọng t&igrave;m ra một ca sĩ với nội lực mạnh mẽ, ban l&agrave;m phim đ&atilde; mời Chris Cornell s&aacute;ng t&aacute;c v&agrave; thực hiện b&agrave;i h&aacute;t n&agrave;y c&ugrave;ng Arnold. Một người ở Ph&aacute;p, một người ở London, v&agrave; sau một th&aacute;ng, họ gặp nhau chắp nối phần ca từ v&agrave; giai điệu trở th&agrave;nh ca kh&uacute;c ho&agrave;n chỉnh mang t&ecirc;n<em>&nbsp;&ldquo;You Know My Name&rdquo;</em>. Với tiết tấu mạnh mẽ, đầy l&ocirc;i cuốn nhưng cũng kh&ocirc;ng k&eacute;m sự từng trải v&agrave; s&acirc;u lắng,<em>&nbsp;&ldquo;You Know My Name&rdquo;</em>&nbsp;được xem l&agrave; ca kh&uacute;c &ldquo;đo ni đ&oacute;ng gi&agrave;y&rdquo; d&agrave;nh cho Chris.</p>\r\n\r\n<p>Ngay khi ra mắt, ca kh&uacute;c đ&atilde; oanh tạc mọi bảng xếp hạng tr&ecirc;n khắp thế giới v&agrave; trở th&agrave;nh b&agrave;i h&aacute;t kinh điển cho loạt phim điệp vi&ecirc;n n&agrave;y. C&aacute;c nh&agrave; ph&ecirc; b&igrave;nh đ&atilde; nhận x&eacute;t t&iacute;ch cực về b&agrave;i h&aacute;t với giai điệu k&igrave; qu&aacute;i nhưng m&ecirc; hoặc đến lạ, đồng thời nắm bắt được vinh quang được l&agrave;m từ m&aacute;u v&agrave; nước mắt của loạt phim điệp vi&ecirc;n kinh điển n&agrave;y. Đồng thời đ&acirc;y cũng l&agrave; ca kh&uacute;c được đề cử giải Grammy v&agrave; l&agrave; sản phẩm &acirc;m nhạc kinh điển của Chris Cornell trong sự nghiệp &acirc;m nhạc của &ocirc;ng.</p>\r\n\r\n<p>Chris Cornell được xem l&agrave; một trong những nghệ sĩ nhạc rock vĩ đại nhất của l&agrave;ng nhạc rock. V&igrave; thế, sự ra đi đột ngột của &ocirc;ng đ&atilde; khiến những t&iacute;n đồ nhạc rock cảm thấy b&agrave;ng ho&agrave;ng. C&oacute; thể n&oacute;i, cuộc đời Chris l&agrave; thi&ecirc;n tiểu thuyết đầy rẫy những bi kịch, kh&oacute; khăn nhưng cũng kh&ocirc;ng &iacute;t những niềm vui khi đạt được đỉnh vinh quang. V&agrave; h&igrave;nh ảnh c&ugrave;ng những ca kh&uacute;c của &ocirc;ng sẽ sống m&atilde;i, như h&igrave;nh tượng điệp vi&ecirc;n 007 hay ca kh&uacute;c&nbsp;<em>&ldquo;You Know My Name&rdquo;</em>.</p>\r\n', 'Sự ra đi đột ngột của Chris Cornell khiến những tín đồ nhạc rock bàng hoàng. Và trong những ngày qua, ca khúc bất hủ “You Know My Name” một lần nữa vang lên, như bài điếu văn cho một cuộc đời lừng lẫy nhưng không kém phần danh vọng.', 'Âu Mỹ', 'res/images/tintuc/chriscornell2015-min-cb04.jpg');

